mapper.select:
delete from t_amz_advert_report_summary_week where bydate >=DATE_SUB(#{begin,jdbcType=CHAR} ,INTERVAL WEEKDAY(#{begin,jdbcType=CHAR}) + 0 DAY) and sellerid=#{sellerid,jdbcType=CHAR} and marketplaceid=#{marketplaceid,jdbcType=CHAR} ;
INSERT ignore INTO t_amz_advert_report_summary_week (id,sellerid,marketplaceid,ctype,bydate, asin, sku, currency, impressions,clicks,ctr,spend,cpc,acos, roas,orders,units,spc,totalsales) SELECT MAX(id) id, sellerid sellerid, marketplaceid marketplaceid, ctype, DATE_SUB(bydate ,INTERVAL WEEKDAY(bydate) + 0 DAY) bydate, asin asin, sku sku,currency, SUM(impressions) impressions, SUM(clicks) clicks, SUM(ctr)/count(0) ctr, SUM(spend) spend, SUM(cpc)/count(0) cpc, SUM(acos)/count(0) acos, sum(roas)/count(0) roas, sum(orders) orders, sum(units) units, sum(spc)/count(0) spc, sum(totalsales) totalsales FROM t_amz_advert_report_summary WHERE bydate >= DATE_SUB(#{begin,jdbcType=CHAR} ,INTERVAL WEEKDAY(#{begin,jdbcType=CHAR}) + 0 DAY) and marketplaceid=#{marketplaceid,jdbcType=CHAR} and sellerid= #{sellerid,jdbcType=CHAR} GROUP BY sellerid,marketplaceid,sku,ctype,DATE_SUB(bydate ,INTERVAL WEEKDAY(bydate) + 0 DAY) ,asin;
delete from t_amz_advert_report_summary_month where bydate >=DATE_ADD(#{begin,jdbcType=CHAR},interval -day(#{begin,jdbcType=CHAR})+1 day) and sellerid=#{sellerid,jdbcType=CHAR} and marketplaceid=#{marketplaceid,jdbcType=CHAR} ;
INSERT ignore INTO t_amz_advert_report_summary_month (id,sellerid,marketplaceid,ctype,bydate, asin, sku, currency, impressions,clicks,ctr,spend,cpc,acos, roas,orders,units,spc,totalsales) SELECT MAX(id) id, sellerid sellerid, marketplaceid marketplaceid, ctype, DATE_ADD(bydate,interval -day(bydate)+1 day) bydate, asin asin, sku sku,currency, SUM(impressions) impressions, SUM(clicks) clicks, SUM(ctr)/count(0) ctr, SUM(spend) spend, SUM(cpc)/count(0) cpc, SUM(acos)/count(0) acos, sum(roas)/count(0) roas, sum(orders) orders, sum(units) units, sum(spc)/count(0) spc, sum(totalsales) totalsales FROM t_amz_advert_report_summary WHERE bydate >=DATE_ADD(#{begin,jdbcType=CHAR},interval -day(#{begin,jdbcType=CHAR})+1 day) and sellerid= #{sellerid,jdbcType=CHAR} and marketplaceid=#{marketplaceid,jdbcType=CHAR} GROUP BY sellerid,marketplaceid,sku,ctype,DATE_ADD(bydate,interval -day(bydate)+1 day),ASIN;

	select * from t_amz_advert_report_summary t 
	where 
	t.sku=#{sku,jdbcType=CHAR} AND marketplaceid=#{marketplaceid,jdbcType=CHAR}  AND t.sellerid=#{sellerid,jdbcType=CHAR}
  and t.bydate > date_add(curdate(),INTERVAL -8 DAY)
  and t.bydate <= date_add(curdate(),INTERVAL -1 DAY) 
	and t.ctype=#{ctype,jdbcType=CHAR}
