mapper.select:
SELECT m.*,ifnull(v.byday,'6666-01-01') byday,v.opttime from t_amz_adv_report_request_type m LEFT JOIN ( SELECT p.marketplaceId,r.campaignType,r.recordType,IFNULL(r.segment,'#') segment,p.`type` ftype, IFNULL(r.creativeType,'#') creativeType,min(byday) byday,max(fileSize) fileSize,p.id profileid,max(r.opttime) opttime from t_amz_adv_report_request r LEFT JOIN t_amz_adv_profile p ON p.id=r.profileId WHERE p.id=#{profileid,jdbcType=CHAR} and r.requesttime>=DATE_ADD(now(),INTERVAL -24 HOUR) GROUP BY p.id,r.campaignType,r.recordType,r.segment,r.creativeType ) v ON v.campaignType=m.campaigntype AND v.recordType=m.reponsetype AND v.segment=IFNULL(m.segment,'#') AND v.creativeType=IFNULL(m.activeType,'#') WHERE ( m.nomarket NOT LIKE CONCAT('%',v.marketplaceid,'%') OR m.nomarket IS NULL ) AND (v.ftype=case when m.disablevendor then 'seller' ELSE v.ftype end OR v.ftype IS null)
and #{non_segment,jdbcType=CHAR} not like concat('%',IFNULL(m.segment,'#'),'%')
ORDER BY ifnull(v.byday,'6666-01-01') DESC ,LEVEL ASC limit 1;

mapper.delete:
   		delete from t_amz_adv_report_request 
	where treat_number<10 
	and (treat_status is null or treat_status='error' or treat_status='')
	and requesttime>=DATE_ADD(now(),INTERVAL -24 HOUR)
	and profileId = #{profileid,jdbcType=CHAR}

	and campaigntype=#{campaigntype,jdbcType=CHAR}


	and recordtype=#{recordtype,jdbcType=CHAR}


	and segment=#{segment,jdbcType=CHAR}

	and isrun=false and (log is null or log ='')
