mapper.select:
select CAST(t.campaignId AS CHAR) campaignId, CAST(t.profileid AS CHAR) profileid, CAST(t.campaignId AS CHAR) id, mk.name market,tg.name groupname,t.name name,'SD' campaignType, t.budget budget,t.budgetType targetingType,t.costtype, t.state status,'' premiumBidAdjustment, t.startDate startDate,t.endDate endDate, t.opttime opttime,tr.iswarn iswarn,p.marketplaceId marketplaceid,tk.remark remark,a.groupid groupid,v.*,'' bidding, '' servingStatus,tr.cycle,tr.fcondition,tr.subtrahend,tr.quota, tr.amount,'' portfolioid, case when t.tactic='T00001' then 'Product and Interest targeting' else case when t.tactic='T00020' then 'Product targeting' else case when t.tactic='T00030' then 'Audiences' else case when t.tactic='remarketing' then 'Legacy Views' else '--' end end end end portfoliosName from t_amz_adv_campaigns_sd t left join ( select c.campaignId campaignId1,c.profileid profileid1, sum(impressions) impressions,sum(clicks) clicks,sum(cost) cost, ifnull((sum(cost) / sum(clicks)),0) avgcost, ifnull((sum(clicks) / sum(impressions)),0) CTR,
case when ifnull(sum(cost),0)>0 and ifnull(sum(ifnull(sales,0)),0)=0 then 10000000000 else ifnull((sum(cost) / sum(ifnull(sales,0))),0) end ACOS, ifnull((sum(ifnull(sales,0)) / sum(cost)),0) ROAS, ifnull(sum(ifnull(sales,0)),0) sumSales, ifnull(sum(ifnull(unitsSold,0)),0) sumUnits, ifnull((sum(ifnull(purchases,0)) / sum(clicks)),0) CSRT, ifnull(sum(purchases),0) attributedConversions, ifnull(sum(unitsSold),0) attributedUnitsOrdered, ifnull(sum(sales),0) attributedSales
from t_amz_adv_rpt2_sd_campaigns c left join t_amz_adv_rpt2_sd_campaigns_attributed_all d on d.campaignId=c.campaignId and d.bydate=c.bydate left join t_amz_adv_rpt2_sd_campaigns_attributed_same s on s.campaignId=c.campaignId and s.bydate=c.bydate left join t_amz_adv_rpt2_sd_campaigns_attributed_view v on v.campaignId=c.campaignId and v.bydate=c.bydate where c.bydate >= #{fromDate,jdbcType=DATE} and c.bydate <= #{endDate,jdbcType=DATE}
and c.profileid = #{profileid,jdbcType=BIGINT}
and c.campaignId = #{campaignid,jdbcType=BIGINT}
group by c.campaignId,c.profileid ) v on t.campaignId = v.campaignId1 and t.profileid = v.profileid1 left join t_amz_adv_profile p on t.profileid = p.id left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0 left join t_amazon_group tg on tg.id = a.groupid left join t_marketplace mk on mk.marketplaceId=p.marketplaceId left join t_amz_adv_remind tr on tr.profileid=t.profileid and t.campaignId = tr.campaignid and tr.adgroupid = 0 and tr.keywordid = 0 and tr.adid = 0 and tr.targetid = 0 left join t_amz_adv_remark tk on tk.profileid =t.profileid and t.campaignId = tk.campaignId and tk.adgroupId = 0 and tk.keywordId = 0 and tk.adId = 0 and tk.targetId = 0 where a.shopid = #{shopid,jdbcType=CHAR}
and t.profileid = #{profileid,jdbcType=BIGINT}
and t.portfolioid = #{portfolios,jdbcType=BIGINT}
and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}
and a.groupid = #{groupid,jdbcType=CHAR}
and t.campaignId = #{campaignid,jdbcType=BIGINT}
and t.state = #{campaignStatus,jdbcType=CHAR}
and (t.state = 'enabled' or t.state = 'ENABLED' or t.state = 'paused' or t.state = 'PAUSED')
and t.name like #{campaignName,jdbcType=CHAR}
and ${paralist}

       select sum(impressions) impressions, sum(clicks) clicks, sum(cost) cost,
			ifnull((sum(cost) / sum(clicks)),0) avgcost,
			ifnull((sum(clicks) / sum(impressions)),0) CTR,
			case when ifnull(sum(cost),0)>0 and
			ifnull(sum(sales),0)=0
			then 10000000000
			else ifnull((sum(cost) / sum(ifnull(sales,0))),0)
			end ACOS,
			ifnull((sum(ifnull(sales,0)) / sum(cost)),0) ROAS,
			ifnull(sum(ifnull(sales,0)),0) sumSales,
			ifnull(sum(ifnull(unitsSold,0)),0) sumUnits,
			ifnull((sum(ifnull(purchases,0)) / sum(clicks)),0) CSRT,
			ifnull(sum(ifnull(purchases,0)) ,0) attributedConversions,
			ifnull(sum(ifnull(unitsSold,0)),0) attributedUnitsOrdered,
			ifnull(sum(ifnull(sales,0)) ,0) attributedSales
		from t_amz_adv_rpt2_sd_campaigns v
		left join t_amz_adv_rpt2_sd_campaigns_attributed_all tt on tt.campaignId=v.campaignId and tt.bydate=v.bydate
		left join t_amz_adv_rpt2_sd_campaigns_attributed_same ts on ts.campaignId=v.campaignId and ts.bydate=v.bydate
		left join t_amz_adv_rpt2_sd_campaigns_attributed_view v1 on  v1.campaignId=v.campaignId and v1.bydate=v.bydate
		left join t_amz_adv_campaigns_sd t on t.campaignId = v.campaignId and t.profileid = v.profileid
		left join t_amz_adv_profile p on t.profileid = p.id
		left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0
		left join t_amazon_group tg on tg.id = a.groupid
		left join t_marketplace mk on mk.marketplaceId=p.marketplaceId
		where v.bydate >= #{fromDate,jdbcType=DATE} and v.bydate <= #{endDate,jdbcType=DATE}
			and a.shopid = #{shopid,jdbcType=CHAR}

			and v.profileid = #{profileid,jdbcType=BIGINT}


			and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}


			and a.groupid = #{groupid,jdbcType=CHAR}


			and v.campaignId = #{campaignid,jdbcType=BIGINT}


			and t.state = #{campaignStatus,jdbcType=CHAR}


			and (t.state = 'enabled' or t.state = 'paused')


			and t.name like #{campaignName,jdbcType=CHAR}


			and ${paralist}

select

	date_format(v.bydate,'%m-%d') bydate,


	date_format(subdate(v.bydate,date_format(v.bydate,'%w')-7),'%m-%d') bydate,


	concat( year(v.bydate) ,'-' ,month(v.bydate)) bydate,


	${serchlist}

from t_amz_adv_rpt2_sd_campaigns v
left join t_amz_adv_rpt2_sd_campaigns_attributed_all tt on tt.campaignId=v.campaignId and tt.bydate=v.bydate
left join t_amz_adv_rpt2_sd_campaigns_attributed_same ts on ts.campaignId=v.campaignId and ts.bydate=v.bydate
left join t_amz_adv_campaigns_sd t on t.campaignId = v.campaignId and t.profileid = v.profileid
left join t_amz_adv_profile p on t.profileid = p.id
left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0
left join t_amazon_group tg on tg.id = a.groupid
where a.shopid = #{shopid,jdbcType=CHAR}
	and v.bydate >= #{fromDate,jdbcType=DATE} and v.bydate <= #{endDate,jdbcType=DATE}

	and v.campaignId = #{campaignid,jdbcType=BIGINT}


	and t.profileid = #{profileid,jdbcType=BIGINT}


	and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}


	and a.groupid = #{groupid,jdbcType=CHAR}


	and t.state = #{campaignStatus,jdbcType=CHAR}


	and t.name like #{campaignName,jdbcType=CHAR}


	group by date_format(v.bydate,'%m-%d')


	group by date_format(subdate(v.bydate,date_format(v.bydate,'%w')-7),'%y-%m-%d')


	group by concat( year(v.bydate) ,'-' ,month(v.bydate))

select

	${serchstr}

from t_amz_adv_rpt2_sd_campaigns t
left join t_amz_adv_rpt2_sd_campaigns_attributed_all tt on tt.campaignId=v.campaignId and tt.bydate=v.bydate
left join t_amz_adv_rpt2_sd_campaigns_attributed_same ts on ts.campaignId=v.campaignId and ts.bydate=v.bydate
where t.profileid = #{profileid,jdbcType=BIGINT}
	and t.campaignId = #{campaignid,jdbcType=BIGINT}

	and t.bydate = #{yesterDay,jdbcType=DATE}


	and t.bydate >= #{severDayBegin,jdbcType=DATE} and t.bydate <= #{severDayEnd,jdbcType=DATE}

group by t.campaignId,t.profileid

select

	${serchstr}

from t_amz_adv_rpt2_sd_campaigns t
left join t_amz_adv_rpt2_sd_campaigns_attributed_all tt on tt.campaignId=v.campaignId and tt.bydate=v.bydate
left join t_amz_adv_rpt2_sd_campaigns_attributed_same ts on ts.campaignId=v.campaignId and ts.bydate=v.bydate
where t.profileid = #{profileid,jdbcType=BIGINT}
	and t.campaignId = #{campaignid,jdbcType=BIGINT}

	and t.bydate = #{lastDay,jdbcType=DATE}


	and t.bydate >= #{lastSeverDayBegin,jdbcType=DATE} 
	and t.bydate <= #{lastSeverDayEnd,jdbcType=DATE}

group by t.campaignId,t.profileid

	   select t.* from t_amz_adv_campaigns_sd t
	   where t.profileid= #{profileid,jdbcType=CHAR}

	       and t.name like #{name,jdbcType=CHAR}


    	 and (t.state = #{campaignStatus,jdbcType=CHAR} or t.state = #{campaignStatusUppercase,jdbcType=CHAR})


    	  and  t.state!='archived' and  t.state!='ARCHIVED' 

	   order by name asc
