select CAST(t.campaignId AS CHAR) campaignId, CAST(t.profileid AS CHAR) profileid, CAST(t.campaignId AS CHAR) id, mk.name market,tg.name groupname,t.name name,'SP' campaignType, t.dailyBudget budget,t.targetingType targetingType, t.state status,t.premiumBidAdjustment premiumBidAdjustment, t.startDate startDate,t.endDate endDate, t.opttime opttime,tr.iswarn iswarn,p.marketplaceId marketplaceid,tk.remark remark,a.groupid groupid,v.*,t.bidding bidding, '' servingStatus,tr.cycle,tr.fcondition,tr.subtrahend,tr.quota, tr.amount,tps.id portfolioid,tps.name portfoliosName from t_amz_adv_campaigns t left join ( select t.campaignId campaignId1,t.profileid profileid1, sum(impressions) impressions,sum(clicks) clicks,sum(cost) cost, ifnull((sum(cost) / sum(clicks)),0) avgcost, ifnull((sum(clicks) / sum(impressions)),0) CTR,
case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales7d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales7d)),0) end ACOS, ifnull((sum(attributedSales7d) / sum(cost)),0) ROAS, ifnull(sum(attributedSales7d),0) sumSales, ifnull(sum(attributedUnitsOrdered7d),0) sumUnits, ifnull((sum(attributedConversions7d) / sum(clicks)),0) CSRT, ifnull(sum(attributedConversions7d) - sum(attributedConversions7dSameSKU),0) attributedConversions, ifnull(sum(attributedConversions7dSameSKU),0) attributedConversionsSameSKU, ifnull(sum(attributedConversions7d),0) attributedUnitsOrdered, ifnull(sum(attributedSales7dSameSKU),0) attributedSalesSameSKU, ifnull(sum(attributedSales7d) - sum(attributedSales7dSameSKU),0) attributedSales,
case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales1d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales1d)),0) end ACOS1d, ifnull((sum(attributedSales1d) / sum(cost)),0) ROAS1d, ifnull(sum(attributedSales1d),0) sumSales1d, ifnull(sum(attributedUnitsOrdered1d),0) sumUnits1d, ifnull((sum(attributedConversions1d) / sum(clicks)),0) CSRT1d, ifnull(sum(attributedConversions1d) - sum(attributedConversions1dSameSKU),0) attributedConversions1d, ifnull(sum(attributedConversions1dSameSKU),0) attributedConversionsSameSKU1d, ifnull(sum(attributedConversions1d),0) attributedUnitsOrdered1d, ifnull(sum(attributedSales1dSameSKU),0) attributedSalesSameSKU1d, ifnull(sum(attributedSales1d) - sum(attributedSales1dSameSKU),0) attributedSales1d,
case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales7d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales7d)),0) end ACOS7d, ifnull((sum(attributedSales7d) / sum(cost)),0) ROAS7d, ifnull(sum(attributedSales7d),0) sumSales7d, ifnull(sum(attributedUnitsOrdered7d),0) sumUnits7d, ifnull((sum(attributedConversions7d) / sum(clicks)),0) CSRT7d, ifnull(sum(attributedConversions7d) - sum(attributedConversions7dSameSKU),0) attributedConversions7d, ifnull(sum(attributedConversions7dSameSKU),0) attributedConversionsSameSKU7d, ifnull(sum(attributedConversions7d),0) attributedUnitsOrdered7d, ifnull(sum(attributedSales7dSameSKU),0) attributedSalesSameSKU7d, ifnull(sum(attributedSales7d) - sum(attributedSales7dSameSKU),0) attributedSales7d,
case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales14d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales14d)),0) end ACOS14d, ifnull((sum(attributedSales14d) / sum(cost)),0) ROAS14d, ifnull(sum(attributedSales14d),0) sumSales14d, ifnull(sum(attributedUnitsOrdered14d),0) sumUnits14d, ifnull((sum(attributedConversions14d) / sum(clicks)),0) CSRT14d, ifnull(sum(attributedConversions14d) - sum(attributedConversions14dSameSKU),0) attributedConversions14d, ifnull(sum(attributedConversions14dSameSKU),0) attributedConversionsSameSKU14d, ifnull(sum(attributedConversions14d),0) attributedUnitsOrdered14d, ifnull(sum(attributedSales14dSameSKU),0) attributedSalesSameSKU14d, ifnull(sum(attributedSales14d) - sum(attributedSales14dSameSKU),0) attributedSales14d,
case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales30d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales30d)),0) end ACOS30d, ifnull((sum(attributedSales30d) / sum(cost)),0) ROAS30d, ifnull(sum(attributedSales30d),0) sumSales30d, ifnull(sum(attributedUnitsOrdered30d),0) sumUnits30d, ifnull((sum(attributedConversions30d) / sum(clicks)),0) CSRT30d, ifnull(sum(attributedConversions30d) - sum(attributedConversions30dSameSKU),0) attributedConversions30d, ifnull(sum(attributedConversions30dSameSKU),0) attributedConversionsSameSKU30d, ifnull(sum(attributedConversions30d),0) attributedUnitsOrdered30d, ifnull(sum(attributedSales30dSameSKU),0) attributedSalesSameSKU30d, ifnull(sum(attributedSales30d) - sum(attributedSales30dSameSKU),0) attributedSales30d from t_amz_adv_rpt2_sp_compaigns t left join t_amz_adv_rpt2_sp_compaigns_attributed d on d.bydate=t.bydate and d.campaignId=t.campaignId left join t_amz_adv_rpt2_sp_compaigns_attributed_same s on s.bydate=t.bydate and s.campaignId=t.campaignId where t.bydate >= #{fromDate,jdbcType=DATE} and t.bydate <= #{endDate,jdbcType=DATE}
and t.profileid = #{profileid,jdbcType=BIGINT}
and t.campaignId = #{campaignid,jdbcType=BIGINT}
group by t.campaignId,t.profileid ) v on t.campaignId = v.campaignId1 and t.profileid = v.profileid1 left join t_amz_adv_profile p on t.profileid = p.id left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0 left join t_amazon_group tg on tg.id = a.groupid left join t_marketplace mk on mk.marketplaceId=p.marketplaceId left join t_amz_adv_portfolios tps on tps.id = t.portfolioid and tps.profileid = t.profileid left join t_amz_adv_remind tr on tr.profileid=t.profileid and t.campaignId = tr.campaignid and tr.adgroupid = 0 and tr.keywordid = 0 and tr.adid = 0 and tr.targetid = 0 left join t_amz_adv_remark tk on tk.profileid =t.profileid and t.campaignId = tk.campaignId and tk.adgroupId = 0 and tk.keywordId = 0 and tk.adId = 0 and tk.targetId = 0 where a.shopid = #{shopid,jdbcType=CHAR}
and t.profileid = #{profileid,jdbcType=BIGINT}
and t.portfolioid = #{portfolios,jdbcType=BIGINT}
and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}
and a.groupid = #{groupid,jdbcType=CHAR}
and t.campaignId = #{campaignid,jdbcType=BIGINT}
and (t.state = #{campaignStatus,jdbcType=CHAR} or t.state = #{campaignStatusUppercase,jdbcType=CHAR})
and (t.state = 'enabled' or t.state = 'paused' or t.state = 'ENABLED' or t.state = 'PAUSED')
and t.name like #{campaignName,jdbcType=CHAR}
and (t.targetingType = #{targetingType,jdbcType=CHAR} or t.targetingType = #{targetingTypeUppercase,jdbcType=CHAR})
and ${paralist}

	select

		date_format(v.bydate,'%m-%d') bydate,


		date_format(subdate(v.bydate,date_format(v.bydate,'%w')-7),'%m-%d') bydate,


		concat( year(v.bydate) ,'-' ,month(v.bydate)) bydate,


		${serchlist}

	from t_amz_adv_rpt2_sp_compaigns v
    left join t_amz_adv_rpt2_sp_compaigns_attributed d on d.bydate=v.bydate and d.campaignId=v.campaignId
    left join t_amz_adv_rpt2_sp_compaigns_attributed_same s on s.bydate=v.bydate and s.campaignId=v.campaignId
	left join t_amz_adv_campaigns t on t.campaignId = v.campaignId and t.profileid = v.profileid
	left join t_amz_adv_profile p on t.profileid = p.id
	left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0
	left join t_amazon_group tg on tg.id = a.groupid
	where a.shopid = #{shopid,jdbcType=CHAR} 
		and v.bydate >= #{fromDate,jdbcType=DATE} and v.bydate <= #{endDate,jdbcType=DATE}

		and v.campaignId = #{campaignid,jdbcType=BIGINT}


		and t.profileid = #{profileid,jdbcType=BIGINT}


		and t.portfolioid = #{portfolios,jdbcType=BIGINT}


		and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}


		and a.groupid = #{groupid,jdbcType=CHAR}



		and (t.state = #{campaignStatus,jdbcType=CHAR} or t.state = #{campaignStatusUppercase,jdbcType=CHAR})


		and (t.state = 'enabled' or t.state = 'paused' or t.state = 'ENABLED' or t.state = 'PAUSED')


		and t.name like #{campaignName,jdbcType=CHAR}


		and (t.targetingType = #{targetingType,jdbcType=CHAR} or t.targetingType = #{targetingTypeUppercase,jdbcType=CHAR})



		group by date_format(v.bydate,'%y-%m-%d')


		group by date_format(subdate(v.bydate,date_format(v.bydate,'%w')-7),'%y-%m-%d')


		group by concat( year(v.bydate) ,'-' ,month(v.bydate))

	select

		${serchstr}

	from t_amz_adv_rpt2_sp_compaigns t
    left join t_amz_adv_rpt2_sp_compaigns_attributed d on d.bydate=t.bydate and d.campaignId=t.campaignId
	where t.profileid = #{profileid,jdbcType=BIGINT}
		and t.campaignId = #{campaignid,jdbcType=BIGINT}

		and t.bydate = #{yesterDay,jdbcType=DATE}


		and t.bydate >= #{severDayBegin,jdbcType=DATE} and t.bydate <= #{severDayEnd,jdbcType=DATE}

	group by t.campaignId,t.profileid

	select

		${serchstr}

	from t_amz_adv_rpt2_sp_compaigns t
    left join t_amz_adv_rpt2_sp_compaigns_attributed d on d.bydate=t.bydate and d.campaignId=t.campaignId
	where t.profileid = #{profileid,jdbcType=BIGINT}
		and t.campaignId = #{campaignid,jdbcType=BIGINT}

		and t.bydate = #{lastDay,jdbcType=DATE}


		and t.bydate >= #{lastSeverDayBegin,jdbcType=DATE} 
		and t.bydate <= #{lastSeverDayEnd,jdbcType=DATE}

	group by t.campaignId,t.profileid

select sum(impressions) impressions, sum(clicks) clicks, sum(cost) cost, ifnull((sum(cost) / sum(clicks)),0) avgcost, ifnull((sum(clicks) / sum(impressions)),0) CTR, case when ifnull(sum(cost),0)>0 and ifnull(sum(attributedSales7d),0)=0 then 10000000000 else ifnull((sum(cost) / sum(attributedSales7d)),0) end ACOS, ifnull((sum(attributedSales7d) / sum(cost)),0) ROAS, ifnull(sum(attributedSales7d),0) sumSales, ifnull(sum(attributedUnitsOrdered7d),0) sumUnits, ifnull((sum(attributedConversions7d) / sum(clicks)),0) CSRT, ifnull(sum(attributedConversions7d) - sum(attributedConversions7dSameSKU),0) attributedConversions, ifnull(sum(attributedConversions7dSameSKU),0) attributedConversionsSameSKU, ifnull(sum(attributedConversions7d),0) attributedUnitsOrdered, ifnull(sum(attributedSales7dSameSKU),0) attributedSalesSameSKU, ifnull(sum(attributedSales7d) - sum(attributedSales7dSameSKU),0) attributedSales from t_amz_adv_rpt2_sp_compaigns v left join t_amz_adv_rpt2_sp_compaigns_attributed d on d.bydate=v.bydate and d.campaignId=v.campaignId left join t_amz_adv_rpt2_sp_compaigns_attributed_same s on s.bydate=v.bydate and s.campaignId=v.campaignId left join t_amz_adv_campaigns t on t.campaignId = v.campaignId and t.profileid = v.profileid left join t_amz_adv_profile p on t.profileid = p.id left join t_amz_adv_auth a on a.id=p.advauthId and a.disable = 0 left join t_amazon_group tg on tg.id = a.groupid left join t_marketplace mk on mk.marketplaceId=p.marketplaceId where v.bydate >= #{fromDate,jdbcType=DATE} and v.bydate <= #{endDate,jdbcType=DATE} and a.shopid = #{shopid,jdbcType=CHAR}
and v.profileid = #{profileid,jdbcType=BIGINT}
and t.portfolioid = #{portfolios,jdbcType=BIGINT}
and p.marketplaceId= #{marketplaceid,jdbcType=CHAR}
and a.groupid = #{groupid,jdbcType=CHAR}
and v.campaignId = #{campaignid,jdbcType=BIGINT}
and (t.state = #{campaignStatus,jdbcType=CHAR} or t.state = #{campaignStatusUppercase,jdbcType=CHAR})
and (t.state = 'enabled' or t.state = 'paused' or t.state = 'ENABLED' or t.state = 'PAUSED')
and t.name like #{campaignName,jdbcType=CHAR}
and (t.targetingType = #{targetingType,jdbcType=CHAR} or t.targetingType = #{targetingTypeUppercase,jdbcType=CHAR})
and ${paralist}

	select t.* from t_amz_adv_campaigns t
	where t.profileid=#{profileid,jdbcType=BIGINT}

		and t.name like #{name,jdbcType=CHAR}


	  and t.state =#{campaignStatus,jdbcType=CHAR}


	   and t.state!='archived'

    order by t.name asc
