SELECT a.id authid,g.shopid,d.groupid ,d.marketplaceid ,d.sku ,
	g.name groupname, m.name region, d.currency custcurrency

	,SUM( case when convert(itemid,CHAR) = #{fielditem.value,jdbcType=CHAR} THEN ifnull(amount,0) ELSE 0 END ) ${fielditem.field}

from t_amz_fin_user_item_data d
left join t_marketplace m on m.marketplaceid=d.marketplaceid
left join t_amazon_group g on g.id=d.groupid
left join t_amazon_auth a on a.groupid=g.id and a.region=case when m.region='UK' then 'EU' else m.region end
where a.shop_id=#{shopid,jdbcType=CHAR}


		and d.byday >= #{fromDate,jdbcType=DATE}


		and d.byday <= #{endDate,jdbcType=DATE}




		and d.byday >= #{setfromDate,jdbcType=DATE}


		and d.byday <= #{setendDate,jdbcType=DATE}




	and m.point_name=#{marketplace_name,jdbcType=CHAR}


	and d.groupid = #{groupid,jdbcType=CHAR}


	and d.sku like #{search,jdbcType=CHAR}

group by d.groupid ,d.marketplaceid ,d.sku,d.currency

    SELECT 

		 SUM( case when convert(itemid,CHAR) = #{fielditem.value,jdbcType=CHAR}    THEN 
		                case d.currency

					        when #{key} then #{value}*ifnull(amount,0) 

		                   ELSE 0  END
		         ELSE 0 END ) ${fielditem.field}

	from t_amz_fin_user_item_data d
	where d.shopid =#{shopid,jdbcType=CHAR}

	    and d.groupid=#{groupid,jdbcType=CHAR}


		and d.marketplaceid=#{marketplaceid,jdbcType=CHAR}


		and d.sku=#{sku,jdbcType=CHAR}


		 and d.byday >= #{fromDate,jdbcType=DATE}
		 and d.byday <= #{endDate,jdbcType=DATE}


		 and d.byday >= #{setfromDate,jdbcType=DATE}
		 and d.byday <= #{setendDate,jdbcType=DATE}

     select sum(
	       case currency

			  when #{key} then #{value}*ifnull(rs.spend,0) 

			 ELSE 0  END ) spend
		   from t_amz_advert_report_summary rs 
		   where 1=1

		   		 and rs.bydate >= #{fromDate,jdbcType=DATE} 
		         and  rs.bydate  <= #{endDate,jdbcType=DATE}


		   		 and  rs.bydate >= #{setfromDate,jdbcType=DATE} 
		         and  rs.bydate  <= #{setendDate,jdbcType=DATE}


			     and rs.sellerid=#{sellerid,jdbcType=CHAR} 


			     and rs.marketplaceid=#{marketplaceid,jdbcType=CHAR} 	


			       and rs.sku =#{sku,jdbcType=CHAR}

     select v.*,g.name groupname,case when ns.transaction_type is null then 0 else 1 end nosku 
	     from ( select Max(r.amazonauthid) amazonauthid,
	            max(r.marketplace_name) maketpointname,transaction_type,amount_type,
	            amount_description, sum(
	            case r.currency

				  when #{key} then #{value}*ifnull( r.amount,0) 

				 ELSE 0  END
	           ) amount
			   from  
			   t_amz_settlement_summary_month   r

			    left join t_amz_settlement_acc_report acc on acc.settlement_id=r.settlementid

			   where 1=1

			   and r.posted_date >=#{fromDate,jdbcType=CHAR}
	  	       and r.posted_date <=#{endDate,jdbcType=CHAR}


	  	        and	acc.deposit_date >=#{fromDate,jdbcType=CHAR}
	  	        and acc.deposit_date <=#{endDate,jdbcType=CHAR}


	  	          and acc.amazonauthid=#{amazonauthid,jdbcType=CHAR} 


	  	          and acc.marketplace_name=#{marketplace_name,jdbcType=CHAR} 



				             and r.amazonauthid=#{amazonauthid,jdbcType=CHAR} 


					   and exists(select id from t_amazon_auth where id=r.amazonAuthId and shop_id= #{shopid,jdbcType=CHAR}

									and groupid = #{groupid,jdbcType=CHAR}

								 )  



				and  r.marketplace_name=#{marketplace_name,jdbcType=CHAR} 

	  	     group by transaction_type,amount_type,amount_description ) v
  	     left join t_amazon_auth a on a.id=v.amazonauthid
  	     left join t_amazon_group g on g.id=a.groupid
  	     left join t_amz_settlement_amount_type_nonsku ns on  ns.transaction_type=v.transaction_type AND ns.amount_type=v.amount_type 
          AND (ns.amount_description=v.amount_description OR ns.amount_description='*')

			 select sr.currency currency,sr.snapshot_date,sum(ifnull(ifnull(sr.amount_6,sr.amount_12),0))  longTermFee
					from t_fba_longterm_storage_fee_report  sr
					where 1=1

					     and sr.snapshot_date >= concat(DATE_FORMAT(#{fromDate,jdbcType=DATE},'%Y-%m'),'-01') 
                         and sr.snapshot_date  <= #{endDate,jdbcType=DATE}


					     and sr.snapshot_date >= #{setfromDate,jdbcType=DATE}
                         and sr.snapshot_date  <= #{setendDate,jdbcType=DATE}



			             and amazonauthid=#{amazonauthid,jdbcType=CHAR} 


							and exists(select id from t_amazon_auth where id=sr.amazonAuthId and shop_id= #{shopid,jdbcType=CHAR}

								and groupid = #{groupid,jdbcType=CHAR}

							 )  

			             and country=#{country,jdbcType=CHAR}
			             and sku=#{sku,jdbcType=CHAR} 
			             group by `amazonauthid`, `country`,  `snapshot_date`

        select fr.currency currency,fr.`month`,sum(fr.monthly_storage_fee) storagefee
		from t_fba_storage_fee_report fr 
		where 1=1 

		and fr.`month` >= DATE_FORMAT(#{fromDate,jdbcType=DATE},'%Y-%m')
		and fr.`month` <=DATE_FORMAT(#{endDate,jdbcType=DATE},'%Y-%m')


		and fr.`month` >= DATE_FORMAT(#{setfromDate,jdbcType=DATE},'%Y-%m')
		and fr.`month` <=DATE_FORMAT(#{setendDate,jdbcType=DATE},'%Y-%m')

        and fr.amazonauthid=#{amazonAuthId,jdbcType=CHAR}
        and fr.country =#{country,jdbcType=CHAR} 
        and fr.asin  =#{asin,jdbcType=CHAR} 
        and fr.fnsku  =#{fnsku,jdbcType=CHAR} 
	    group by `amazonauthid`, `country`, `month`

SELECT round(SUM( case 'CNY'
when #{key} then #{value}*ifnull( (trans.singleprice*trans.transweight+IFNULL(trans.otherfee,0))* (case when cfg3.id is not null then (case when cfg3.shipmentStyle='weight' then d.weight when cfg3.shipmentStyle='dim_weight' then (case when d.weight>(d.length*d.width*d.height/ifnull(td.drate,5000)) then d.weight else (d.length*d.width*d.height/ifnull(td.drate,5000)) end) else (case when trans.wtype=0 then d.weight ELSE d.length*d.width*d.height/ifnull(td.drate,5000) END) end) else (case when cfg.shipmentStyle='weight' then d.weight when cfg.shipmentStyle='dim_weight' then (case when d.weight>(d.length*d.width*d.height/ifnull(td.drate,5000)) then d.weight else (d.length*d.width*d.height/ifnull(td.drate,5000)) end) else (case when trans.wtype=0 then d.weight ELSE d.length*d.width*d.height/ifnull(td.drate,5000) END) end) end)
*i.QuantityShipped/ ( SELECT SUM( (case when cfg2.id is not null then (case when cfg2.shipmentStyle='weight' then d2.weight when cfg2.shipmentStyle='dim_weight' then (case when d2.weight>(d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) then d2.weight else (d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) end) else (case when trans.wtype=0 then d2.weight ELSE d2.length*d2.width*d2.height/ifnull(td2.drate,5000) END) end) else (case when cfg1.shipmentStyle='weight' then d2.weight when cfg1.shipmentStyle='dim_weight' then (case when d2.weight>(d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) then d2.weight else (d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) end) else (case when trans.wtype=0 then d2.weight ELSE d2.length*d2.width*d2.height/ifnull(td2.drate,5000) END) end) end) *i2.QuantityShipped) FROM t_erp_ship_inbounditem i2 LEFT JOIN t_erp_ship_inboundshipment s2 ON i2.ShipmentId=s2.ShipmentId AND s2.inboundplanid=i2.inboundplanid LEFT JOIN t_erp_ship_inboundplan p2 ON p2.id=s2.inboundplanid LEFT JOIN t_product_info info2 ON info2.sku=i2.SellerSKU AND info2.marketplaceid=p2.marketplaceid LEFT JOIN t_amazon_auth a2 ON a2.id=info2.amazonAuthId LEFT JOIN t_product_in_opt opt2 ON opt2.pid=info2.id LEFT JOIN t_erp_material m2 ON m2.sku=ifnull(opt2.msku,info2.sku) AND m2.shopid=p2.shopid AND m2.isDelete=0 LEFT JOIN t_dimensions d2 ON d2.id=m2.pkgDimensions left join t_amazon_group gp on gp.id=a2.groupid left join t_profitcfg cfg1 on cfg1.id=gp.profitcfgid left join t_profitcfg cfg2 on cfg2.id=opt2.profitid left join t_erp_ship_inboundtrans trans2 on trans2.shipmentid=s2.ShipmentId left join t_erp_ship_transdetail td2 on trans2.channel=td2.id and td2.company=trans2.company WHERE s2.ShipmentId=s.ShipmentId AND s2.inboundplanid=s.inboundplanid AND a2.groupid=p2.amazongroupid
and s2.status5date >= DATE_FORMAT(#{setfromDate,jdbcType=TIMESTAMP}, '%Y/%m/%d') and s2.status5date <= DATE_FORMAT(#{setendDate,jdbcType=TIMESTAMP}, '%Y/%m/%d')
and s2.status5date >= DATE_FORMAT(#{fromDate,jdbcType=TIMESTAMP}, '%Y/%m/%d') and s2.status5date <= DATE_FORMAT(date_add(#{endDate,jdbcType=TIMESTAMP}, interval 1 day), '%Y/%m/%d')
) ,0)
ELSE 0 END ),2) firstShipment FROM t_erp_ship_inbounditem i LEFT JOIN t_erp_ship_inboundshipment s ON i.ShipmentId=s.ShipmentId AND s.inboundplanid=i.inboundplanid LEFT JOIN t_erp_ship_inboundtrans trans ON trans.shipmentid =s.ShipmentId left join t_erp_ship_transdetail td on trans.channel=td.id and td.company=trans.company LEFT JOIN t_erp_ship_inboundplan p ON p.id=s.inboundplanid LEFT JOIN t_product_info info ON info.sku=i.SellerSKU AND info.marketplaceid=p.marketplaceid LEFT JOIN t_amazon_auth a ON a.id=info.amazonAuthId and a.groupid=p.amazongroupid LEFT JOIN t_product_in_opt opt ON opt.pid=info.id LEFT JOIN t_erp_material m ON m.sku=ifnull(opt.msku,info.sku) AND m.shopid=p.shopid AND m.isDelete=0 LEFT JOIN t_dimensions d ON d.id=m.pkgDimensions LEFT JOIN t_amazon_group g ON g.id=p.amazongroupid left join t_profitcfg cfg on cfg.id=g.profitcfgid left join t_profitcfg cfg3 on cfg3.id=opt.profitid WHERE s.`status`>=5 AND p.shopid=#{shopid,jdbcType=CHAR} AND trans.transweight>0
and s.status5date >= DATE_FORMAT(#{setfromDate,jdbcType=TIMESTAMP}, '%Y/%m/%d') and s.status5date <= DATE_FORMAT(#{setendDate,jdbcType=TIMESTAMP}, '%Y/%m/%d')
and s.status5date >= DATE_FORMAT(#{fromDate,jdbcType=TIMESTAMP} , '%Y/%m/%d') and s.status5date <= DATE_FORMAT(date_add(#{endDate,jdbcType=TIMESTAMP}, interval 1 day) , '%Y/%m/%d')
and i.SellerSKU=#{sku,jdbcType=CHAR}
and p.marketplaceid =#{marketplaceid,jdbcType=CHAR}
and p.amazongroupid=#{groupid,jdbcType=CHAR}

	 select   ifnull(sum(ifnull(rt.quantity,0)),0) refundnum , ifnull(sum(ifnull(rt.mfnqty,0)),0) mfnqty 
              from  t_amz_settlement_summary_returns rt

				    left join t_amz_settlement_acc_report acc on acc.settlement_id=rt.settlementid

              where 1=1

                 and rt.posted_date >= #{fromDate,jdbcType=DATE} 
                 and rt.posted_date  <= #{endDate,jdbcType=DATE}


		  	        and	acc.deposit_date>=#{fromDate,jdbcType=CHAR}
		  	        and acc.deposit_date<=#{endDate,jdbcType=CHAR}


		  	          and acc.amazonauthid=#{amazonauthid,jdbcType=CHAR} 


		  	          and acc.marketplace_name=#{marketplace_name,jdbcType=CHAR} 



				 and rt.amazonAuthId=#{amazonAuthId,jdbcType=CHAR} 


				 and rt.marketplace_name=#{marketplace_name,jdbcType=CHAR} 


				 and rt.sku=#{sku,jdbcType=CHAR} 


					and exists(select id from t_amazon_auth where id=rt.amazonAuthId and shop_id= #{shopid,jdbcType=CHAR}

						and groupid = #{groupid,jdbcType=CHAR}

					)

 	select 

	  sum(v.salenum)  salenum,
	  sum(v.ordernum)  ordernum,	
	        sum(case 'CNY'


			       when #{key} then #{value}*ifnull(ifnull(mt.price,0)*(v.salenum-ifnull(v2.refundnum,0)+ifnull(v2.mfnqty,0)),0) 


			       when #{key} then #{value}*ifnull(ifnull(mt.price,0)*(v.salenum-ifnull(v2.refundnum,0)),0) 


			       when #{key} then #{value}*ifnull(ifnull(mt.price,0)*(v.salenum),0) 


			 ELSE 0  END)  price,	
	    sum(case 'CNY'

			  when #{key} then #{value}*ifnull(ifnull(mt.other_cost,0)*v.salenum,0) 

			 ELSE 0  END)  otherCost,	
	    sum(case m.currency

			  when #{key} then #{value}*ifnull( pip.othersfee*v.salenum,0) 

			 ELSE 0  END)  othersfee,	
	     sum(case m.currency

			  when #{key} then #{value}*ifnull( pip.shipmentfee*v.salenum,0) 

			 ELSE 0  END)  shipmentfee  ,
			 sum(ifnull(d.length,0)*ifnull(d.width,0)*ifnull(d.height,0)) dim
      from 
		(select 
			t.sku,
			t.marketplace_name market,
			t.marketplace_name marketplace_name,
			t.amazonAuthId,
			sum(ifnull(t.sales,0)) salenum,
			sum(ifnull(t.order_amount,0)) ordernum
			from t_amz_settlement_summary_sku t

		    left join t_amz_settlement_acc_report acc on acc.settlement_id=t.settlementid

			where  1=1

			      and t.posted_date >= #{fromDate,jdbcType=DATE}
				  and t.posted_date <= #{endDate,jdbcType=DATE}


  	        and	acc.deposit_date>=#{fromDate,jdbcType=CHAR}
  	        and acc.deposit_date<=#{endDate,jdbcType=CHAR}


  	          and acc.amazonauthid=#{amazonauthid,jdbcType=CHAR} 


  	          and acc.marketplace_name=#{marketplace_name,jdbcType=CHAR} 



				and t.marketplace_name=#{marketplace_name,jdbcType=CHAR}


			and exists(select id from t_amazon_auth where id=t.amazonAuthId and shop_id= #{shopid,jdbcType=CHAR}

				and groupid = #{groupid,jdbcType=CHAR}

			)  


			and t.amazonAuthId= #{amazonAuthId,jdbcType=CHAR}

			group by t.amazonAuthId,t.sku,t.marketplace_name
		) v
		left join (
		    select 
		    t.sku,
			t.marketplace_name market,
			t.marketplace_name marketplace_name,
			t.amazonAuthId,
		    sum(quantity) refundnum,
		    sum(mfnqty) mfnqty
 		    from t_amz_settlement_summary_returns t

		       left join t_amz_settlement_acc_report acc on acc.settlement_id=t.settlementid

			where  1=1

			      and t.posted_date >= #{fromDate,jdbcType=DATE}
				  and t.posted_date <= #{endDate,jdbcType=DATE}


  	        and	acc.deposit_date>=#{fromDate,jdbcType=CHAR}
  	        and acc.deposit_date<=#{endDate,jdbcType=CHAR}


  	          and acc.amazonauthid=#{amazonauthid,jdbcType=CHAR} 


  	          and acc.marketplace_name=#{marketplace_name,jdbcType=CHAR} 



				and t.marketplace_name=#{marketplace_name,jdbcType=CHAR}


			and exists(select id from t_amazon_auth where id=t.amazonAuthId and shop_id= #{shopid,jdbcType=CHAR}

				and groupid = #{groupid,jdbcType=CHAR}

			)  


			and t.amazonAuthId= #{amazonAuthId,jdbcType=CHAR}

	    group by t.amazonAuthId,t.sku,t.marketplace_name
		) v2 on v.marketplace_name=v2.marketplace_name and v.amazonAuthId=v2.amazonAuthId and v.sku=v2.sku
		left join t_amazon_auth au on au.id=v.amazonAuthId
	    left join t_marketplace m on m.point_name=v.market
		left join t_product_info info on info.marketplaceid=m.marketplaceId and info.sku=v.sku and info.amazonauthid=v.amazonAuthId
		left join t_product_in_opt opt on opt.pid=info.id
		left join t_product_in_profit pip on pip.pid=info.id
		left join t_erp_material mt on mt.sku=ifnull(opt.msku,info.sku) and mt.shopid=au.shop_id and mt.isDelete = 0
		left join t_dimensions d on d.id=mt.pkgDimensions

select v.sku, opt.fnsku, mt.id mid, au.groupid, v.market, mt.sku msku, mt.name cname, v.marketplace_name, v.amazonAuthId authid, v.amazonAuthId amazonAuthId, m.marketplaceId marketplaceid, info.asin asin, au.sellerid, v.principal, v.salenum, v.ordernum, v.avgprice, v.commission, v.fbafee, v.refund, v.otherfee, v.setincome, ifnull(mt.price,0)*v.salenum price, ifnull(mt.price,0) itemprice, mt.owner, IFNULL(pic.location,pic.url) image, info.name pname, mt.color, ifnull(mt.other_cost,0) otherCost, ui.name ownername, g.name groupname, m.name region, m.currency, m.market country, pip.othersfee othersfee, ifnull(dim.length,0)*ifnull(dim.width,0)*ifnull(dim.height,0) volume, ifnull(dim.weight,0) weight from (select t.sku, t.marketplace_name market, t.marketplace_name marketplace_name, t.amazonAuthId, case when t.marketplace_name='Amazon.fr' or t.marketplace_name='Amazon.nl' or t.marketplace_name='Amazon.co.uk' or t.marketplace_name='Amazon.de' or t.marketplace_name='Amazon.es' or t.marketplace_name='Amazon.it' then sum(ifnull(t.principal,0))+ sum(ifnull(t.tax,0)) else sum(ifnull(t.principal,0)) end principal, sum(ifnull(t.sales,0)) salenum, sum(ifnull(t.order_amount,0)) ordernum, case when sum(ifnull(t.sales,0)) = 0 then 0 else sum(ifnull(t.principal,0))/sum(ifnull(t.sales,0)) end avgprice, sum(ifnull(t.commission,0)) commission, ABS(sum(ifnull(t.fbafee,0))) fbafee, ABS(sum(ifnull(t.refund,0))) refund, case when t.marketplace_name='Amazon.fr' or t.marketplace_name='Amazon.nl' or t.marketplace_name='Amazon.co.uk' or t.marketplace_name='Amazon.de' or t.marketplace_name='Amazon.es' or t.marketplace_name='Amazon.it' then sum(ifnull(t.otherfee,0)) else sum(ifnull(t.otherfee,0))+ sum(ifnull(t.tax,0)) end otherfee, sum(ifnull(t.principal,0)+ifnull(t.commission,0) +ifnull(t.fbafee,0)+ifnull(t.refund,0) +ifnull(t.otherfee,0)+ifnull(t.tax,0)) setincome from t_amz_settlement_summary_sku t
left join t_amz_settlement_acc_report acc on acc.settlement_id=t.settlementid
where 1=1
and t.posted_date >= #{fromDate,jdbcType=DATE} and t.posted_date <= #{endDate,jdbcType=DATE}
and acc.deposit_date>=#{fromDate,jdbcType=CHAR} and acc.deposit_date<=#{endDate,jdbcType=CHAR}
and acc.amazonauthid=#{amazonauthid,jdbcType=CHAR}
and acc.marketplace_name=#{marketplace_name,jdbcType=CHAR}
and t.marketplace_name=#{marketplace_name,jdbcType=CHAR}
and exists(select id from t_amazon_auth where id=t.amazonAuthId and shop_id= #{shopid,jdbcType=CHAR}
and groupid = #{groupid,jdbcType=CHAR}
)
and t.amazonAuthId= #{amazonAuthId,jdbcType=CHAR}
and t.sku like #{search,jdbcType=CHAR}
group by t.amazonAuthId,t.sku
,t.marketplace_name
) v left join t_amazon_auth au on au.id=v.amazonAuthId left join t_amazon_group g on g.id=au.groupid left join t_marketplace m on m.point_name=v.market left join t_product_info info on info.marketplaceid=m.marketplaceId and info.sku=v.sku and info.amazonauthid=v.amazonAuthId left join t_product_in_opt opt on opt.pid=info.id left join t_product_in_profit pip on pip.pid=info.id left join t_erp_material mt on mt.sku=ifnull(opt.msku,info.sku) and mt.shopid=au.shop_id and mt.isDelete = 0 left join t_picture pic on pic.id=info.image left join t_userinfo ui on ui.id=mt.owner left join t_dimensions dim on dim.id=mt.pkgDimensions where 1=1
and mt.color = #{color,jdbcType=CHAR}
and mt.owner = #{owner,jdbcType=CHAR}
and mt.owner = #{myself,jdbcType=CHAR}

select v.sku, v.groupid, v.market, mt.sku msku, mt.name cname, v.authid, m.marketplaceId marketplaceid, info.asin asin, v.sellerid, v.principal, v.salenum, v.ordernum, v.avgprice, v.commission, v.fbafee, v.refund, v.otherfee, v.setincome, sto.storagefee, term.longTermFee, ifnull(tr.refundnum,0) refundnum,
ifnull(adv.spend,0) spend,
ifnull(mt.price,0)*(v.salenum-ifnull(tr.refundnum,0)+ifnull(tr.mfnqty,0)) price,
ifnull(mt.price,0)*(v.salenum-ifnull(tr.refundnum,0)) price,
ifnull(mt.price,0)*(v.salenum) price,
IFNULL(pic.location,pic.url) image, info.name pname, mt.color,ifnull(mt.other_cost,0) otherCost, v.groupname, m.name region, m.currency, pip.costDetail
,shipment.firstShipment
from (select t.sku, t.marketplace_name market, au.groupid, au.id authid, au.sellerid, au.shop_id shopid, t.marketplace_name marketplace_name, case when au.region='EU' then sum(ifnull(t.principal,0))+ sum(ifnull(t.tax,0)) else sum(ifnull(t.principal,0)) end principal, sum(ifnull(t.sales,0)) salenum, sum(ifnull(t.order_amount,0)) ordernum, case when sum(ifnull(t.sales,0)) = 0 then 0 else sum(ifnull(t.principal,0))/sum(ifnull(t.sales,0)) end avgprice, ABS(sum(ifnull(t.commission,0))) commission, ABS(sum(ifnull(t.fbafee,0))) fbafee, ABS(sum(ifnull(t.refund,0))) refund, case when au.region='EU' then sum(ifnull(t.otherfee,0)) else sum(ifnull(t.otherfee,0))+ sum(ifnull(t.tax,0)) end otherfee, sum(ifnull(t.principal,0)+ifnull(t.commission,0) +ifnull(t.fbafee,0)+ifnull(t.refund,0) +ifnull(t.otherfee,0)+ifnull(t.tax,0)) setincome, g.name groupname from t_amz_settlement_summary_sku t left join t_amazon_auth au on t.amazonAuthId=au.id left join t_amazon_group g on g.id=au.groupid where au.shop_id=#{shopid,jdbcType=CHAR}
and t.posted_date >= #{fromDate,jdbcType=DATE}
and t.posted_date <= #{endDate,jdbcType=DATE}
and t.marketplace_name=#{marketplace_name,jdbcType=CHAR}
and au.groupid = #{groupid,jdbcType=CHAR}
and t.sku like #{search,jdbcType=CHAR}
and t.amazonAuthId= #{amazonAuthId,jdbcType=CHAR}
group by t.amazonAuthId,t.sku,t.marketplace_name) v left join t_marketplace m on m.point_name=v.market left join t_product_info info on info.marketplaceid=m.marketplaceId and info.sku=v.sku and info.amazonauthid=v.authid left join t_product_in_opt opt on opt.pid=info.id left join t_product_in_profit pip on pip.pid=info.id left join t_erp_material mt on mt.sku=ifnull(opt.msku,info.sku) and mt.shopid=v.shopid and mt.isDelete = 0 left join t_picture pic on pic.id=info.image
