and rt.sku like #{search,jdbcType=CHAR}
and a.shop_id=#{shopid,jdbcType=CHAR}
and a.groupid = #{groupid,jdbcType=CHAR}
and rt.amazonAuthId= #{amazonAuthId,jdbcType=CHAR}
and rt.marketplace_name= #{marketplace_name,jdbcType=CHAR}
group by rt.amazonAuthId,rt.marketplace_name,rt.sku ) tr on tr.trsku = v.sku and tr.tramazonAuthId = v.authid and tr.trmarketplace_name = v.market left join (select fr.amazonauthid,fr.country,fr.asin,max(fr.`month`) month, sum( 此处逻辑分为四个部分1：当开始日期与结束日期在同一个月时 费用除以当月天数乘以开始日期与结束日期的相差天数 2：当开始月当前月份吻合，说明要算开始日期到月底的天数对应的分摊费用 3：当结束日期与月份吻合，说明要算月首到结束日期的天数对应的分摊费用 4：当日期不与开始日期和结束日期吻合时，直接使用当月费用
case when month(STR_TO_DATE(#{endDate,jdbcType=DATE},'%Y-%m-%d'))=month(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d')) then fr.monthly_storage_fee/ DATEDIFF(DATE_ADD(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d')-day(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d'))+1,interval 1 MONTH ), DATE_ADD(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d'),interval -day(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d'))+1 DAY)) * (DATEDIFF(STR_TO_DATE(#{endDate,jdbcType=DATE},'%Y-%m-%d'),STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d')) +1)
ELSE case when month(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d'))=month(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d')) then fr.monthly_storage_fee / DATEDIFF(DATE_ADD(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d')-day(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'))+1,interval 1 MONTH ), DATE_ADD(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'),interval -day(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'))+1 DAY)) * DATEDIFF(DATE_ADD(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d')-day(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'))+1,interval 1 MONTH ), STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d')) ELSE case when month(STR_TO_DATE(#{endDate,jdbcType=DATE},'%Y-%m-%d'))=month(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d')) then fr.monthly_storage_fee / DATEDIFF(DATE_ADD(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d')-day(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'))+1,interval 1 MONTH ), DATE_ADD(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'),interval -day(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'))+1 DAY)) * DATEDIFF(STR_TO_DATE(#{endDate,jdbcType=DATE},'%Y-%m-%d'), DATE_ADD(STR_TO_DATE(CONCAT(fr.`month`,'-01'),'%Y-%m-%d')-day(STR_TO_DATE(CONCAT(`month`,'-01'),'%Y-%m-%d'))+1,INTERVAL -1 DAY)) ELSE fr.monthly_storage_fee END
END
END ) storagefee from t_fba_storage_fee_report fr left join t_amazon_auth a on a.id=fr.amazonauthid where fr.`month`>= DATE_FORMAT(#{fromDate,jdbcType=DATE},'%Y-%m') and fr.`month` <=DATE_FORMAT(#{endDate,jdbcType=DATE},'%Y-%m') and a.shop_id=#{shopid,jdbcType=CHAR}
