ELSE case when month(STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d'))=month(snapshot_date) then ifnull(ifnull(sr.amount_6,sr.amount_12),0) / DATEDIFF(DATE_ADD(snapshot_date-day(snapshot_date)+1,interval 1 MONTH ), DATE_ADD(snapshot_date,interval -day(snapshot_date)+1 DAY)) * DATEDIFF(DATE_ADD(snapshot_date-day(snapshot_date)+1,interval 1 MONTH ), STR_TO_DATE(#{fromDate,jdbcType=DATE},'%Y-%m-%d')) ELSE case when month(STR_TO_DATE(#{endDate,jdbcType=DATE},'%Y-%m-%d'))=month(snapshot_date) then ifnull(ifnull(sr.amount_6,sr.amount_12),0) / DATEDIFF(DATE_ADD(snapshot_date-day(snapshot_date)+1,interval 1 MONTH ), DATE_ADD(snapshot_date,interval -day(snapshot_date)+1 DAY)) * DATEDIFF(STR_TO_DATE(#{endDate,jdbcType=DATE},'%Y-%m-%d'), DATE_ADD(snapshot_date-day(snapshot_date)+1,INTERVAL -1 DAY)) ELSE ifnull(ifnull(sr.amount_6,sr.amount_12),0) END
END
END
) longTermFee from t_fba_longterm_storage_fee_report sr left join t_amazon_auth a on a.id=sr.amazonauthid where sr.snapshot_date >= concat(DATE_FORMAT(#{fromDate,jdbcType=DATE},'%Y-%m'),'-01') and sr.snapshot_date <= #{endDate,jdbcType=DATE} and a.shop_id=#{shopid,jdbcType=CHAR}
