mapper.select:
		select  
		   sku,
           max(currency) currency,
		   sum(rs.spend)   spend,
		   sum(rs.totalsales) totalsales,
		   sum(rs.units) units
	   from t_amz_advert_report_summary_month rs 
	   where rs.sellerid=#{sellerid,jdbcType=CHAR} 
	   		  and rs.bydate >=  #{fromDate,jdbcType=DATE} 
	          and  rs.bydate < #{endDate,jdbcType=DATE}
		      and rs.marketplaceid=#{marketplaceid,jdbcType=CHAR} 	
		      GROUP BY rs.sku

select
m.sku,max(m.asin) asin,
m.msku,
m.categoryid,
m.owner,
m.groupid,
m.asin,
m.parentasin,max(g.id) groupid, max(mk.marketplaceid) marketplaceid, sum(case when m.parentasin=m.asin then 0 else 1 end ) subnum,
m.posted_date,
max(m.currency) currency, max(g.name) groupname, max(mk.name) marketname, max(i.name) pname, max(mt.name) mname, max(ifnull(p.location,p.url)) image, max(c.name) categoryname, max(u.name) ownername, sum(m.`order_amount`) `order_amount`, sum(m.`sales`) `sales`, sum(m.`refundsales`) `refundsales`, sum(m.`refundorder`) `refundorder`, SUM(CASE WHEN m.marketplace_name = 'Amazon.fr' OR m.marketplace_name = 'Amazon.nl' OR m.marketplace_name = 'Amazon.co.uk' OR m.marketplace_name = 'Amazon.de' OR m.marketplace_name = 'Amazon.es' OR m.marketplace_name = 'Amazon.it' THEN IFNULL(m.principal, 0) + IFNULL(m.tax, 0) ELSE IFNULL(m.principal, 0) END) principal, sum(m.`commission`) `commission`, sum(m.`fbafee`) `fbafee`, sum(m.`refund`) `refund`, sum(m.`shipping`) `shipping`, sum(m.`promotion`) `promotion`, SUM(CASE WHEN m.marketplace_name = 'Amazon.fr' OR m.marketplace_name = 'Amazon.nl' OR m.marketplace_name = 'Amazon.co.uk' OR m.marketplace_name = 'Amazon.de' OR m.marketplace_name = 'Amazon.es' OR m.marketplace_name = 'Amazon.it' THEN IFNULL(m.otherfee, 0) ELSE IFNULL(m.otherfee, 0) + IFNULL(m.tax, 0) END) otherfee,
sum(ifnull(m.principal,0)+ifnull(m.commission,0) +ifnull(m.fbafee,0)+ifnull(m.refund,0) +ifnull(m.otherfee,0)+ifnull(m.tax,0)+ifnull(m.shipping,0)+ifnull(m.promotion,0)) setincome, sum(m.`share_storage_fee`) `share_storage_fee`, sum(m.`share_long_storage_fee`) `share_long_storage_fee`, sum(m.`share_adv_spend_fee`) `share_adv_spend_fee`, sum(m.`share_coupon_redemption_fee`) `share_coupon_redemption_fee`, sum(m.`share_reserve_fee`) `share_reserve_fee`, sum(m.`share_shop_other_fee`) `share_shop_other_fee`, sum(m.share_reimbursement_fee) share_reimbursement_fee, sum( ifnull(m.principal,0) +ifnull(m.commission,0) +ifnull(m.fbafee,0) +ifnull(m.refund,0) +ifnull(m.otherfee,0) +ifnull(m.tax,0) +ifnull(m.shipping,0) +ifnull(m.promotion,0) +ifnull(m.share_storage_fee,0) +ifnull(m.share_long_storage_fee,0) +ifnull(m.share_adv_spend_fee,0) +ifnull(m.share_coupon_redemption_fee,0) +ifnull(m.share_reserve_fee,0) +ifnull(m.share_shop_other_fee,0) +ifnull(m.share_reimbursement_fee,0) ) income, sum(m.`local_price`) `local_price`, sum(m.`local_other_cost`) `local_other_cost`, sum(m.`local_return_tax`) `local_return_tax`, sum(m.`local_shipment_item_fee`) `local_shipment_item_fee`, sum(m.`local_fifo_shipment_fee`) `local_fifo_shipment_fee`, sum(m.`local_fifo_cost`) `local_fifo_cost`, sum(m.`profit_local_shipmentfee`) `profit_local_shipmentfee`, sum(m.`profit_vat`) `profit_vat`, sum(m.`profit_companytax`) `profit_companytax`, sum(m.`profit_customstax`) `profit_customstax`, sum(m.`profit_exchangelost`) `profit_exchangelost`, sum(m.`profit_lostrate`) `profit_lostrate`, sum(m.`profit_marketfee`) `profit_marketfee`, sum(m.`profit_otherfee`) `profit_otherfee`, sum(m.`rpt_storage_fee`) `rpt_storage_fee`, sum(m.`rpt_long_storage_fee`) `rpt_long_storage_fee`, sum(m.`rpt_adv_spend_fee`) `rpt_adv_spend_fee`, sum(m.`rpt_adv_sales`) `rpt_adv_sales`, sum(m.`rpt_adv_units`) `rpt_adv_units`, sum(m.`rpt_reimbursements_fee`) `rpt_reimbursements_fee`, sum(m.`fin_sum_fee`) `fin_sum_fee`, sum(m.`profit`) `profit`, max(mt.price) `price`, max(m.mid) `mid` from
t_amz_settlement_summary_sku_month m
t_amz_settlement_summary_sku_month_cny m
t_amz_settlement_summary_sku_month_usd m
left join t_product_info i on i.id=m.pid left join t_marketplace mk on mk.point_name=m.marketplace_name left join t_erp_material mt on mt.id=m.mid left join t_erp_material_category c on c.id=m.categoryid left join t_picture p on p.id=i.image left join t_amazon_auth a on a.id=m.amazonAuthId left join t_amazon_group g on g.id=a.groupid left join t_userinfo u on u.id=m.owner where a.shop_id=#{param.shopid,jdbcType=CHAR}
and m.asin like #{param.search,jdbcType=CHAR}
and m.sku like #{param.search,jdbcType=CHAR}
and m.msku like #{param.search,jdbcType=CHAR}
and m.parentasin like #{param.search,jdbcType=CHAR}
and i.name like #{param.search,jdbcType=CHAR}
and m.marketplace_name=#{param.marketplaceName,jdbcType=CHAR}
and m.owner=#{param.owner,jdbcType=CHAR}
and m.groupid=#{param.groupid,jdbcType=CHAR}
and m.posted_date>=#{param.fromDate,jdbcType=CHAR}
and m.posted_date<=#{param.endDate,jdbcType=CHAR}
and m.parentasin =#{param.parentasin,jdbcType=CHAR}
and m.categoryid =#{param.categoryid,jdbcType=CHAR}
group by m.amazonAuthId,m.marketplace_name,m.sku
group by m.msku
group by m.categoryid
group by m.owner
group by m.groupid
group by m.amazonAuthId,m.marketplace_name,m.asin
group by m.amazonAuthId,m.marketplace_name,m.parentasin
group by m.posted_date

select max(m.currency) currency, sum(m.`order_amount`) `order_amount`, sum(m.`sales`) `sales`, sum(m.`refundsales`) `refundsales`, sum(m.`refundorder`) `refundorder`, sum(case when m.marketplace_name='Amazon.fr' or m.marketplace_name='Amazon.nl' or m.marketplace_name='Amazon.co.uk' or m.marketplace_name='Amazon.de' or m.marketplace_name='Amazon.es' or m.marketplace_name='Amazon.it' then ifnull(m.principal,0)+ ifnull(m.tax,0) else ifnull(m.principal,0) end) principal, sum(m.`commission`) `commission`, sum(m.`fbafee`) `fbafee`, sum(m.`refund`) `refund`, sum(m.`shipping`) `shipping`, sum(m.`promotion`) `promotion`,
sum(case when m.marketplace_name='Amazon.fr' or m.marketplace_name='Amazon.nl' or m.marketplace_name='Amazon.co.uk' or m.marketplace_name='Amazon.de' or m.marketplace_name='Amazon.es' or m.marketplace_name='Amazon.it' then ifnull(m.otherfee,0) else ifnull(m.otherfee,0)+ifnull(m.tax,0) end) otherfee,
sum(ifnull(m.principal,0)+ifnull(m.commission,0) +ifnull(m.fbafee,0)+ifnull(m.refund,0) +ifnull(m.otherfee,0)+ifnull(m.tax,0)+ifnull(m.shipping,0)+ifnull(m.promotion,0)) setincome, sum(m.`share_storage_fee`) `share_storage_fee`, sum(m.`share_long_storage_fee`) `share_long_storage_fee`, sum(m.`share_adv_spend_fee`) `share_adv_spend_fee`, sum(m.`share_coupon_redemption_fee`) `share_coupon_redemption_fee`, sum(m.`share_reserve_fee`) `share_reserve_fee`, sum(m.`share_shop_other_fee`) `share_shop_other_fee`, sum( ifnull(m.principal,0) +ifnull(m.commission,0) +ifnull(m.fbafee,0) +ifnull(m.refund,0) +ifnull(m.otherfee,0) +ifnull(m.tax,0) +ifnull(m.shipping,0) +ifnull(m.promotion,0) +ifnull(m.share_storage_fee,0) +ifnull(m.share_long_storage_fee,0) +ifnull(m.share_adv_spend_fee,0) +ifnull(m.share_coupon_redemption_fee,0) +ifnull(m.share_reserve_fee,0) +ifnull(m.share_shop_other_fee,0) +ifnull(m.share_reimbursement_fee,0) ) income, sum(m.`local_price`) `local_price`, sum(m.`local_other_cost`) `local_other_cost`, sum(m.`local_return_tax`) `local_return_tax`, sum(m.`local_shipment_item_fee`) `local_shipment_item_fee`, sum(m.`local_fifo_shipment_fee`) `local_fifo_shipment_fee`, sum(m.`local_fifo_cost`) `local_fifo_cost`, sum(m.`profit_local_shipmentfee`) `profit_local_shipmentfee`, sum(m.`profit_vat`) `profit_vat`, sum(m.`profit_companytax`) `profit_companytax`, sum(m.`profit_customstax`) `profit_customstax`, sum(m.`profit_exchangelost`) `profit_exchangelost`, sum(m.`profit_lostrate`) `profit_lostrate`, sum(m.`profit_marketfee`) `profit_marketfee`, sum(m.`profit_otherfee`) `profit_otherfee`, sum(m.`rpt_storage_fee`) `rpt_storage_fee`, sum(m.`rpt_long_storage_fee`) `rpt_long_storage_fee`, sum(m.`rpt_adv_spend_fee`) `rpt_adv_spend_fee`, sum(m.`rpt_adv_sales`) `rpt_adv_sales`, sum(m.`rpt_adv_units`) `rpt_adv_units`, sum(m.`rpt_reimbursements_fee`) `rpt_reimbursements_fee`, sum(m.`fin_sum_fee`) `fin_sum_fee`, sum(m.`profit`) `profit` from
t_amz_settlement_summary_sku_month m
t_amz_settlement_summary_sku_month_cny m
t_amz_settlement_summary_sku_month_usd m
left join t_amazon_auth a on a.id=m.amazonAuthId left join t_product_info i on i.id=m.pid where a.shop_id=#{param.shopid,jdbcType=CHAR}
and m.marketplace_name=#{param.marketplaceName,jdbcType=CHAR}
and m.owner=#{param.owner,jdbcType=CHAR}
and m.groupid=#{param.groupid,jdbcType=CHAR}
and m.posted_date>=#{param.fromDate,jdbcType=CHAR}
and m.posted_date<=#{param.endDate,jdbcType=CHAR}
and m.asin like #{param.search,jdbcType=CHAR}
and m.sku like #{param.search,jdbcType=CHAR}
and m.msku like #{param.search,jdbcType=CHAR}
and m.parentasin like #{param.search,jdbcType=CHAR}
and i.name like #{param.search,jdbcType=CHAR}

mapper.delete:
delete from t_amz_settlement_summary_sku_month where amazonAuthId=#{amazonAuthId,jdbcType=CHAR}  
and marketplace_name=#{marketplaceName,jdbcType=CHAR}  and posted_date=#{fromDate,jdbcType=DATE};
delete from t_amz_settlement_summary_sku_month_usd where amazonAuthId=#{amazonAuthId,jdbcType=CHAR}  
and marketplace_name=#{marketplaceName,jdbcType=CHAR}  and posted_date=#{fromDate,jdbcType=DATE};
delete from t_amz_settlement_summary_sku_month_cny where amazonAuthId=#{amazonAuthId,jdbcType=CHAR}  
and marketplace_name=#{marketplaceName,jdbcType=CHAR}  and posted_date=#{fromDate,jdbcType=DATE};
