mapper.select:
select sum(case when (ifnull(item.QuantityShipped,0)-ifnull(item.QuantityReceived,0))>=0 then ifnull(item.QuantityShipped,0)-ifnull(item.QuantityReceived,0) else 0 end ) quantity from t_erp_ship_inbounditem item left join t_erp_ship_inboundshipment inbound on inbound.ShipmentId=item.ShipmentId left join t_erp_ship_inboundPlan plan on plan.id=item.inboundplanid left join t_erp_warehouse w on w.id = plan.warehouseid left join t_amazon_group ag on ag.id=plan.amazongroupid left join t_marketplace m on m.marketplaceId=plan.marketplaceid where item.SellerSKU = #{sku,jdbcType=CHAR}
and m.region='EU'
and plan.marketplaceid= #{marketplaceid,jdbcType=CHAR}
and plan.createdate>ADDDATE(NOW(), INTERVAL -1 year) and plan.amazongroupid= #{groupid,jdbcType=CHAR} and inbound.status>=2 and inbound.status <6

SELECT * FROM ( select plan.marketplaceid warehouse, de.transtype, max(mkp.name) marketname, max(tt.name) name, sum(ifnull(dd.weight,0)*item.Quantity) readweight, SUM(item.Quantity) totalqty, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped else 0 end ) totalout, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived else 0 end ) totalrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived-item.QuantityShipped else 0 end ) lessrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped*m.price else 0 end ) worth, sum(case when shipment.`status` >=2 and shipment.`status`<=5 then item.QuantityShipped else 0 end ) needrec, sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.Quantity-item.QuantityReceived else 0 end ) needout from t_erp_ship_inbounditem item left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_material m on m.sku=item.SellerSKU and plan.shopid=m.shopid and m.isDelete = 0 left join t_Dimensions dd on dd.id=m.pkgDimensions left join t_erp_ship_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transchannel ch on ch.id=de.channel left join t_erp_transtype tt on tt.id=de.transtype left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{param.shopid,jdbcType=CHAR} and shipment.status>=5 and com.`name` is not null
and plan.createdate >=#{param.fromDate,jdbcType=DATE} and plan.createdate <=#{param.endDate,jdbcType=DATE}
and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE} and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}
and w.id=#{param.warehouseid,jdbcType=CHAR}
and de.company =#{param.companyid,jdbcType=CHAR}
and trans.channel=#{param.channelid,jdbcType=CHAR}
and (com.`name` like #{param.search,jdbcType=CHAR} )
and (mkp.`market` like #{param.search,jdbcType=CHAR} or mkp.region like #{param.search,jdbcType=CHAR} )
group by plan.marketplaceid,de.transtype ) v left join ( select plan.marketplaceid warehouse, de.transtype, sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg, sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm, sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee, sum(trans.otherfee) totalotherfee, count(shipment.shipmentid) shipmentnum,sum(shipment.boxnum) totalbox, ROUND(avg(ifnull(datediff(shipment.start_receive_date, shipment.shiped_date),0)),2) avgtime, count(if(shipment.status='-1',true,null )) problem FROM t_erp_ship_inboundshipment shipment left join t_erp_ship_inboundplan plan on plan.id=shipment.inboundplanid left join t_marketplace mkp on mkp.marketplaceid=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.ShipmentId left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{param.shopid,jdbcType=CHAR} and (shipment.status>=5 or shipment.start_receive_date is not null) and com.`name` is not null
and plan.createdate >=#{param.fromDate,jdbcType=DATE} and plan.createdate <=#{param.endDate,jdbcType=DATE}
and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE} and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}
and w.id=#{param.warehouseid,jdbcType=CHAR}
and de.company =#{param.companyid,jdbcType=CHAR}
and trans.channel=#{param.channelid,jdbcType=CHAR}
and (com.`name` like #{param.search,jdbcType=CHAR} )
and (mkp.`market` like #{param.search,jdbcType=CHAR} or mkp.region like #{param.search,jdbcType=CHAR} )
group by plan.marketplaceid,de.transtype ) n on v.transtype=n.transtype and v.warehouse =n.warehouse

SELECT * FROM ( select
item.SellerSKU sku,
de.id, max(de.company) company, max(com.`name`) logitics, max(de.channame) channame, max(de.subarea) subarea, max(ch.name) channelname, max(tt.name) transtype,
case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end warehouse,
sum(ifnull(dd.weight,0)*item.Quantity) readweight, SUM(item.Quantity) totalqty, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped else 0 end ) totalout, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived else 0 end ) totalrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived-item.QuantityShipped else 0 end ) lessrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped*m.price else 0 end ) worth, sum(case when shipment.`status` >=2 and shipment.`status`<=5 then item.QuantityShipped else 0 end ) needrec, sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.Quantity-item.QuantityReceived else 0 end ) needout from t_erp_ship_inbounditem item left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_material m on m.sku=item.SellerSKU and plan.shopid=m.shopid and m.isDelete = 0 left join t_Dimensions dd on dd.id=m.pkgDimensions left join t_erp_ship_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transchannel ch on ch.id=de.channel left join t_erp_transtype tt on tt.id=de.transtype left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{param.shopid,jdbcType=CHAR} and shipment.status>=5 and com.`name` is not null
and plan.createdate >=#{param.fromDate,jdbcType=DATE} and plan.createdate <=#{param.endDate,jdbcType=DATE}
and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE} and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}
and w.id=#{param.warehouseid,jdbcType=CHAR}
and de.company =#{param.companyid,jdbcType=CHAR}
and trans.channel=#{param.channelid,jdbcType=CHAR}
and (com.`name` like #{param.search,jdbcType=CHAR} )
and (mkp.`market` like #{param.search,jdbcType=CHAR} or mkp.region like #{param.search,jdbcType=CHAR} )
group by de.id
group by case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end
group by item.SellerSKU
) v
left join ( select
de.id,
case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end warehouse,
sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg, sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm, sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee, sum(trans.otherfee) totalotherfee, count(shipment.shipmentid) shipmentnum,sum(shipment.boxnum) totalbox, ROUND(avg(ifnull(datediff(shipment.start_receive_date, shipment.shiped_date),0)),2) avgtime, count(if(shipment.status='-1',true,null )) problem FROM t_erp_ship_inboundshipment shipment left join t_erp_ship_inboundplan plan on plan.id=shipment.inboundplanid left join t_marketplace mkp on mkp.marketplaceid=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.ShipmentId left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{param.shopid,jdbcType=CHAR} and (shipment.status>=5 or shipment.start_receive_date is not null) and com.`name` is not null
and plan.createdate >=#{param.fromDate,jdbcType=DATE} and plan.createdate <=#{param.endDate,jdbcType=DATE}
and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE} and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}
and w.id=#{param.warehouseid,jdbcType=CHAR}
and de.company =#{param.companyid,jdbcType=CHAR}
and trans.channel=#{param.channelid,jdbcType=CHAR}
and (com.`name` like #{param.search,jdbcType=CHAR} )
and (mkp.`market` like #{param.search,jdbcType=CHAR} or mkp.region like #{param.search,jdbcType=CHAR} )
group by de.id
group by case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end
) n on
v.id=n.id
v.warehouse =n.warehouse

