mapper.select:
select  max(form.name) shipname,
	    form.*,
	    max(DATE_FORMAT(form.createdate, '%Y/%m/%d')) createdate2,
		max(addre.id) addressid,
		max(wh.name) warename,
		max(tm.market) country,
		max(u.name) creatorname,
		max(form.skunum) skucount,
		sum(entry.Quantity) sumquantity, 
		max(tm.name) destination, 
		max(ag.name) groupname
from t_erp_ship_inboundplan form
left join t_amazon_group ag on ag.id=form.amazongroupid
left join t_erp_ship_address addre on addre.id=form.shipFromAddressID
left join t_erp_ship_inbounditem entry on entry.inboundplanid=form.id
left join t_userinfo u on u.id=form.creator
left join t_marketplace tm on tm.marketplaceId=form.marketplaceid
left join t_erp_warehouse wh on wh.id=form.warehouseid
where form.shopid =#{param.shopid,jdbcType=CHAR}

	and form.auditstatus=#{param.auditstatus,jdbcType=INTEGER}


	and form.createdate >= #{param.fromDate,jdbcType=TIMESTAMP}
	and form.createdate <= #{param.toDate,jdbcType=TIMESTAMP}


	and form.amazongroupid= #{param.groupid,jdbcType=CHAR}


	and wh.id= #{param.warehouseid,jdbcType=CHAR}


	and form.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}


	and form.id= #{param.planid,jdbcType=CHAR}



	 and form.number like #{param.search,jdbcType=CHAR}


	 and form.remark like #{param.search,jdbcType=CHAR}


	and  entry.SellerSKU like #{param.search,jdbcType=CHAR}


	 and  form.name like #{param.search,jdbcType=CHAR}


group by form.id

select max(si.ShipmentId) ShipmentId, max(sm.ShipToAddressID) ShipToAddressID , max(sm.transtyle) transtyle, max(sm.DestinationFulfillmentCenterId) DestinationFulfillmentCenterId, count(si.SellerSKU) skucount, sum(si.Quantity) toquantity, max(sm.name) name, sum(dd.weight*si.Quantity) readweight, max(dd.weight_units) readunit, max(sm.status) status, sum(case when ifnull(dd.length,0)*ifnull(dd.width,0)*ifnull(dd.height,0)/5000 > dd.weight then ifnull(dd.length,0)*ifnull(dd.width,0)*ifnull(dd.height,0)/5000*si.Quantity else dd.weight*si.Quantity end) weight, SUM(case when ifnull(m.boxnum,0) > 0 and ifnull(dm2.id,0) > 0 then ifnull(ifnull(si.Quantity/m.boxnum,0)*(dm2.length*dm2.width*dm2.height),0)/1000000 else ifnull(si.Quantity*(dd.length*dd.width*dd.height),0)/1000000 end) boxvolume, max(dd.weight_units) weightunit, sum(m.price*si.Quantity) itemprice , max(sm.remark) remark, max(tc.name) company, max(td.channame) channel, max(tc.id) companyid, max(td.id) channelid from t_erp_ship_inbounditem si left join t_erp_ship_inboundplan sp on sp.id=si.inboundplanid left join t_erp_ship_inboundshipment sm on si.ShipmentId=sm.ShipmentId left join t_erp_ship_inboundtrans ts on ts.shipmentid=si.shipmentid left join t_erp_ship_transcompany tc on tc.id=ts.company left join t_erp_ship_transdetail td on td.company=ts.company and td.id=ts.channel left join t_marketplace mkt on mkt.marketplaceid=sp.marketplaceid left join t_amazon_auth auth on auth.groupid=sp.amazongroupid and auth.region=case when mkt.region='UK' then 'EU' ELSE mkt.region end left join t_product_info pro on pro.marketplaceid=sp.marketplaceid and pro.sku=si.SellerSKU and pro.amazonAuthId=auth.id left join t_product_in_opt o on o.pid=pro.id left join t_erp_material m on m.sku=ifnull(o.msku,pro.sku) and m.shopid=sp.shopid and m.isDelete=0 left join t_Dimensions dd on dd.id=m.pkgDimensions left join t_Dimensions dm2 on dm2.id=m.boxDimensions left join t_erp_ship_addressto addre on addre.id=sm.ShipToAddressID where 1=1
and sp.id=#{planid,jdbcType=CHAR}
and sm.ShipmentId=#{shipmentid,jdbcType=CHAR}
group by sm.ShipmentId

SELECT b.ShipmentId shipmentid,a.marketplaceid,b.SellerSKU,chan.channame channel,company.simplename company, ifnull(b.QuantityReceived,0) reveiveQuantity, ts.arrivalTime,a.createdate, c.shiped_date ,ifnull(b.QuantityShipped,b.Quantity) Quantity, ts.outarrtime ,ts.inarrtime , case c.status when 1 then '等待确认' when 2 then '配货' when 0 then '取消货件' when 3 then '装箱' when 4 then '待发货' when 5 then '已发货' when 6 then '已完成' else '--' end statusName, c.status from t_erp_ship_inboundplan a LEFT JOIN t_erp_ship_inbounditem b on a.id=b.inboundplanid LEFT JOIN t_erp_ship_inboundshipment c on a.id=c.inboundplanid and c.ShipmentId = b.ShipmentId LEFT JOIN t_erp_ship_inboundtrans ts on ts.shipmentid=c.shipmentid LEFT JOIN t_erp_ship_transdetail chan on chan.id=ts.channel LEFT JOIN t_erp_ship_transcompany company on company.id=ts.company LEFT JOIN t_erp_ship_status d on c.ShipmentStatus=d.`status` left join t_marketplace m on m.marketplaceid=a.marketplaceid where a.shopid=#{shopid,jdbcType=CHAR} and ((a.auditstatus=1 and c.status=1) or (c.status in (2,3,4,5))) and (m.marketplaceid=#{marketplaceid,jdbcType=CHAR} or m.region=#{marketplaceid,jdbcType=CHAR})
and a.amazongroupid=#{groupid,jdbcType=CHAR}
and b.SellerSKU=#{sku,jdbcType=CHAR}
and a.createdate > DATE_SUB(NOW(), INTERVAL 1 YEAR) ORDER BY a.createdate desc

select

	date_format(plan.createdate,'%Y-%m-%d') createdate,


	date_format(subdate(plan.createdate,date_format(plan.createdate,'%w')-7),'%Y-%m-%d') createdate,


	concat( year(plan.createdate) ,'-' ,month(plan.createdate)) createdate,

	ifnull(sum(item.QuantityShipped),0) spqty
from t_erp_ship_inbounditem item
left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid
left join t_erp_ship_inboundshipment ship on ship.shipmentid=item.shipmentid
left join t_erp_warehouse w on w.id=plan.warehouseid
where plan.shopid=#{shopid,jdbcType=CHAR} 
	and ship.`status` >=5

	and item.SellerSKU=#{sku,jdbcType=CHAR}


	and w.parentid =#{warehouseid,jdbcType=CHAR}


	and plan.amazongroupid =#{amazongroup,jdbcType=CHAR}


	and plan.marketplaceid =#{marketplaceid,jdbcType=CHAR}


	and plan.createdate >=#{begindate,jdbcType=CHAR}


	and plan.createdate <=#{enddate,jdbcType=CHAR}



	group by date_format(plan.createdate,'%Y-%m-%d')


	group by date_format(subdate(plan.createdate,date_format(plan.createdate,'%w')-7),'%Y-%m-%d')


	group by concat( year(plan.createdate) ,'-' ,month(plan.createdate))

select item.SellerSKU sku,ship.ShipmentId,inp.number,g.name
	groupname,w.name warehouse,mkp.market,ship.shiped_date,
	trans.arrivalTime,item.QuantityShipped,item.QuantityReceived,inp.createdate,
	ship.DestinationFulfillmentCenterId center ,detail.channame ,
	case when stat.`status`='WORKING' and ship.status>=5 then '已删除' else stat.name end shipmentstatus,
	ifnull(item.QuantityReceived,0) sumrec,ship.status,
	status0date,status1date,status2date,status3date,status4date,status5date,status6date
from t_erp_ship_inbounditem item
left join t_erp_ship_inboundshipment ship on ship.ShipmentId =item.ShipmentId
left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.ShipmentId
left join t_erp_ship_transdetail detail on detail.id = trans.channel
left join t_erp_ship_transcompany cop on cop.id=detail.company
left join t_erp_ship_status stat on stat.`status`=ship.ShipmentStatus
left join t_erp_ship_inboundplan inp on inp.id=item.inboundplanid
left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid
left join t_amazon_group g on g.id=inp.amazongroupid
left join t_erp_warehouse w on w.id=inp.warehouseid
where inp.shopid =#{param.shopid,jdbcType=CHAR}
	and ship.status>1


		and inp.createdate >=#{param.fromDate,jdbcType=TIMESTAMP}
		and inp.createdate <=#{param.endDate,jdbcType=TIMESTAMP}


		and ship.shiped_date >=#{param.fromDate,jdbcType=TIMESTAMP}
		and ship.shiped_date <=#{param.endDate,jdbcType=TIMESTAMP}



	and inp.amazongroupid= #{param.groupid,jdbcType=CHAR}


	and inp.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}


	and (item.SellerSKU like #{param.search,jdbcType=CHAR} 
	or ship.ShipmentId like #{param.search,jdbcType=CHAR} or inp.number like #{param.search,jdbcType=CHAR} )


	and cop.name like #{param.company,jdbcType=CHAR} 


	and w.id = #{param.warehouseid,jdbcType=CHAR} 



		and item.QuantityReceived!=item.QuantityShipped


		and item.QuantityReceived=item.QuantityShipped

select item.SellerSKU sku,ship.ShipmentId,inp.number,g.name groupname, w.name warehouse,mkp.market,ship.shiped_date, trans.arrivalTime,item.QuantityShipped,item.QuantityReceived,inp.createdate, ship.DestinationFulfillmentCenterId center ,detail.channame , case when stat.`status`='WORKING' and ship.status>=5 then '已删除' else stat.name end shipmentstatus, ifnull(item.QuantityReceived,0) sumrec,ship.status, status0date,status1date,status2date,status3date,status4date,status5date,status6date, m.sku mainsku,sub.sku subsku,ifnull(sub.name,m.name) name, ass.subnumber,ifnull(ass.subnumber,0)*item.QuantityShipped subskuout from t_erp_ship_inbounditem item left join t_erp_ship_inboundshipment ship on ship.ShipmentId =item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.ShipmentId left join t_erp_ship_transdetail detail on detail.id = trans.channel left join t_erp_ship_status stat on stat.`status`=ship.ShipmentStatus left join t_erp_ship_inboundplan inp on inp.id=item.inboundplanid left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid left join t_amazon_group g on g.id=inp.amazongroupid left join t_amazon_auth a on a.groupid=g.id and a.region=case when mkp.region='UK' then 'EU' ELSE mkp.region end left join t_product_info p on p.sku=item.SellerSKU and p.marketplaceid=inp.marketplaceid and p.amazonauthid=a.id left join t_product_in_opt opt on p.id=opt.pid left join t_erp_material m on m.sku=IFNULL(opt.msku,p.sku) and m.shopid=inp.shopid and m.isDelete=0 left join t_erp_assembly ass on ass.mainmid=m.id left join t_erp_material sub on sub.id=submid left join t_erp_warehouse w on w.id=inp.warehouseid where inp.shopid =#{shopid,jdbcType=CHAR} and ship.status>1
and inp.createdate >=#{fromDate,jdbcType=TIMESTAMP} and inp.createdate <=#{endDate,jdbcType=TIMESTAMP}
and ship.shiped_date>=#{fromDate,jdbcType=TIMESTAMP} and ship.shiped_date<=#{endDate,jdbcType=TIMESTAMP}
and inp.amazongroupid= #{groupid,jdbcType=CHAR}
and inp.marketplaceid= #{marketplaceid,jdbcType=CHAR}
and (item.SellerSKU like #{search,jdbcType=CHAR} or ship.ShipmentId like #{search,jdbcType=CHAR} or inp.number like #{search,jdbcType=CHAR} )

select sum(item.QuantityShipped) QuantityShipped, sum(item.QuantityReceived) QuantityReceived
from t_erp_ship_inbounditem item
left join t_erp_ship_inboundshipment ship on ship.ShipmentId =item.ShipmentId
left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.ShipmentId
left join t_erp_ship_inboundplan inp on inp.id=item.inboundplanid
left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid
left join t_amazon_group g on g.id=inp.amazongroupid
left join t_erp_warehouse w on w.id=inp.warehouseid
where inp.shopid =#{shopid,jdbcType=CHAR}
	and ship.status>1


		and inp.createdate>=#{fromDate,jdbcType=TIMESTAMP}
		and inp.createdate<=#{endDate,jdbcType=TIMESTAMP}


		and ship.shiped_date>=#{fromDate,jdbcType=TIMESTAMP}
		and ship.shiped_date<=#{endDate,jdbcType=TIMESTAMP}



	and inp.amazongroupid= #{groupid,jdbcType=CHAR}


	and inp.warehouseid= #{warehouseid,jdbcType=CHAR}


	and inp.marketplaceid= #{marketplaceid,jdbcType=CHAR}


	and (item.SellerSKU like #{search,jdbcType=CHAR} or inp.number like #{search,jdbcType=CHAR} 
		or ship.ShipmentId like #{search,jdbcType=CHAR} )

select ship.ShipmentId, max(g.name) groupname, max(mkp.market) market, max(ship.DestinationFulfillmentCenterId) center , max(w.name) warehouse, max(inp.createdate) createdate, max(ship.shiped_date) shiped_date, max(trans.arrivalTime) arrivalTime, max(status6date) status6date, max(company.name) company , max(detail.channame) channame, count(item.SellerSKU) skunum, max(inp.number) number, sum(item.QuantityShipped) qtyshipped, sum(item.QuantityReceived) qtyreceived, max(trans.otherfee) otherfee, max(trans.singleprice*trans.transweight) price, max(ifnull(trans.otherfee,0))+ max(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)) totalprice, max(transweight) transweight, max(wunit ) wunit, sum(d.weight*item.Quantity) weightkg, max(case when stat.`status`='WORKING' and ship.status>=5 then '货件异常' else stat.name end) shipmentstatus, sum(ifnull(item.QuantityReceived,0)) sumrec, max(ship.status) status, max(status0date) status0date, max(status1date) status1date, max(status2date) status2date, max(status3date) status3date, max(status4date) status4date, max(status5date) status5date, max(start_receive_date) start_receive_date, max(trt.name) transtype, max(v.weight) boxweight, max(v.volume) boxvolume from t_erp_ship_inboundshipment ship left join ( select b.shipmentid,sum(ifnull(weight,0)) weight, ROUND(sum(ifnull(length,0)*ifnull(width,0)*ifnull(height,0)/ifnull(detail.drate,5000)),2) volume from t_erp_ship_inboundbox b left join t_erp_ship_inboundtrans trans on trans.shipmentid=b.ShipmentId left join t_erp_ship_transdetail detail on detail.id = trans.channel group by b.shipmentid ) v on v.shipmentid=ship.shipmentid left join t_erp_ship_inbounditem item on ship.ShipmentId =item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.ShipmentId left join t_erp_ship_transcompany company on company.id=trans.company left join t_erp_ship_transdetail detail on detail.id = trans.channel left join t_erp_transtype trt on trt.id=detail.transtype left join t_erp_ship_status stat on stat.`status`=ship.ShipmentStatus left join t_erp_ship_inboundplan inp on inp.id=item.inboundplanid left join t_product_info p on p.sku=item.SellerSKU and p.marketplaceid=inp.marketplaceid LEFT JOIN t_amazon_auth a ON a.id=p.amazonauthid left join t_product_in_opt opt on p.id=opt.pid left join t_erp_material m on m.sku=IFNULL(opt.msku,p.sku) and m.shopid=inp.shopid AND m.isDelete=0 left join t_dimensions d on d.id=m.pkgDimensions left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid left join t_amazon_group g on g.id=inp.amazongroupid left join t_erp_warehouse w on w.id=inp.warehouseid where inp.shopid =#{param.shopid,jdbcType=CHAR} and ship.status>=1 and a.groupid=inp.amazongroupid
and inp.createdate >= #{param.fromDate,jdbcType=TIMESTAMP} and inp.createdate <= #{param.endDate,jdbcType=TIMESTAMP}
and ship.shiped_date >=#{param.fromDate,jdbcType=DATE} and ship.shiped_date <=#{param.endDate,jdbcType=DATE}
and inp.amazongroupid= #{param.groupid,jdbcType=CHAR}
and inp.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}
and ship.ShipmentId like #{param.search,jdbcType=CHAR}
and company.name like #{param.company,jdbcType=CHAR}
and company.id = #{param.companyid,jdbcType=CHAR}
and w.id = #{param.warehouseid,jdbcType=CHAR}
and item.QuantityShipped!=item.QuantityReceived
and item.QuantityShipped=item.QuantityReceived
group by ship.ShipmentId
