mapper.select:
select * from ( select inbound.shipmentId , max(inbound.name) name , max(form.id) planid, max(form.marketplaceid) marketplaceid, max(mp.market) countryCode , count(item.SellerSKU) skuamount, sum(item.Quantity) sumQuantity, max(case when (stat.`status`='WORKING' and inbound.status>=5) or (stat.`status`!='WORKING' and inbound.status<5 and inbound.status>0) or (stat.`status`!='CANCELLED' and stat.`status`!='DELETED' and inbound.status<=0) then '状态异常' else stat.name end) shipmentstatus, max(inbound.DestinationFulfillmentCenterId) centerId, sum(ifnull(item.QuantityShipped,0)) sumshipped, sum(ifnull(item.QuantityReceived,0)) sumrec, max(detail.channame) channame, max(form.createdate) createdate, max(w.name) warehouse, max(mp.name) country, max(mp.market) market, max(ag.name) groupname, max(inbound.transtyle) transtyle, max(inbound.status) status, max(inbound.remark) remark, max(date_add(trans.arrivalTime,INTERVAL ifnull(rt.put_on_days,0) DAY)) shopdate, max(trans.arrivalTime) arrivalTime, max(stat.`status`) shipstatus, max(case when item.SellerSKU like ifnull(#{param.search,jdbcType=CHAR},'') then 1 else 0 end) hassku , max(status0date) status0date, max(status1date) status1date, max(status2date) status2date, max(status3date) status3date, max(status4date) status4date, max(status5date) status5date, max(status6date) status6date, max(shiped_date) shiped_date, max(start_receive_date) receivedate, max(inbound.sync_inv) syncinv, max(form.number) number, max(c.name) company, max(c.id) companyid, max(ty.name) transtypename, max(trans.ordernum) ordernum, max(form.warehouseid) warehouseid, max(form.amazongroupid) groupid, max(tu.`status`) traceupstatus from t_erp_ship_inboundshipment inbound left join t_erp_ship_inboundtrans trans on trans.shipmentid=inbound.shipmentid left join t_erp_ship_status stat on stat.`status`=inbound.ShipmentStatus left join t_erp_ship_addressto ad on ad.id=inbound.ShipToAddressID left join t_erp_ship_inbounditem item on item.ShipmentId=inbound.ShipmentId left join t_erp_ship_inboundplan form on form.id=inbound.inboundplanid left join t_erp_ship_transdetail detail on detail.id = trans.channel left join t_erp_ship_transcompany c on c.id=detail.company left join t_erp_ship_inboundshipment_traceupload tu on tu.shipmentid=inbound.shipmentid left join t_marketplace mp on mp.marketplaceid=form.marketplaceid left join t_erp_warehouse_fba rt on rt.shopid=form.shopid and (rt.marketplaceid=mp.region or rt.marketplaceid=mp.marketplaceId) left join t_amazon_group ag on ag.id=form.amazongroupid left join t_erp_warehouse w on form.warehouseid=w.id and w.shopid=form.shopid left join t_erp_transtype ty on ty.id=detail.transtype where form.shopid=#{param.shopid,jdbcType=CHAR} and inbound.`status` in (0,2,3,4,5,6)
and form.amazongroupid in(
#{groupid,jdbcType=CHAR}
)
and inbound.status = 0
and inbound.status = 3
and inbound.ShipmentId = #{param.shipmentid,jdbcType=CHAR}
and inbound.`status` = 6
and inbound.status = 5 and inbound.ShipmentStatus!='RECEIVING'
and inbound.status = 5 and inbound.ShipmentStatus ='RECEIVING'
and (inbound.status = 2 or inbound.status = 3 or inbound.status = 4)
and form.createdate >= #{param.fromdate,jdbcType=TIMESTAMP} and form.createdate <= #{param.enddate,jdbcType=TIMESTAMP}
and form.marketplaceid = #{param.marketplaceid,jdbcType=CHAR}
and form.amazongroupid= #{param.groupid,jdbcType=CHAR}
and w.id= #{param.warehouseid,jdbcType=CHAR}
and trans.company = #{param.company,jdbcType=CHAR}
and trans.channel = #{param.channel,jdbcType=CHAR}
and ty.id=#{param.transtype,jdbcType=CHAR}
and (inbound.ShipmentId like #{param.search,jdbcType=CHAR})
and inbound.remark like #{param.search,jdbcType=CHAR}
and form.number like #{param.search,jdbcType=CHAR}
and inbound.name like #{param.search,jdbcType=CHAR}
and ( (inbound.ignorerec=0 and inbound.status = 6 ) or (inbound.ignorerec=0 and stat.`status`='WORKING' and inbound.status>=5 ) or (inbound.ignorerec=0 and stat.`status`!='WORKING' and inbound.status<5 and inbound.status>0) )
group by inbound.ShipmentId
having ((sum(ifnull(item.QuantityShipped,0))-sum(ifnull(item.QuantityReceived,0))>=10) or (sum(ifnull(item.QuantityReceived,0))-sum(ifnull(item.QuantityShipped,0))>=10))
) V where 1=1
and hassku=1

select name 
from t_erp_ship_config_carrier
where country =#{country,jdbcType=CHAR} 
	and transtyle =#{transtyle,jdbcType=CHAR}

        SELECT country,m.marketplaceId from t_amz_ship_fulfillment_center c 
		LEFT JOIN t_marketplace m ON m.market=c.country
		WHERE c.code=#{destinationFulfillmentCenterId,jdbcType=CHAR}

		     AND m.region=#{region,jdbcType=CHAR}

		limit 1;

select pd.sku sku, ir.fnsku fnsku, 0 quantity, pd.name title ,mp.market country
from t_product_info pd
left join t_marketplace mp on mp.marketplaceId=pd.marketplaceid
left join t_amazon_auth auth on auth.id=pd.amazonAuthId and auth.shop_id=#{shopid,jdbcType=CHAR}
left join t_inventory_report ir on ir.sku=pd.sku and ir.amazonAuthId=auth.id 
	and ir.marketplaceid=case when mp.region='EU' then 'EU' else pd.marketplaceid end
where auth.id is not null
	and pd.marketplaceid=#{marketplace,jdbcType=CHAR}
	and auth.groupid=#{groupid,jdbcType=CHAR}

	and(

		pd.sku=#{sku,jdbcType=CHAR}

	)

SELECT s.* from t_erp_ship_inboundplan p
left join t_erp_ship_inbounditem i on i.inboundplanid=p.id
left join t_erp_ship_inboundshipment s on p.id=s.inboundplanid
where p.shopid=#{shopid,jdbcType=CHAR} 
	and i.SellerSKU=#{sku,jdbcType=CHAR}

	SELECT s.* from t_erp_ship_inboundplan p
    left join t_erp_ship_inbounditem i on i.inboundplanid=p.id
    left join t_erp_ship_inboundshipment s on p.id=s.inboundplanid
    where p.shopid=#{shopid,jdbcType=CHAR} 
	and i.SellerSKU=#{sku,jdbcType=CHAR}
	and p.marketplaceid=#{marketplaceid,jdbcType=CHAR}
