select sellersku,qty,NAME,opttime,location,msku,ownername,skufee,round(skufee/qty,2) avgfee,groupid,marketplaceid from ( SELECT i.sellersku, sum(i.QuantityShipped) qty, max(info.name) NAME, max(s.status5date) opttime, IFNULL(max(pp.location),max(pp.url)) location, max(m.sku) msku, max(u.name) ownername, max(g.id) groupid, max(p.marketplaceid) marketplaceid, round(SUM(ifnull(i.totaltransfee, (trans.singleprice*trans.transweight+IFNULL(trans.otherfee,0))* case when cfg3.id is not null then (case when cfg3.shipmentStyle='weight' then d.weight*i.QuantityShipped when cfg3.shipmentStyle='dim_weight' then (case when d.weight>(d.length*d.width*d.height/ifnull(td.drate,5000)) then d.weight*i.QuantityShipped else (d.length*d.width*d.height/ifnull(td.drate,5000))*i.QuantityShipped end) else (case when trans.wtype=0 then d.weight*i.QuantityShipped ELSE d.length*d.width*d.height/ifnull(td.drate,5000)*i.QuantityShipped END) end) else (case when cfg.shipmentStyle='weight' then d.weight*i.QuantityShipped when cfg.shipmentStyle='dim_weight' then (case when d.weight>(d.length*d.width*d.height/ifnull(td.drate,5000)) then d.weight*i.QuantityShipped else (d.length*d.width*d.height/ifnull(td.drate,5000))*i.QuantityShipped end) else (case when trans.wtype=0 then d.weight*i.QuantityShipped ELSE (d.length*d.width*d.height/ifnull(td.drate,5000))*i.QuantityShipped END) end) end
/ ( SELECT SUM( (case when cfg2.id is not null then (case when cfg2.shipmentStyle='weight' then d2.weight when cfg2.shipmentStyle='dim_weight' then (case when d2.weight>(d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) then d2.weight else (d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) end) else (case when trans.wtype=0 then d2.weight ELSE d2.length*d2.width*d2.height/ifnull(td2.drate,5000) END) end) else (case when cfg1.shipmentStyle='weight' then d2.weight when cfg1.shipmentStyle='dim_weight' then (case when d2.weight>(d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) then d2.weight else (d2.length*d2.width*d2.height/ifnull(td2.drate,5000)) end) else (case when trans.wtype=0 then d2.weight ELSE d2.length*d2.width*d2.height/ifnull(td2.drate,5000) END) end) end) *i2.QuantityShipped
) FROM t_erp_ship_inbounditem i2 LEFT JOIN t_erp_ship_inboundshipment s2 ON i2.ShipmentId=s2.ShipmentId AND s2.inboundplanid=i2.inboundplanid LEFT JOIN t_erp_ship_inboundplan p2 ON p2.id=s2.inboundplanid LEFT JOIN t_product_info info2 ON info2.sku=i2.SellerSKU AND info2.marketplaceid=p2.marketplaceid LEFT JOIN t_amazon_auth a2 ON a2.id=info2.amazonAuthId LEFT JOIN t_product_in_opt opt2 ON opt2.pid=info2.id LEFT JOIN t_erp_material m2 ON m2.sku=ifnull(opt2.msku,info2.sku) AND m2.shopid=p2.shopid AND m2.isDelete=0 LEFT JOIN t_dimensions d2 ON d2.id=m2.pkgDimensions LEFT JOIN t_erp_ship_inboundtrans trans2 ON trans2.shipmentid =s2.ShipmentId LEFT JOIN t_erp_ship_transdetail td2 ON td2.id=trans2.channel AND td2.company=trans2.company left join t_amazon_group gp on gp.id=a2.groupid left join t_profitcfg cfg1 on cfg1.id=gp.profitcfgid left join t_profitcfg cfg2 on cfg2.id=opt2.profitid WHERE s2.ShipmentId=s.ShipmentId AND s2.inboundplanid=s.inboundplanid AND a2.groupid=p.amazongroupid
and s2.status5date >= DATE_FORMAT(#{param.fromDate,jdbcType=TIMESTAMP}, '%Y/%m/%d') and s2.status5date <= DATE_FORMAT(date_add(#{param.endDate,jdbcType=TIMESTAMP}, interval 1 day), '%Y/%m/%d')
) )),2) skufee FROM t_erp_ship_inbounditem i LEFT JOIN t_erp_ship_inboundshipment s ON i.ShipmentId=s.ShipmentId AND s.inboundplanid=i.inboundplanid LEFT JOIN t_erp_ship_inboundtrans trans ON trans.shipmentid =s.ShipmentId LEFT JOIN t_erp_ship_transdetail td ON td.id=trans.channel AND td.company=trans.company LEFT JOIN t_erp_ship_inboundplan p ON p.id=s.inboundplanid LEFT JOIN t_amazon_group g ON g.id=p.amazongroupid LEFT JOIN t_product_info info ON info.sku=i.SellerSKU AND info.marketplaceid=p.marketplaceid LEFT JOIN t_picture pp ON pp.id=info.image LEFT JOIN t_product_in_opt opt ON opt.pid=info.id LEFT JOIN t_erp_material m ON m.sku=ifnull(opt.msku,info.sku) AND m.shopid=p.shopid AND m.isDelete=0 left join t_userinfo u on u.id=m.owner LEFT JOIN t_dimensions d ON d.id=m.pkgDimensions LEFT JOIN t_amazon_auth a ON a.id=info.amazonAuthId left join t_profitcfg cfg on cfg.id=g.profitcfgid left join t_profitcfg cfg3 on cfg3.id=opt.profitid WHERE s.`status`>=5 AND p.shopid=#{param.shopid,jdbcType=CHAR} AND a.groupid=p.amazongroupid AND trans.transweight>0
and s.status5date >= DATE_FORMAT(#{param.fromDate,jdbcType=TIMESTAMP} , '%Y/%m/%d') and s.status5date <= DATE_FORMAT(date_add(#{param.endDate,jdbcType=TIMESTAMP}, interval 1 day) , '%Y/%m/%d')
and p.amazongroupid= #{param.groupid,jdbcType=CHAR}
and p.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}
and i.sellersku like #{param.search,jdbcType=CHAR}
and trans.company= #{param.company,jdbcType=CHAR}
and trans.channel= #{param.channel,jdbcType=CHAR}
and i.shipmentid= #{param.shipmentid,jdbcType=CHAR}
GROUP BY i.sellersku) v

