) skuweight, (trans.singleprice*trans.transweight+IFNULL(trans.otherfee,0)) shipmentfee, ifnull(i.totaltransfee, round( (trans.singleprice*trans.transweight+IFNULL(trans.otherfee,0))* (case when cfg3.id is not null then (case when cfg3.shipmentStyle='weight' then d.weight when cfg3.shipmentStyle='dim_weight' then (case when d.weight>(d.length*d.width*d.height/ifnull(td.drate,5000)) then d.weight else (d.length*d.width*d.height/ifnull(td.drate,5000)) end) else (case when trans.wtype=0 then d.weight ELSE d.length*d.width*d.height/ifnull(td.drate,5000) END) end) else (case when cfg.shipmentStyle='weight' then d.weight when cfg.shipmentStyle='dim_weight' then (case when d.weight>(d.length*d.width*d.height/ifnull(td.drate,5000)) then d.weight else (d.length*d.width*d.height/ifnull(td.drate,5000)) end) else (case when trans.wtype=0 then d.weight ELSE d.length*d.width*d.height/ifnull(td.drate,5000) END) end) end) *i.QuantityShipped/
