and s2.status5date >= DATE_FORMAT(#{param.fromDate,jdbcType=TIMESTAMP} , '%Y/%m/%d') and s2.status5date <= DATE_FORMAT(date_add(#{param.endDate,jdbcType=TIMESTAMP}, interval 1 day) , '%Y/%m/%d')
),2 )) skufee FROM t_erp_ship_inbounditem i LEFT JOIN t_erp_ship_inboundshipment s ON i.ShipmentId=s.ShipmentId AND s.inboundplanid=i.inboundplanid left join t_erp_ship_status st on st.status=s.ShipmentStatus LEFT JOIN t_erp_ship_inboundtrans trans ON trans.shipmentid =s.ShipmentId LEFT JOIN t_erp_ship_transdetail td ON td.id=trans.channel AND td.company=trans.company LEFT JOIN t_erp_ship_transcompany tc ON tc.id=td.company LEFT JOIN t_erp_ship_inboundplan p ON p.id=s.inboundplanid left join t_erp_warehouse w on w.id=p.warehouseid LEFT JOIN t_marketplace mp ON mp.marketplaceId=p.marketplaceid LEFT JOIN t_amazon_group g ON g.id=p.amazongroupid LEFT JOIN t_product_info info ON info.sku=i.SellerSKU AND info.marketplaceid=p.marketplaceid LEFT JOIN t_picture pp ON pp.id=info.image LEFT JOIN t_product_in_opt opt ON opt.pid=info.id LEFT JOIN t_erp_material m ON m.sku=ifnull(opt.msku,info.sku) AND m.shopid=p.shopid AND m.isDelete=0 left join t_userinfo u on u.id=m.owner left join t_userinfo u2 on u2.id=trans.operator LEFT JOIN t_dimensions d ON d.id=m.pkgDimensions LEFT JOIN t_amazon_auth a ON a.id=info.amazonAuthId left join t_profitcfg cfg on cfg.id=g.profitcfgid left join t_profitcfg cfg3 on cfg3.id=opt.profitid WHERE p.shopid=#{param.shopid,jdbcType=CHAR} AND trans.transweight>0 AND a.groupid=p.amazongroupid
and s.status5date >= DATE_FORMAT(#{param.fromDate,jdbcType=TIMESTAMP} , '%Y/%m/%d') and s.status5date <= DATE_FORMAT(date_add(#{param.endDate,jdbcType=TIMESTAMP}, interval 1 day), '%Y/%m/%d')
and p.amazongroupid= #{param.groupid,jdbcType=CHAR}
and p.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}
and (i.sellersku like #{param.search,jdbcType=CHAR} or i.ShipmentId like #{param.search,jdbcType=CHAR})
and trans.company= #{param.company,jdbcType=CHAR}
and trans.channel= #{param.channel,jdbcType=CHAR}
and i.shipmentid= #{param.shipmentid,jdbcType=CHAR}
and s.`status`>=5

SELECT sum(v.amount) amount,v.posted_date, '头程运费' name
from (
		SELECT ((trans.singleprice * trans.transweight) + ifnull(trans.otherfee,0)) amount,
			DATE_FORMAT(s.status5date, '%y-%m') posted_date 
		FROM  t_erp_ship_inboundshipment s
		LEFT JOIN t_erp_ship_inboundtrans trans ON trans.shipmentid =s.ShipmentId
		LEFT JOIN t_erp_ship_inboundplan p ON p.id=s.inboundplanid
		WHERE s.`status` >= 5
			AND p.shopid = #{shopid,jdbcType=CHAR}
			AND trans.transweight > 0

			and s.status5date >= #{fromDate,jdbcType=CHAR}
			and s.status5date <= #{endDate,jdbcType=CHAR}


			and s.status5date >= #{setfromDate,jdbcType=CHAR}
			and s.status5date <= #{setendDate,jdbcType=CHAR}


				and p.marketplaceid= #{marketplaceid,jdbcType=CHAR}


				and p.amazongroupid= #{groupid,jdbcType=CHAR}

	)v
group by v.posted_date;
