mapper.select:
SELECT ifnull(SUM(i.QuantityShipped),0) qty FROM t_erp_ship_v2_inbounditem i
LEFT JOIN t_erp_ship_v2_inboundshipment s  ON s.ShipmentId=i.ShipmentId
LEFT JOIN t_erp_ship_v2_inboundplan p ON p.id=s.inboundplanid AND p.id=i.inboundplanid
WHERE s.`status`>=2 AND s.`status`<=4 AND i.msku=#{msku,jdbcType=CHAR}
AND p.auditstatus=3
AND p.shopid= #{shopid,jdbcType=CHAR} AND p.warehouseid=#{warehouseid,jdbcType=CHAR}

select sum(case when (ifnull(item.QuantityShipped,0)-ifnull(item.QuantityReceived,0))>=0 then ifnull(item.QuantityShipped,0)-ifnull(item.QuantityReceived,0) else 0 end ) quantity from t_erp_ship_v2_inbounditem item left join t_erp_ship_v2_inboundshipment inbound on inbound.ShipmentId=item.ShipmentId left join t_erp_ship_v2_inboundplan plan on plan.id=item.inboundplanid left join t_erp_warehouse w on w.id = plan.warehouseid left join t_amazon_group ag on ag.id=plan.amazongroupid left join t_marketplace m on m.marketplaceId=plan.marketplaceid where item.SellerSKU = #{sku,jdbcType=CHAR}
and m.region='EU'
and plan.marketplaceid= #{marketplaceid,jdbcType=CHAR}
and plan.createdate>ADDDATE(NOW(), INTERVAL -1 year) and plan.amazongroupid= #{groupid,jdbcType=CHAR} and inbound.status>=2 and inbound.status <6

select m.name , d.length boxlength, d.width boxwidth, d.height boxheight, d.weight boxweight, m.color mcolor, m.boxnum, m.issfg, ifnull( (select sum(iv.quantity) from t_erp_inventory iv where iv.materialid=m.id and iv.warehouseid=v.warehouseid and iv.`status`='fulfillable' ) ,0) invquantity,
ifnull( ( select sum(iv2.quantity) from t_erp_inventory iv2 where iv2.materialid=m.id and iv2.warehouseid=v.warehouseid and iv2.status like 'outbound_%' ),0) outbound, v.* from ( select GROUP_CONCAT(s.check_inv) id, max(ifnull(opt.msku,item.SellerSKU)) msku, sum(ifnull(item.Quantity,0)) Quantity, sum(ifnull(ifnull(item.QuantityShipped,item.Quantity),0)) QuantityShipped, sum(ifnull(item.QuantityReceived,0)) QuantityReceived, max(ifnull(pt2.location,pt2.url)) image, max(item.FulfillmentNetworkSKU) FulfillmentNetworkSKU, max(item.SellerSKU) sellersku, max(item.FulfillmentNetworkSKU) FNSKU , max(info.asin) asin, max(info.name) pname, max(plan.warehouseid) warehouseid, max(item.totaltransfee) totaltransfee, max(item.unittransfee) unittransfee, max(item.totalcost) totalcost, max(item.unitcost) unitcost, max(item.PrepOwner) PrepOwner, max(item.PrepInstruction) PrepInstruction, max(plan.shopid) shopid from t_erp_ship_v2_inbounditem item left join t_erp_ship_v2_inboundshipment s on s.ShipmentId=item.ShipmentId left join t_erp_ship_v2_inboundplan plan on plan.id=item.inboundplanid left join t_marketplace mk on mk.marketplaceid =plan.marketplaceid left join t_amazon_auth auth on auth.groupid=plan.amazongroupid and auth.aws_region=mk.aws_region left join t_product_info info on info.marketplaceid=mk.marketplaceid and info.sku=item.SellerSKU and info.amazonauthid=auth.id left join t_picture pt2 on pt2.id=info.image left join t_product_in_opt opt on opt.pid=info.id left join t_product_in_order od on od.pid=info.id left join t_erp_ship_inboundtrans tr on tr.shipmentid=item.shipmentid left join t_erp_ship_transdetail de on de.company=tr.company and de.id=tr.channel where item.ShipmentId in (
#{shipmentid,jdbcType=CHAR}
) group by item.sellersku ) v left join t_erp_material m on m.sku =v.msku and v.shopid=m.shopid and m.isDelete = 0 left join t_dimensions d on d.id=m.boxDimensions left join t_dimensions dim on dim.id=m.pkgDimensions
order by v.sellersku asc

SELECT * FROM (
select
plan.marketplaceid warehouse,
de.transtype,
max(mkp.name) marketname,
max(tt.name) name,
sum(ifnull(dd.weight,0)*item.quantity) readweight,
SUM(item.quantity) totalqty,
sum(case when shipment.`status`=5 or shipment.`status`=6 then item.quantity else 0 end ) totalout,
sum(case when shipment.`status`=5 or shipment.`status`=6 then item.received else 0 end ) totalrec,
sum(case when shipment.`status`=5 or shipment.`status`=6 then item.received-item.quantity else 0 end ) lessrec,
sum(case when shipment.`status`=5 or shipment.`status`=6 then item.quantity*m.price else 0 end ) worth,
sum(case when shipment.`status` >=2 and shipment.`status`<=5 then item.quantity else 0 end ) needrec,
sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.quantity-item.received else 0 end ) needout
from t_erp_ship_v2_inboundshipment_item item
left join t_erp_ship_v2_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId
left join t_erp_ship_v2_inboundplan plan on plan.id=shipment.formid
left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid
left join t_erp_warehouse w on plan.warehouseid=w.id
left join t_erp_material m on m.sku=item.sku and plan.shopid=m.shopid and m.isDelete = 0
left join t_dimensions dd on dd.id=m.pkgDimensions
left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.shipment_confirmation_id
left join t_erp_ship_transdetail de on de.id=trans.channel
left join t_erp_ship_transchannel ch on ch.id=de.channel
left join t_erp_transtype tt on tt.id=de.transtype
left join t_erp_ship_transcompany com on com.id=de.company
where plan.shopid=#{param.shopid,jdbcType=CHAR}
and shipment.status>=5
and com.`name` is not null

	and plan.createtime >=#{param.fromDate,jdbcType=DATE}
	and plan.createtime <=#{param.endDate,jdbcType=DATE}


	and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE}
	and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}


	and w.id=#{param.warehouseid,jdbcType=CHAR}


	and de.company =#{param.companyid,jdbcType=CHAR}


	and trans.channel=#{param.channelid,jdbcType=CHAR}


	and item.sku  like #{param.search,jdbcType=CHAR}


	and plan.groupid=#{param.groupid,jdbcType=CHAR}


	and plan.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}



group by plan.marketplaceid,de.transtype
) v
left join (
select
plan.marketplaceid warehouse,
de.transtype,
sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg,
sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm,
sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee,
sum( boxv.boxnum) totalbox,
ROUND(avg(ifnull(datediff(shipment.start_receive_date, shipment.shiped_date),0)),2) avgtime,
count(if(shipment.status='-1',true,null )) problem
FROM t_erp_ship_v2_inboundshipment shipment
left join t_erp_ship_v2_inboundplan plan on plan.id=shipment.formid
left join ( select box.shipmentid,count(box.id) boxnum from t_erp_ship_v2_inboundshipment_box box ) boxv on boxv.shipmentid=shipment.shipmentid
left join t_marketplace mkp on mkp.marketplaceid=plan.marketplaceid
left join t_erp_warehouse w on plan.warehouseid=w.id
left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.shipment_confirmation_id
left join t_erp_ship_transdetail de on de.id=trans.channel
left join t_erp_ship_transcompany com on com.id=de.company
where plan.shopid=#{param.shopid,jdbcType=CHAR}
and (shipment.status>=5 or shipment.start_receive_date is not null)
and com.`name` is not null

	and plan.createtime >=#{param.fromDate,jdbcType=DATE}
	and plan.createtime <=#{param.endDate,jdbcType=DATE}


	and  shipment.shiped_date  >=#{param.fromDate,jdbcType=DATE}
	and   shipment.shiped_date <=#{param.endDate,jdbcType=DATE}


	and w.id=#{param.warehouseid,jdbcType=CHAR}


	and de.company =#{param.companyid,jdbcType=CHAR}


	and trans.channel=#{param.channelid,jdbcType=CHAR}


	and plan.groupid=#{param.groupid,jdbcType=CHAR}


	and plan.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}

group by plan.marketplaceid,de.transtype
) n on v.transtype=n.transtype    and v.warehouse =n.warehouse

SELECT ${param.groupby}, max(gname) gname, max(market) market, max(company) company, max(logitics) logitics, max(channame) channame, max(subarea) subarea, max(channelname) channelname, max(transtype) transtype, max(warehousename) warehousename, SUM(readweight) readweight, SUM(totalqty) totalqty, SUM(totalout) totalout, SUM(totalrec) totalrec, SUM(lessrec) lessrec, SUM(worth) worth, SUM(needrec) needrec, SUM(needout) needout, sum(transweight_kg) transweight_kg, sum(transweight_cbm) transweight_cbm, sum(shipfee) shipfee, sum(totalbox) totalbox, sum(totalotherfee) totalotherfee, count(shipmentid) shipmentnum, ROUND(avg(avgtime),2) avgtime, SUM(problem) problem
FROM ( SELECT
max(plan.groupid) groupid, max(plan.marketplaceid) marketplaceid, max(plan.warehouseid) warehouseid, max(de.id ) channeldetailid, max(item.sku) sku, shipment.shipment_confirmation_id shipmentid, shipment.ShipmentId ShipmentId1, max(case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end) warehouse, max(g.name) gname, max(mk.name) market, max(de.company) company, max(com.`name`) logitics, max(de.channame) channame, max(de.subarea) subarea, max(ch.name) channelname, max(tt.name) transtype, max(w.name) warehousename, sum(ifnull(dd.weight,0)*item.quantity) readweight, sum(item.quantity) totalqty, sum( item.quantity ) totalout, sum( item.received ) totalrec, sum( item.received-item.quantity ) lessrec, sum( item.quantity*m.price ) worth, sum( item.quantity ) needrec, sum( item.quantity-item.received ) needout
plan.groupid groupid, plan.marketplaceid, plan.warehouseid, de.id channeldetailid, item.sku sku, shipment.shipment_confirmation_id shipmentid, shipment.ShipmentId ShipmentId1, case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end warehouse, g.name gname, mk.name market, de.company company, com.`name` logitics, de.channame channame, de.subarea subarea, ch.name channelname, tt.name transtype, w.name warehousename, ifnull(dd.weight,0)*item.quantity readweight, item.quantity totalqty, item.quantity totalout, item.received totalrec, item.received-item.quantity lessrec, item.quantity*m.price worth, item.quantity needrec, item.quantity-item.received needout
from t_erp_ship_v2_inboundshipment_item item left join t_erp_ship_v2_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId left join t_erp_ship_v2_inboundplan plan on plan.id=shipment.formid left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_material m on m.sku=item.sku and plan.shopid=m.shopid and m.isDelete = 0 left join t_dimensions dd on dd.id=m.pkgDimensions left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.shipment_confirmation_id left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transchannel ch on ch.id=de.channel left join t_erp_transtype tt on tt.id=de.transtype left join t_erp_ship_transcompany com on com.id=de.company LEFT JOIN t_amazon_group g ON g.id=plan.groupid LEFT JOIN t_marketplace mk ON mk.marketplaceId=plan.marketplaceid where plan.shopid=#{param.shopid,jdbcType=CHAR} and shipment.status>=5
and plan.createtime >=#{param.fromDate,jdbcType=DATE} and plan.createtime <=#{param.endDate,jdbcType=DATE}
and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE} and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}
and plan.warehouseid=#{param.warehouseid,jdbcType=CHAR}
and plan.groupid=#{param.groupid,jdbcType=CHAR}
and plan.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}
and de.company =#{param.companyid,jdbcType=CHAR}
and trans.channel=#{param.channelid,jdbcType=CHAR}
and item.sku like #{param.search,jdbcType=CHAR}
GROUP BY ShipmentId1
) v left join ( select shipment.shipmentid ShipmentId2, sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg, sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm, sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee, sum(trans.otherfee) totalotherfee, count(shipment.shipmentid) shipmentnum, sum( (select count(b.id) from t_erp_ship_v2_inboundshipment_box b left join t_erp_ship_v2_inboundshipment sh on sh.shipmentid=b.shipmentid where sh.shipment_confirmation_id=shipment.shipment_confirmation_id) ) totalbox, max(datediff(shipment.start_receive_date, shipment.shiped_date)) avgtime, count(if(shipment.status='-1',true,null )) problem FROM t_erp_ship_v2_inboundshipment shipment left join t_erp_ship_v2_inboundplan plan on plan.id=shipment.formid left join t_marketplace mkp on mkp.marketplaceid=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.shipment_confirmation_id left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{param.shopid,jdbcType=CHAR} and (shipment.status>=5 or shipment.start_receive_date is not null)
and plan.createtime >=#{param.fromDate,jdbcType=DATE} and plan.createtime <=#{param.endDate,jdbcType=DATE}
and shipment.shiped_date >=#{param.fromDate,jdbcType=DATE} and shipment.shiped_date <=#{param.endDate,jdbcType=DATE}
and plan.warehouseid=#{param.warehouseid,jdbcType=CHAR}
and plan.groupid=#{param.groupid,jdbcType=CHAR}
and plan.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}
and de.company =#{param.companyid,jdbcType=CHAR}
and trans.channel=#{param.channelid,jdbcType=CHAR}
GROUP BY shipment.ShipmentId ) n ON v.ShipmentId1 =n.ShipmentId2 GROUP BY ${param.groupby}

SELECT * FROM (
select
SUM(item.quantity) totalqty,
sum(item.quantity) totalout,
sum(item.received ) totalrec,
sum(item.received-item.quantity) lessrec,
sum(item.quantity*m.price) worth,
sum(item.quantity) needrec,
sum(item.quantity-item.received) needout,
sum(ifnull(dd.weight,0)*item.quantity) readweight
from t_erp_ship_v2_inboundshipment_item item
left join t_erp_ship_v2_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId
left join t_erp_ship_v2_inboundplan plan on plan.id=shipment.formid
left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid
left join t_erp_warehouse w on plan.warehouseid=w.id
left join t_erp_material m on m.sku=item.sku and plan.shopid=m.shopid and m.isDelete = 0
left join t_dimensions dd on dd.id=m.pkgDimensions
left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId
left join t_erp_ship_transdetail de on de.id=trans.channel
left join t_erp_ship_transcompany com on com.id=de.company
where plan.shopid=#{shopid,jdbcType=CHAR}
and shipment.status>=5

	and  plan.createtime >=#{fromDate,jdbcType=DATE}
	and  plan.createtime <=#{endDate,jdbcType=DATE}


	and  shipment.shiped_date >=#{fromDate,jdbcType=DATE}
	and  shipment.shiped_date <=#{endDate,jdbcType=DATE}


	and plan.warehouseid=#{warehouseid,jdbcType=CHAR}


	and plan.groupid=#{groupid,jdbcType=CHAR}


	and plan.marketplaceid=#{marketplaceid,jdbcType=CHAR}


	and de.company =#{companyid,jdbcType=CHAR}


	and trans.channel=#{channelid,jdbcType=CHAR}


	and item.sku  like #{search,jdbcType=CHAR}

) v

	join (
	select sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg,
	sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm,
	sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee,
	sum(trans.otherfee) totalotherfee,
	sum( boxv.boxnum) totalbox,
	count(shipment.shipmentid) shipmentnum,
	ROUND(avg(ifnull(datediff(shipment.start_receive_date, shipment.shiped_date),0)),2) avgtime,
	count(if(shipment.status='-1',true,null )) problem
	FROM t_erp_ship_v2_inboundshipment shipment
	left join t_erp_ship_v2_inboundplan plan on plan.id=shipment.formid
	left join ( select box.shipmentid,count(box.id) boxnum from t_erp_ship_v2_inboundshipment_box box ) boxv on boxv.shipmentid=shipment.shipmentid
	left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid
	left join t_erp_warehouse w on plan.warehouseid=w.id
	left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.shipment_confirmation_id
	left join t_erp_ship_transdetail de on de.id=trans.channel
	left join t_erp_ship_transcompany com on com.id=de.company
	where plan.shopid=#{shopid,jdbcType=CHAR}
	and (shipment.status>=5 or shipment.start_receive_date is not null)

		and  plan.createtime >=#{fromDate,jdbcType=DATE}
		and  plan.createtime <=#{endDate,jdbcType=DATE}


		and  shipment.shiped_date >=#{fromDate,jdbcType=DATE}
		and  shipment.shiped_date <=#{endDate,jdbcType=DATE}


		and plan.warehouseid=#{warehouseid,jdbcType=CHAR}


		and plan.groupid=#{groupid,jdbcType=CHAR}


		and plan.marketplaceid=#{marketplaceid,jdbcType=CHAR}


		and de.company =#{companyid,jdbcType=CHAR}


		and trans.channel=#{channelid,jdbcType=CHAR}

	) n

mapper.update:
    UPDATE t_erp_ship_v2_inbounditem i 
	LEFT JOIN (SELECT shipmentid,sku,sum(quantity) qty
	from  t_amz_rpt_orders_fulfilled_shipments_fee
	 where amazonauthid= #{amazonauthid,jdbcType=CHAR}
	 and sku= #{sku,jdbcType=CHAR}
	 and marketplaceid=#{marketplaceid,jdbcType=CHAR}
	 and shipment_date>=#{shipmentStartDate,jdbcType=CHAR} 
	 and shipment_date<#{shipmentEndDate,jdbcType=CHAR} 
	GROUP BY shipmentid,sku) v  ON  v.shipmentid=i.ShipmentId AND v.sku=i.SellerSKU
	SET  
	i.QuantityReceivedSub=ifnull(i.QuantityReceivedSub,0)-ifnull(v.qty,0),
	i.QuantityReceivedBalance= i.QuantityReceived -(ifnull(i.QuantityReceivedSub,0)-ifnull(v.qty,0))
	WHERE v.shipmentid=i.ShipmentId AND v.sku=i.SellerSKU

mapper.select:
SELECT  g.name groupname,m.name marketname,s.status5date shipdate,IFNULL(pt.location,pt.url) image,
i.SellerSKU sku,f.name ,i.`status`,p.number,s.ShipmentId shipmentid,i.QuantityShipped,i.QuantityReceived,
st.name shipmentstatus,i.ReceivedDate,i.QuantityReceivedSub,i.unitcost,i.totalcost,i.totaltransfee,i.unittransfee,
i.opttime,
i.QuantityReceivedBalance,
case when i.`status`=1 then '未开始' 
     when i.`status`=2 then '扣减中'   
     when i.`status`=3 then '已扣完'  
     else '未处理' end statusname
 from t_erp_ship_v2_inbounditem i
LEFT JOIN t_erp_ship_v2_inboundshipment s ON s.ShipmentId=i.ShipmentId
LEFT JOIN t_erp_ship_status st ON st.`status`=s.ShipmentStatus
LEFT JOIN t_marketplace m  ON m.marketplaceId=i.marketplaceid
LEFT JOIN t_product_info f ON f.sku=i.SellerSKU AND f.marketplaceid=i.marketplaceid AND f.amazonAuthId=i.amazonauthid
LEFT JOIN t_erp_ship_v2_inboundplan p ON p.id=s.inboundplanid
LEFT JOIN t_amazon_group g ON g.id=p.amazongroupid
LEFT JOIN t_picture pt ON pt.id=f.image
left join t_product_in_opt opt on opt.pid=f.id
WHERE s.`status`>=5 AND  i.ReceivedDate IS NOT NULL
AND p.shopid=#{param.shopid,jdbcType=CHAR} 

	AND p.amazongroupid=#{param.groupid,jdbcType=CHAR} 


	AND p.marketplaceid=#{param.marketplaceid,jdbcType=CHAR} 

AND i.ReceivedDate>#{param.fromDate,jdbcType=CHAR}
AND i.ReceivedDate <=#{param.toDate,jdbcType=CHAR}


		and i.SellerSKU like #{param.search,jdbcType=CHAR}


		and p.number like #{param.search,jdbcType=CHAR}


		and s.ShipmentId like #{param.search,jdbcType=CHAR}



	AND i.`status`=#{param.status,jdbcType=CHAR} 


	AND opt.owner=#{param.owner,jdbcType=CHAR}
