select v.*,sumdays-hasday- (select COUNT(h.id) from t_holiday h WHERE h.shopid=v.shopid and h.`day` >=v.auditime AND h.`day` <= DATE_ADD(v.auditime, INTERVAL ifnull(v.transday,8) DAY) ) days from ( SELECT max(form.`name`) name, max(form.remark) remark, max(form.operator) operator, max(form.creator) creator, max(form.opttime) opttime, max(form.`number`) `number`, max(form.createtime) createtime, max(form.auditime) auditime, max(addre.id) addressid, max(wh.`name`) warename, max(form.auditstatus) auditstatus, max(tm.market) country, max(tm.name) countryname, max(u.`name`) creatorname, sum(ifnull(entry.confirm_quantity,entry.quantity)) sumquantity, COUNT(entry.id) skunum, max(tm.`name`) destination, max(ag.`name`) groupname, form.id id, max(wh.id) warehouseid, max(form.marketplaceid) marketplaceid, max(form.id) inboundPlanId, max(ag.id) amazongroupid, max(form.amazonauthid) amazonauthid, max(ifnull(form.invstatus,0)) invstatus, max(form.invtype) invtype, max(form.check_inv) checkInv, max(form.placement_option_id) placementOptionId, max(ifnull(form.submitbox,0)) hasSubmitPackage, max(ty.name) transtypename, max(tyd.day) transday, max(form.submitbox) submitbox, max(form.shipping_date) shippingDate, MAX(DATEDIFF(ifnull(form.shipping_date,now()),form.auditime)) sumdays, MAX(ifnull(tyd.day,8)) hasday, MAX(form.shopid) shopid from t_erp_ship_v2_inboundplan form left join t_amazon_group ag on ag.id=form.groupid left join t_erp_ship_address addre on addre.id=form.source_address left join t_erp_ship_v2_inbounditem entry on entry.formid=form.id left join t_userinfo u on u.id=form.creator left join t_marketplace tm on tm.marketplaceId=form.marketplaceid left join t_erp_warehouse wh on wh.id=form.warehouseid left join t_erp_transtype ty on ty.id=form.transtype left join t_erp_ship_transtype_day tyd on tyd.transtypeid=ty.id and tyd.shopid=form.shopid where form.shopid =#{param.shopid,jdbcType=CHAR}
and form.auditstatus=#{param.auditstatus,jdbcType=INTEGER}
and form.createtime >= #{param.fromDate,jdbcType=TIMESTAMP} and form.createtime <= #{param.toDate,jdbcType=TIMESTAMP}
and form.groupid= #{param.groupid,jdbcType=CHAR}
and wh.id= #{param.warehouseid,jdbcType=CHAR}
and form.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}
and form.id= #{param.formid,jdbcType=CHAR}
and form.number like #{param.search,jdbcType=CHAR}
and form.remark like #{param.search,jdbcType=CHAR}
and entry.sku like #{param.search,jdbcType=CHAR}
and form.name like #{param.search,jdbcType=CHAR}
group by form.id ) v

SELECT b.ShipmentId shipmentid,a.marketplaceid,b.sku,chan.channame channel,company.simplename company, ifnull(b.received,0) reveiveQuantity, ts.arrivalTime,a.createdate, c.shiped_date ,ifnull(b.QuantityShipped,b.Quantity) Quantity, ts.outarrtime ,ts.inarrtime , case c.status when 1 then '等待确认' when 2 then '配货' when 0 then '取消货件' when 3 then '装箱' when 4 then '待发货' when 5 then '已发货' when 6 then '已完成' else '--' end statusName, c.status from t_erp_ship_v2_inboundplan a LEFT JOIN t_erp_ship_v2_inboundshipment c on c.formid=a.id LEFT JOIN t_erp_ship_v2_inboundshipment_item b on b.shipmentid=c.shipmentid LEFT JOIN t_erp_ship_inboundtrans ts on ts.shipmentid=c.shipment_confirmation_id LEFT JOIN t_erp_ship_transdetail chan on chan.id=ts.channel LEFT JOIN t_erp_ship_transcompany company on company.id=ts.company LEFT JOIN t_erp_ship_status d on c.ShipmentStatus=d.`status` left join t_marketplace m on m.marketplaceid=a.marketplaceid where a.shopid=#{shopid,jdbcType=CHAR} and ((a.auditstatus=1 and c.status=1) or (c.status in (2,3,4,5))) and (m.marketplaceid=#{marketplaceid,jdbcType=CHAR} or m.region=#{marketplaceid,jdbcType=CHAR})
and a.amazongroupid=#{groupid,jdbcType=CHAR}
and b.sku=#{sku,jdbcType=CHAR}
and a.createdate > DATE_SUB(NOW(), INTERVAL 1 YEAR) ORDER BY a.createdate desc

select

	date_format(plan.createdate,'%Y-%m-%d') createdate,


	date_format(subdate(plan.createdate,date_format(plan.createdate,'%w')-7),'%Y-%m-%d') createdate,


	concat( year(plan.createdate) ,'-' ,month(plan.createdate)) createdate,

	ifnull(sum(item.QuantityShipped),0) spqty
from t_erp_ship_v2_inbounditem item
left join t_erp_ship_v2_inboundshipment ship on ship.shipmentid=item.shipmentid
left join t_erp_ship_v2_inboundplan plan on plan.id=ship.formid
left join t_erp_warehouse w on w.id=plan.warehouseid
where plan.shopid=#{shopid,jdbcType=CHAR} 
	and ship.`status` >=5

	and item.sku=#{sku,jdbcType=CHAR}


	and w.parentid =#{warehouseid,jdbcType=CHAR}


	and plan.amazongroupid =#{amazongroup,jdbcType=CHAR}


	and plan.marketplaceid =#{marketplaceid,jdbcType=CHAR}


	and plan.createdate >=#{begindate,jdbcType=CHAR}


	and plan.createdate <=#{enddate,jdbcType=CHAR}



	group by date_format(plan.createdate,'%Y-%m-%d')


	group by date_format(subdate(plan.createdate,date_format(plan.createdate,'%w')-7),'%Y-%m-%d')


	group by concat( year(plan.createdate) ,'-' ,month(plan.createdate))

select item.sku sku,ship.shipment_confirmation_id ShipmentId,inp.number,g.name
	groupname,w.name warehouse,mkp.market,ship.shiped_date,
	trans.arrivalTime,item.quantity,item.received,inp.createtime,
	ship.destination center ,detail.channame ,
	case when stat.`status`='WORKING' and ship.status>=5 then '已删除' else stat.name end shipmentstatus,
	ifnull(item.received,0) sumrec,ship.status
from t_erp_ship_v2_inboundshipment_item item
left join t_erp_ship_v2_inboundshipment ship on ship.ShipmentId =item.ShipmentId
left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.shipment_confirmation_id
left join t_erp_ship_transdetail detail on detail.id = trans.channel
left join t_erp_ship_transcompany cop on cop.id=detail.company
left join t_erp_ship_status stat on stat.`status`=ship.ShipmentStatus
left join t_erp_ship_v2_inboundplan inp on inp.id=ship.formid
left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid
left join t_amazon_group g on g.id=inp.groupid
left join t_erp_warehouse w on w.id=inp.warehouseid
where inp.shopid =#{param.shopid,jdbcType=CHAR}
	and ship.status>1


		and inp.createtime >=#{param.fromDate,jdbcType=TIMESTAMP}
		and inp.createtime <=#{param.endDate,jdbcType=TIMESTAMP}


		and ship.shiped_date >=#{param.fromDate,jdbcType=TIMESTAMP}
		and ship.shiped_date <=#{param.endDate,jdbcType=TIMESTAMP}



	and inp.groupid= #{param.groupid,jdbcType=CHAR}


	and inp.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}


	and (item.sku like #{param.search,jdbcType=CHAR}
	or ship.ShipmentId like #{param.search,jdbcType=CHAR} or inp.number like #{param.search,jdbcType=CHAR} )


	and cop.name like #{param.company,jdbcType=CHAR} 


	and w.id = #{param.warehouseid,jdbcType=CHAR} 



		and item.received!=item.QuantityShipped


		and item.received=item.QuantityShipped

select item.sku sku,ship.ShipmentId,inp.number,g.name groupname, w.name warehouse,mkp.market,ship.shiped_date, trans.arrivalTime,item.quantity,item.received,inp.createtime createdate, ship.destination center ,detail.channame , case when stat.`status`='WORKING' and ship.status>=5 then '已删除' else stat.name end shipmentstatus, ifnull(item.received,0) sumrec,ship.status, m.sku mainsku,sub.sku subsku,ifnull(sub.name,m.name) name, ass.subnumber,ifnull(ass.subnumber,0)*item.QuantityShipped subskuout from t_erp_ship_v2_inbounditem item left join t_erp_ship_v2_inboundshipment ship on ship.ShipmentId =item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.ShipmentId left join t_erp_ship_transdetail detail on detail.id = trans.channel left join t_erp_ship_status stat on stat.`status`=ship.ShipmentStatus left join t_erp_ship_v2_inboundplan inp on inp.id=ship.formid left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid left join t_amazon_group g on g.id=inp.groupid left join t_amazon_auth a on a.groupid=g.id and a.region=case when mkp.region='UK' then 'EU' ELSE mkp.region end left join t_product_info p on p.sku=item.sku and p.marketplaceid=inp.marketplaceid and p.amazonauthid=a.id left join t_product_in_opt opt on p.id=opt.pid left join t_erp_material m on m.sku=IFNULL(opt.msku,p.sku) and m.shopid=inp.shopid and m.isDelete=0 left join t_erp_assembly ass on ass.mainmid=m.id left join t_erp_material sub on sub.id=submid left join t_erp_warehouse w on w.id=inp.warehouseid where inp.shopid =#{shopid,jdbcType=CHAR} and ship.status>1
and inp.createtime >=#{fromDate,jdbcType=TIMESTAMP} and inp.createtime <=#{endDate,jdbcType=TIMESTAMP}
and ship.shiped_date>=#{fromDate,jdbcType=TIMESTAMP} and ship.shiped_date<=#{endDate,jdbcType=TIMESTAMP}
and inp.groupid= #{groupid,jdbcType=CHAR}
and inp.marketplaceid= #{marketplaceid,jdbcType=CHAR}
and (item.sku like #{search,jdbcType=CHAR} or ship.shipment_confirmation_id like #{search,jdbcType=CHAR} or inp.number like #{search,jdbcType=CHAR} )

select sum(item.quantity) QuantityShipped, sum(item.received) QuantityReceived
from t_erp_ship_v2_inboundshipment_item item
left join t_erp_ship_v2_inboundshipment ship on ship.ShipmentId =item.ShipmentId
left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.ShipmentId
left join t_erp_ship_v2_inboundplan inp on inp.id=ship.formid
left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid
left join t_amazon_group g on g.id=inp.groupid
left join t_erp_warehouse w on w.id=inp.warehouseid
where inp.shopid =#{shopid,jdbcType=CHAR}
	and ship.status>1


		and inp.createtime>=#{fromDate,jdbcType=TIMESTAMP}
		and inp.createtime<=#{endDate,jdbcType=TIMESTAMP}


		and ship.shiped_date>=#{fromDate,jdbcType=TIMESTAMP}
		and ship.shiped_date<=#{endDate,jdbcType=TIMESTAMP}



	and inp.groupid= #{groupid,jdbcType=CHAR}


	and inp.warehouseid= #{warehouseid,jdbcType=CHAR}


	and inp.marketplaceid= #{marketplaceid,jdbcType=CHAR}


	and (item.sku like #{search,jdbcType=CHAR} or inp.number like #{search,jdbcType=CHAR}
		or ship.shipment_confirmation_id like #{search,jdbcType=CHAR} )

select ship.shipment_confirmation_id ShipmentId, max(g.name) groupname, max(mkp.market) market, max(ship.destination) center , max(w.name) warehouse, max(inp.createtime) createdate, max(ship.shiped_date) shiped_date, max(trans.arrivalTime) arrivalTime, max(ship.shiped_date) status6date, max(company.name) company , max(detail.channame) channame, count(item.sku) skunum, max(inp.number) number, sum(item.quantity) qtyshipped, sum(item.received) qtyreceived, max(trans.otherfee) otherfee, max(trans.singleprice*trans.transweight) price, max(trans.singleprice) singleprice, max(ifnull(trans.transweight,0)) transweight, max(ifnull(trans.otherfee,0))+ max(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)) totalprice, max(wunit ) wunit, sum(d.weight*item.Quantity) weightkg, max(case when stat.`status`='WORKING' and ship.status>=5 then '货件异常' else stat.name end) shipmentstatus, sum(ifnull(item.received,0)) sumrec, max(ship.status) status, max(start_receive_date) start_receive_date, max(trt.name) transtype, max(v.weight) boxweight, max(v.volume) boxvolume from t_erp_ship_v2_inboundshipment ship left join ( select b.shipmentid,sum(ifnull(weight,0)) weight, ROUND(sum(ifnull(length,0)*ifnull(width,0)*ifnull(height,0)/ifnull(detail.drate,5000)),2) volume from t_erp_ship_v2_inboundshipment_box b LEFT JOIN t_erp_ship_v2_inboundshipment s ON s.ShipmentId=b.shipmentid LEFT JOIN t_erp_ship_inboundtrans trans ON trans.shipmentid = s.shipment_confirmation_id LEFT JOIN t_erp_ship_transdetail detail ON detail.id = trans.channel LEFT JOIN t_erp_ship_v2_inboundplan p ON p.id=s.formid WHERE p.shopid = #{param.shopid,jdbcType=CHAR}
and p.createtime >= #{param.fromDate,jdbcType=TIMESTAMP} and p.createtime <= #{param.endDate,jdbcType=TIMESTAMP}
and s.shiped_date >=#{param.fromDate,jdbcType=DATE} and s.shiped_date <=#{param.endDate,jdbcType=DATE}
and p.groupid= #{param.groupid,jdbcType=CHAR}
and p.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}
and s.shipment_confirmation_id like #{param.search,jdbcType=CHAR}
group by b.shipmentid ) v on v.shipmentid=ship.ShipmentId left join t_erp_ship_v2_inboundshipment_item item on ship.ShipmentId =item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=ship.shipment_confirmation_id left join t_erp_ship_transcompany company on company.id=trans.company left join t_erp_ship_transdetail detail on detail.id = trans.channel left join t_erp_transtype trt on trt.id=detail.transtype left join t_erp_ship_status stat on stat.`status`=ship.ShipmentStatus left join t_erp_ship_v2_inboundplan inp on inp.id=ship.formid left join t_product_info p on p.sku=item.sku and p.marketplaceid=inp.marketplaceid LEFT JOIN t_amazon_auth a ON a.id=p.amazonauthid left join t_product_in_opt opt on p.id=opt.pid left join t_erp_material m on m.sku=IFNULL(opt.msku,p.sku) and m.shopid=inp.shopid AND m.isDelete=0 left join t_dimensions d on d.id=m.pkgDimensions left join t_marketplace mkp on mkp.marketplaceId=inp.marketplaceid left join t_amazon_group g on g.id=inp.groupid left join t_erp_warehouse w on w.id=inp.warehouseid where inp.shopid =#{param.shopid,jdbcType=CHAR} and ship.status>=1 and a.groupid=inp.groupid
and inp.createtime >= #{param.fromDate,jdbcType=TIMESTAMP} and inp.createtime <= #{param.endDate,jdbcType=TIMESTAMP}
and ship.shiped_date >=#{param.fromDate,jdbcType=DATE} and ship.shiped_date <=#{param.endDate,jdbcType=DATE}
and inp.groupid= #{param.groupid,jdbcType=CHAR}
and inp.marketplaceid= #{param.marketplaceid,jdbcType=CHAR}
and ship.shipment_confirmation_id like #{param.search,jdbcType=CHAR}
and company.name like #{param.company,jdbcType=CHAR}
and company.id = #{param.companyid,jdbcType=CHAR}
and w.id = #{param.warehouseid,jdbcType=CHAR}
and item.quantity!=item.received
and item.quantity=item.received
group by ship.ShipmentId
