select iv.sku,max(m.price) price,max(m.remark) remark, sum(ifnull(iv.afn_fulfillable_quantity,0) ) fulfillable, sum(ifnull(iv.afn_reserved_quantity,0) ) reserved, sum(ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0)) inbound, sum((ifnull(iv.afn_fulfillable_quantity,0)+ ifnull(iv.afn_reserved_quantity,0) + ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0))*m.price) totalprice, sum((ifnull(iv.afn_fulfillable_quantity,0)+ ifnull(iv.afn_reserved_quantity,0) + ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0))*m.other_cost) otherFeer, sum((ifnull(iv.afn_fulfillable_quantity,0)+ ifnull(iv.afn_reserved_quantity,0) + ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0)) * p.shipmentfee) firstLegCharges from t_inventory_report_his iv left join ( select max(a.shop_id) shopid, info.amazonauthid amazonauthid, ifnull(max(opt.msku),max(info.sku)) msku, max(a.groupid) groupid, info.sku psku, case when mp.region='EU' then 'EU' else info.marketplaceid end marketplaceid, ifnull(max(pro.shipmentfee * ex.price/100),0) shipmentfee from t_product_info info left join t_amazon_auth a on a.id=info.amazonauthid left join t_product_in_opt opt on opt.pid=info.id left join t_product_in_profit pro on pro.pid=info.id left join t_marketplace mp on mp.marketplaceId = info.marketplaceid left join t_exchangerate ex on ex.name=mp.currency where a.shop_id=#{shopid,jdbcType=CHAR}
and a.groupid=#{groupid,jdbcType=CHAR}
and (info.marketplaceid=#{marketplaceid,jdbcType=CHAR} or a.region=#{marketplaceid,jdbcType=CHAR})
and info.sku like #{sku,jdbcType=CHAR}
group by info.amazonauthid,case when mp.region='EU' then 'EU' else info.marketplaceid end,info.sku ) p on p.psku=iv.sku and p.amazonauthid=iv.amazonauthid and p.marketplaceid=iv.marketplaceid left join t_erp_material m on m.sku=p.msku and m.shopid=p.shopid and m.isDelete = 0 where p.shopid=#{shopid,jdbcType=CHAR} and iv.byday=#{byday,jdbcType=CHAR}
and iv.marketplaceid=#{marketplaceid,jdbcType=CHAR}
group by iv.sku

select sum(ifnull(iv.afn_fulfillable_quantity,0) ) fulfillable, sum(ifnull(iv.afn_reserved_quantity,0) ) reserved, sum(ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0)) inbound, sum((ifnull(iv.afn_fulfillable_quantity,0)+ ifnull(iv.afn_reserved_quantity,0) + ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0))*m.price) totalprice, sum((ifnull(iv.afn_fulfillable_quantity,0)+ ifnull(iv.afn_reserved_quantity,0) + ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0))*m.other_cost) otherFeer, sum((ifnull(iv.afn_fulfillable_quantity,0)+ ifnull(iv.afn_reserved_quantity,0) + ifnull(iv.afn_inbound_shipped_quantity,0)+ ifnull(iv.afn_inbound_receiving_quantity,0)) * p.shipmentfee) firstLegCharges from t_inventory_report_his iv left join ( select max(a.shop_id) shopid, info.amazonauthid, ifnull(max(opt.msku),max(info.sku)) msku,a.groupid, info.sku psku,case when mp.region='EU' then 'EU' else info.marketplaceid end marketplaceid, ifnull(max(pro.shipmentfee * ex.price/100),0) shipmentfee from t_product_info info left join t_amazon_auth a on a.id=info.amazonauthid left join t_product_in_opt opt on opt.pid=info.id left join t_product_in_profit pro on pro.pid=info.id left join t_marketplace mp on mp.marketplaceId = info.marketplaceid left join t_exchangerate ex on ex.name=mp.currency where a.shop_id=#{shopid,jdbcType=CHAR}
and a.groupid=#{groupid,jdbcType=CHAR}
and (info.marketplaceid=#{marketplaceid,jdbcType=CHAR} or a.region=#{marketplaceid,jdbcType=CHAR})
and info.sku like #{sku,jdbcType=CHAR}
group by info.amazonauthid,case when mp.region='EU' then 'EU' else info.marketplaceid end,info.sku ) p on p.psku=iv.sku and p.amazonauthid=iv.amazonauthid and p.marketplaceid=iv.marketplaceid left join t_erp_material m on m.sku=p.msku and m.shopid=p.shopid and m.isDelete = 0 where p.shopid=#{shopid,jdbcType=CHAR} and iv.byday=#{byday,jdbcType=CHAR}
and iv.marketplaceid=#{marketplaceid,jdbcType=CHAR}

SELECT IFNULL(f2.groupname,f1.groupname) groupname,f1.totalprice,f2.total_amount transfer_total,
IFNULL(f2.currency,f1.currency) currency ,f1.findex
from (
	select max(p.groupname) groupname,p.findex,p.marketplaceid,p.currency,
	sum(
		(
			ifnull(iv.afn_fulfillable_quantity,0)+
			ifnull(iv.afn_reserved_quantity,0)+
			ifnull(iv.afn_inbound_shipped_quantity,0)+
			ifnull(iv.afn_inbound_receiving_quantity,0) 
		)*m.price
	) totalprice
	from t_inventory_report_his iv
	left join
	(
		select max(a.shop_id) shopid,info.amazonauthid,ifnull(max(opt.msku),max(info.sku)) msku,info.sku psku,
			min(mk.findex) findex, case when mk.region='EU' then 'EUR' else mk.currency end currency,
			case when mk.region='EU' then 'EU' else info.marketplaceid end marketplaceid, 
			max(ap.`name`) groupname ,max(ap.id) gid
		from t_product_info info
		left join t_marketplace mk on info.marketplaceid=mk.marketplaceId
		left join t_amazon_auth a on a.id=info.amazonauthid
		left join t_amazon_group ap on ap.id=a.groupid
		left join t_product_in_opt opt on opt.pid=info.id
		where a.shop_id=#{shopid,jdbcType=CHAR}
		group by info.amazonauthid,case when mk.region='EU' then 'EU' else info.marketplaceid end,info.sku
	) p on p.psku=iv.sku and p.amazonauthid=iv.amazonauthid and p.marketplaceid=iv.marketplaceid
	left join t_erp_material m on m.sku=p.msku and m.shopid=#{shopid,jdbcType=CHAR}
	where p.shopid=#{shopid,jdbcType=CHAR}
		and iv.byday=DATE_FORMAT(NOW(),'%Y-%m-%d')
	group by p.gid,p.currency 
) f1
left join (
	SELECT max(g.id) gid,max(g.name) groupname, SUM(total_amount) total_amount,t.currency
	from t_amz_settlement_acc_report t
	LEFT JOIN t_amazon_auth a ON a.id=t.amazonauthid
	LEFT JOIN t_amazon_group g ON g.id=a.groupid
	WHERE a.shop_id = #{shopid,jdbcType=CHAR} 
		and a.`disable`=0 and (
			(
				t.settlement_start_date >=#{beginDate,jdbcType=CHAR}
				and t.settlement_start_date <#{endDate,jdbcType=CHAR} 
			) or
			(
				t.settlement_end_date >=#{beginDate,jdbcType=CHAR}
				and t.settlement_end_date <#{endDate,jdbcType=CHAR}
			) 
		)
	GROUP BY a.id,t.currency
)f2 on f1.groupname=f2.groupname and f1.currency= f2.currency
UNION 
SELECT IFNULL(f2.groupname,f1.groupname) groupname,f1.totalprice,f2.total_amount transfer_total,
IFNULL(f2.currency,f1.currency) currency ,f1.findex
from (
	select max(p.groupname) groupname,p.findex,p.marketplaceid,p.currency,
	sum(
		(
			ifnull(iv.afn_fulfillable_quantity,0)+
			ifnull(iv.afn_reserved_quantity,0)+
			ifnull(iv.afn_inbound_shipped_quantity,0)+
			ifnull(iv.afn_inbound_receiving_quantity,0) 
		)*m.price
	) totalprice
	from t_inventory_report_his iv
	left join
	(
		select max(a.shop_id) shopid,info.amazonauthid,ifnull(max(opt.msku),max(info.sku)) msku,info.sku psku,
			min(mk.findex) findex, case when mk.region='EU' then 'EUR' else mk.currency end currency,
			case when mk.region='EU' then 'EU' else info.marketplaceid end marketplaceid, 
			max(ap.`name`) groupname ,max(ap.id) gid
		from t_product_info info
		left join t_marketplace mk on info.marketplaceid=mk.marketplaceId
		left join t_amazon_auth a on a.id=info.amazonauthid
		left join t_amazon_group ap on ap.id=a.groupid
		left join t_product_in_opt opt on opt.pid=info.id
		where a.shop_id=#{shopid,jdbcType=CHAR}
		group by info.amazonauthid,case when mk.region='EU' then 'EU' else info.marketplaceid end,info.sku
	) p on p.psku=iv.sku and p.amazonauthid=iv.amazonauthid and p.marketplaceid=iv.marketplaceid
	left join t_erp_material m on m.sku=p.msku and m.shopid=#{shopid,jdbcType=CHAR}
	where p.shopid=#{shopid,jdbcType=CHAR}
		and iv.byday=DATE_FORMAT(NOW(),'%Y-%m-%d')
	group by p.gid,p.currency 
) f1
right join (
	SELECT max(g.id) gid,max(g.name) groupname, SUM(total_amount) total_amount,t.currency
	from t_amz_settlement_acc_report t
	LEFT JOIN t_amazon_auth a ON a.id=t.amazonauthid
	LEFT JOIN t_amazon_group g ON g.id=a.groupid
	WHERE a.shop_id = #{shopid,jdbcType=CHAR} 
		and a.`disable`=0 and (
			(
				t.settlement_start_date >=#{beginDate,jdbcType=CHAR}
				and t.settlement_start_date <#{endDate,jdbcType=CHAR} 
			) or
			(
				t.settlement_end_date >=#{beginDate,jdbcType=CHAR}
				and t.settlement_end_date <#{endDate,jdbcType=CHAR}
			) 
		)
	GROUP BY a.id,t.currency
)f2 on f1.groupname=f2.groupname and f1.currency= f2.currency
order by groupname,findex

