select marketplaceid,sum(ir.afn_fulfillable_quantity) qty
from t_inventory_report_his ir
left join t_amazon_auth auth on auth.id=ir.amazonAuthId
where auth.shop_id=#{shopid,jdbcType=CHAR}
	and byday>=#{byday,jdbcType=DATE}
	and byday < DATE_ADD(#{byday,jdbcType=DATE},INTERVAL 1 DAY)

	and sku=#{sku,jdbcType=CHAR}


	and ir.marketplaceid=#{marketplaceid,jdbcType=CHAR}


	and auth.groupid=#{groupid,jdbcType=CHAR}

group by marketplaceid

select marketplaceid,sum(ir.afn_fulfillable_quantity) qty,ir.sku
from t_inventory_report_his ir
left join t_amazon_auth auth on auth.id=ir.amazonAuthId
where auth.shop_id=#{shopid,jdbcType=CHAR}
	and byday>=#{byday,jdbcType=DATE}
	and byday < DATE_ADD(#{byday,jdbcType=DATE},INTERVAL 1 DAY)
	and (ir.afn_fulfillable_quantity>0)
	and auth.groupid=#{groupid,jdbcType=CHAR} and auth.`disable`=0

	and ir.marketplaceid=#{marketplaceid,jdbcType=CHAR}

group by marketplaceid,ir.sku

select marketplaceid,sum(ir.afn_fulfillable_quantity) qty,ir.sku
from t_inventory_report_his ir
left join t_amazon_auth auth on auth.id=ir.amazonAuthId
where auth.shop_id=#{shopid,jdbcType=CHAR}
	and byday>=#{byday,jdbcType=DATE}
	and byday < DATE_ADD(#{byday,jdbcType=DATE},INTERVAL 1 DAY)

	and ir.marketplaceid=#{marketplaceid,jdbcType=CHAR}

group by marketplaceid,ir.sku

select case when m.region='EU' then 'EU' else s.marketplaceid end marketplaceid, sum(quantity) salesum 
from t_orders_summary s
left join t_marketplace m on m.marketplaceId=s.marketplaceid
left join t_amazon_auth auth on auth.id =s.amazonAuthId
where auth.shop_id=#{shopid,jdbcType=CHAR}
	and purchase_date>=#{fromDate,jdbcType=DATE}
	and purchase_date<=#{endDate,jdbcType=DATE}

	and auth.groupid=#{groupid,jdbcType=CHAR} 


	and s.sku=#{sku,jdbcType=CHAR}


	and case when m.region='EU' then 'EU' else s.marketplaceid end=#{marketplaceid,jdbcType=CHAR}

group by case when m.region='EU' then 'EU' else s.marketplaceid end

select case when m.region='EU' then 'EU' else s.marketplaceid end marketplaceid, s.sku sku, sum(quantity) salesum
from t_orders_summary s
left join t_marketplace m on m.marketplaceId=s.marketplaceid
left join t_amazon_auth auth on auth.id =s.amazonAuthId
where auth.shop_id=#{shopid,jdbcType=CHAR}
	and purchase_date>=#{fromDate,jdbcType=DATE}
	and purchase_date<=#{endDate,jdbcType=DATE}
	and auth.groupid=#{groupid,jdbcType=CHAR} and auth.`disable`=0

	and case when m.region='EU' then 'EU' else s.marketplaceid end=#{marketplaceid,jdbcType=CHAR}

group by case when m.region='EU' then 'EU' else s.marketplaceid end,s.sku

SELECT ir.marketplaceid,ir.sku, ifnull(afn_fulfillable_quantity,0)+ifnull(afn_inbound_working_quantity,0)+ ifnull(afn_inbound_shipped_quantity,0)+ifnull(afn_inbound_receiving_quantity,0)+ case when rr.id is null then ifnull(afn_reserved_quantity,0) else ifnull(rr.reserved_fc_processing,0)+ifnull(rr.reserved_fc_transfers,0) end quantity,
ifnull(afn_fulfillable_quantity,0)+ case when rr.id is null then ifnull(afn_reserved_quantity,0) else ifnull(rr.reserved_fc_processing,0)+ifnull(rr.reserved_fc_transfers,0) end fulfillable FROM t_inventory_report ir left join t_inventory_reserved_report rr on ir.sku=rr.sku and ir.marketplaceid=rr.marketplaceid and ir.amazonAuthId=rr.amazonAuthId WHERE ir.amazonAuthId=#{amazonAuthId,jdbcType=CHAR} AND ir.isnewest=true and ir.marketplaceid=#{marketplaceid,jdbcType=CHAR}
and ir.sku=#{sku,jdbcType=CHAR}

select max(a.groupid) groupid, max(ag.name) groupname, max(a.shop_id) shopid, max(i.marketplaceid) wid, max(CONCAT('FBA-',case when i.marketplaceid='EU' then '欧洲' else m.name end )) warehouse, max(i.sku) sku, max(p.`name`) pname, max(pc.location) image, max(i.afn_warehouse_quantity) afnWarehouseQuantity, max(i.afn_fulfillable_quantity) afnFulfillableQuantity, max(i.afn_reserved_quantity) afnReservedQuantity, max(i.afn_inbound_working_quantity) afnInboundWorkingQuantity , max(i.afn_inbound_shipped_quantity) afnInboundShippedQuantity , max(i.afn_inbound_receiving_quantity) afnInboundReceivingQuantity, max(i.afn_unsellable_quantity) afnUnsellableQuantity, max(i.afn_researching_quantity) afnResearchingQuantity from t_inventory_report i left join t_amazon_auth a on i.amazonAuthId=a.id LEFT JOIN t_marketplace m ON (m.marketplaceId=i.marketplaceid OR m.region=i.marketplaceid) left join t_amazon_group ag on ag.id=a.groupid left join t_product_info p on p.sku=i.sku and p.marketplaceid=m.marketplaceId and p.amazonAuthId=a.id left join t_product_in_opt opt on opt.pid=p.id left join t_picture pc on pc.id=p.image where a.shop_id=#{param.shopid,jdbcType=CHAR} and a.disable=0 and i.isnewest=TRUE
and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}
and a.groupid=#{param.groupid,jdbcType=CHAR}
and i.sku = #{param.sku,jdbcType=CHAR}
and i.sku like #{param.search,jdbcType=CHAR}
and opt.owner=#{param.myself,jdbcType=CHAR}
group by i.id

select max('') sku, sum(afnWarehouseQuantity) afnWarehouseQuantity, sum(afnFulfillableQuantity) afnFulfillableQuantity, sum(afnReservedQuantity) afnReservedQuantity, sum(afnInboundWorkingQuantity) afnInboundWorkingQuantity , sum(afnInboundShippedQuantity) afnInboundShippedQuantity , sum(afnInboundReceivingQuantity) afnInboundReceivingQuantity, sum(afnUnsellableQuantity) afnUnsellableQuantity, sum(afnResearchingQuantity) afnResearchingQuantity from ( select max(i.afn_warehouse_quantity) afnWarehouseQuantity, max(i.afn_fulfillable_quantity) afnFulfillableQuantity, max(i.afn_reserved_quantity) afnReservedQuantity, max(i.afn_inbound_working_quantity) afnInboundWorkingQuantity , max(i.afn_inbound_shipped_quantity) afnInboundShippedQuantity , max(i.afn_inbound_receiving_quantity) afnInboundReceivingQuantity, max(i.afn_unsellable_quantity) afnUnsellableQuantity, max(i.afn_researching_quantity) afnResearchingQuantity from t_inventory_report i left join t_amazon_auth a on i.amazonAuthId=a.id LEFT JOIN t_marketplace m ON (m.marketplaceId=i.marketplaceid OR m.region=i.marketplaceid) left join t_amazon_group ag on ag.id=a.groupid left join t_product_info p on p.sku=i.sku and p.marketplaceid=m.marketplaceId and p.amazonAuthId=a.id left join t_product_in_opt opt on opt.pid=p.id left join t_erp_material mt on mt.sku=ifnull(opt.msku,p.sku) and mt.shopid=a.shop_id and mt.isDelete=0 left join t_picture pc on pc.id=p.image where a.shop_id=#{shopid,jdbcType=CHAR} and a.disable=0 and i.isnewest=TRUE
and i.marketplaceid=#{marketplaceid,jdbcType=CHAR}
and a.groupid=#{groupid,jdbcType=CHAR}
and i.sku like #{search,jdbcType=CHAR}
and mt.owner=#{myself,jdbcType=CHAR}
group by i.id ) i
