mapper.select:
 select sum(t.quantity) quantity
from t_amz_returns_report t

 t.sellerid IN (SELECT a.sellerid FROM  t_amazon_auth a  WHERE  a.shop_id = #{shopid,jdbcType=CHAR}

		and a.groupid=#{groupid,jdbcType=CHAR}

   )

		and t.return_date >=#{beginDate,jdbcType=DATE}


		and t.return_date <#{endDate,jdbcType=DATE}

select
date_format(t.return_date,'%Y-%m-%d') return_date,
date_format(subdate(return_date, case when date_format(return_date,'%w')=0 then 0 else date_format(return_date,'%w') -7 end),'%Y-%m-%d') return_date,
concat( year(t.return_date) ,'-' ,month(t.return_date)) return_date,
sum(t.quantity) quantity from t_amz_returns_report t
t.sellerid IN (SELECT a.sellerid FROM t_amazon_auth a WHERE a.shop_id = #{shopid,jdbcType=CHAR}
and a.groupid=#{groupid,jdbcType=CHAR}
)
and t.return_date >=#{beginDate,jdbcType=DATE}
and t.return_date <#{endDate,jdbcType=DATE}
group by date_format(t.return_date,'%Y-%m-%d')
group by date_format(subdate(return_date, case when date_format(return_date,'%w')=0 then 0 else date_format(return_date,'%w') -7 end),'%Y-%m-%d')
group by concat( year(return_date) ,'-' ,month(return_date))

 SELECT r.*,d.name detailedDispositionName,rs.name reasonname,s.name statusname,ifnull(p.location,p.url) image,
        m.name marketname,g.name groupname,i.name,rr.purchase_date  purchaseDate,rr.item_price itemPrice
 FROM  t_amz_returns_report   r
 left join t_amazon_auth a on a.sellerid=r.sellerid
 left join t_orders_report rr on rr.amazon_order_id=r.order_id  and  rr.amazonAuthId=a.id and rr.sku=r.sku
 LEFT JOIN t_marketplace m ON m.point_name=rr.sales_channel
 left join t_amazon_group g on g.id=a.groupid
 left join t_product_info i on i.sku=r.sku and i.marketplaceid=ifnull(m.marketplaceid,r.marketplaceid) and i.amazonauthid=a.id
 left join t_product_in_opt o on o.pid=i.id
 left join t_picture p on p.id=i.image
 LEFT JOIN t_amz_returns_status s ON s.code=r.`status`
 LEFT JOIN t_amz_returns_reason rs ON rs.code=r.reason
 LEFT JOIN t_amz_returns_detailed_disposition d ON d.code=r.detailed_disposition
 WHERE
 r.return_date >=#{param.startDate,jdbcType=TIMESTAMP}
 AND r.return_date <=#{param.endDate,jdbcType=TIMESTAMP}
 AND r.sellerid=#{param.sellerid,jdbcType=CHAR}

	 and o.owner=#{param.owner,jdbcType=CHAR}


	 and r.sku=#{param.sku,jdbcType=CHAR}


	 and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}



		 and rr.purchase_date >=date_sub(r.return_date,interval 30 day)


		 and rr.purchase_date <=date_sub(r.return_date,interval 30 day)




		 and (  (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE'  ,'MISSING_PARTS' ,'EXTRA_ITEM' ))
		     or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in (  'NO_REASON_GIVEN', 'DEFECTIVE', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'NOT_AS_DESCRIBED', 'DAMAGED_BY_FC' )
		                                                   and rr.purchase_date  <=date_sub(r.return_date,interval 16 day)
		         )
		     or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE'))
		     or  (r.detailed_disposition='DEFECTIVE'
		                                             and r.reason in ('DAMAGED_BY_CARRIER', 'FOUND_BETTER_PRICE', 'DEFECTIVE', 'NO_REASON_GIVEN', 'NOT_AS_DESCRIBED', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'DAMAGED_BY_FC')
		                                             and rr.purchase_date  <=date_sub(r.return_date,interval 16 day)
		          )
		       )
		     or r.customer_comments is not null
		    )


		 and not (
				 (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE'  ,'MISSING_PARTS' ,'EXTRA_ITEM' ))
				 or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'NO_REASON_GIVEN','DEFECTIVE','UNWANTED_ITEM', 'NOT_COMPATIBLE','NOT_AS_DESCRIBED','DAMAGED_BY_FC' )
				    and rr.purchase_date  <=date_sub(r.return_date,interval 16 day)
				 )
				 or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE'))
				 or (r.detailed_disposition='DEFECTIVE'  and r.reason in ('DAMAGED_BY_CARRIER','FOUND_BETTER_PRICE','DEFECTIVE', 'NO_REASON_GIVEN','NOT_AS_DESCRIBED','UNWANTED_ITEM', 'NOT_COMPATIBLE','DAMAGED_BY_FC')
				     and rr.purchase_date <=date_sub(r.return_date,interval 16 day)
				 )
				 )
				 or r.customer_comments is not null
		 )



	 and (r.detailed_disposition='SELLABLE'  and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))


	 and (r.detailed_disposition!='SELLABLE' and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))


	 and (not (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))



		 and r.sku like #{param.search,jdbcType=CHAR}


		 and r.asin=#{param.search,jdbcType=CHAR}


		 and r.order_id=#{param.search,jdbcType=CHAR}

select v.*,v.quantity/(select sum(rt.quantity) from t_orders_report rt where rt.amazonAuthId=v.amazonAuthId and rt.sales_channel=v.sales_channel and rt.sku=v.sku and rt.purchase_date >=#{param.startDate,jdbcType=TIMESTAMP} AND rt.purchase_date <=#{param.endDate,jdbcType=TIMESTAMP} ) returnrate from ( SELECT sum(r.quantity) quantity, m.marketplaceid, max(ifnull(p.location,p.url)) image, max(m.name) marketname, max(g.name) groupname, max(i.name) name, sum(rr.item_price) itemPrice, max(r.asin) asin, max(a.id) amazonAuthId, max(rr.sales_channel) sales_channel, max(r.sku) sku, sum(case when r.detailed_disposition='SELLABLE' and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory') then 1 else 0 end ) sellable, sum(case when r.detailed_disposition!='SELLABLE' and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory') then 1 else 0 end ) unsellable, sum(case when not (r.status='Repackaged Successfully' or r.status='Unit returned to inventory') then 1 else 0 end ) other, sum(case when ( (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE' ,'MISSING_PARTS' ,'EXTRA_ITEM' )) or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'NO_REASON_GIVEN', 'DEFECTIVE', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'NOT_AS_DESCRIBED', 'DAMAGED_BY_FC' ) and rr.purchase_date <=date_sub(r.return_date,interval 16 day) ) or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE')) or (r.detailed_disposition='DEFECTIVE' and r.reason in ('DAMAGED_BY_CARRIER', 'FOUND_BETTER_PRICE', 'DEFECTIVE', 'NO_REASON_GIVEN', 'NOT_AS_DESCRIBED', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'DAMAGED_BY_FC') and rr.purchase_date <=date_sub(r.return_date,interval 16 day) ) ) or r.customer_comments is not null ) then 1 else 0 end) defective FROM t_amz_returns_report r left join t_amazon_auth a on a.sellerid=r.sellerid left join t_orders_report rr on rr.amazon_order_id=r.order_id and rr.amazonAuthId=a.id and rr.sku=r.sku LEFT JOIN t_marketplace m ON m.point_name=rr.sales_channel left join t_amazon_group g on g.id=a.groupid left join t_product_info i on i.sku=r.sku and i.marketplaceid=ifnull(m.marketplaceid,r.marketplaceid) and i.amazonauthid=a.id left join t_product_in_opt o on o.pid=i.id left join t_picture p on p.id=i.image WHERE r.return_date >=#{param.startDate,jdbcType=TIMESTAMP} AND r.return_date <=#{param.endDate,jdbcType=TIMESTAMP} AND r.sellerid=#{param.sellerid,jdbcType=CHAR}
and o.owner=#{param.owner,jdbcType=CHAR}
and r.sku=#{param.sku,jdbcType=CHAR}
and rr.purchase_date >=date_sub(r.return_date,interval 30 day)
and rr.purchase_date <=date_sub(r.return_date,interval 30 day)
and ( (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE' ,'MISSING_PARTS' ,'EXTRA_ITEM' )) or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'NO_REASON_GIVEN', 'DEFECTIVE', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'NOT_AS_DESCRIBED', 'DAMAGED_BY_FC' ) and rr.purchase_date <=date_sub(r.return_date,interval 16 day) ) or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE')) or (r.detailed_disposition='DEFECTIVE' and r.reason in ('DAMAGED_BY_CARRIER', 'FOUND_BETTER_PRICE', 'DEFECTIVE', 'NO_REASON_GIVEN', 'NOT_AS_DESCRIBED', 'UNWANTED_ITEM','NOT_COMPATIBLE', 'DAMAGED_BY_FC') and rr.purchase_date <=date_sub(r.return_date,interval 16 day) ) ) or r.customer_comments is not null )
and not ( (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE' ,'MISSING_PARTS' ,'EXTRA_ITEM' )) or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ('NO_REASON_GIVEN', 'DEFECTIVE', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'NOT_AS_DESCRIBED', 'DAMAGED_BY_FC' ) and rr.purchase_date <=date_sub(r.return_date,interval 16 day) ) or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE')) or (r.detailed_disposition='DEFECTIVE' and r.reason in ('DAMAGED_BY_CARRIER', 'FOUND_BETTER_PRICE', 'DEFECTIVE', 'NO_REASON_GIVEN', 'NOT_AS_DESCRIBED', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'DAMAGED_BY_FC') and rr.purchase_date <=date_sub(r.return_date,interval 16 day) ) ) or r.customer_comments is not null )
and (r.detailed_disposition='SELLABLE' and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))
and (r.detailed_disposition!='SELLABLE' and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))
and (not (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))
and r.sku like #{param.search,jdbcType=CHAR}
and r.asin=#{param.search,jdbcType=CHAR}
and r.order_id=#{param.search,jdbcType=CHAR}
group by r.sellerid,m.marketplaceid,r.sku ) v

SELECT
    date_format(r.return_date,'%Y-%m-%d') returnDate,
    sum(r.quantity) quantity,
    sum(rr.item_price) itemPrice
FROM  t_amz_returns_report   r
left join t_amazon_auth a on a.sellerid=r.sellerid
left join t_orders_report rr on rr.amazon_order_id=r.order_id  and  rr.amazonAuthId=a.id and rr.sku=r.sku
LEFT JOIN t_marketplace m  ON  m.point_name=rr.sales_channel
left join t_amazon_group g on g.id=a.groupid
left join t_product_info i on i.sku=r.sku and i.marketplaceid=ifnull(m.marketplaceid,r.marketplaceid)  and i.amazonauthid=a.id
left join t_product_in_opt o on o.pid=i.id
WHERE
r.return_date >=#{param.startDate,jdbcType=TIMESTAMP}
AND r.return_date <=#{param.endDate,jdbcType=TIMESTAMP}
AND r.sellerid=#{param.sellerid,jdbcType=CHAR}

	and o.owner=#{param.owner,jdbcType=CHAR}


	and r.sku=#{param.sku,jdbcType=CHAR}


	and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}



		and rr.purchase_date >=date_sub(r.return_date,interval 30 day)


		and rr.purchase_date <=date_sub(r.return_date,interval 30 day)




		and (
		(r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE'  ,'MISSING_PARTS' ,'EXTRA_ITEM' ))
		or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in (  'NO_REASON_GIVEN', 'DEFECTIVE', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'NOT_AS_DESCRIBED', 'DAMAGED_BY_FC' )
		and rr.purchase_date <=date_sub(r.return_date,interval 16 day)
		)
		or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE'))
		or (r.detailed_disposition='DEFECTIVE'
		and r.reason in ('DAMAGED_BY_CARRIER', 'FOUND_BETTER_PRICE', 'DEFECTIVE', 'NO_REASON_GIVEN', 'NOT_AS_DESCRIBED', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'DAMAGED_BY_FC')
		and rr.purchase_date <=date_sub(r.return_date,interval 16 day)
		)
		)
		or r.customer_comments is not null
		)


		and not (
		(r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ( 'QUALITY_UNACCEPTABLE'  ,'MISSING_PARTS' ,'EXTRA_ITEM' ))
		or (r.detailed_disposition='CUSTOMER_DAMAGED' and r.reason in ('NO_REASON_GIVEN', 'DEFECTIVE', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'NOT_AS_DESCRIBED', 'DAMAGED_BY_FC' )
		and rr.purchase_date <=date_sub(r.return_date,interval 16 day)
		)
		or ((r.detailed_disposition='DEFECTIVE' and r.reason in ('MISSING_PARTS','QUALITY_UNACCEPTABLE'))
		or (r.detailed_disposition='DEFECTIVE'
		and r.reason in ('DAMAGED_BY_CARRIER', 'FOUND_BETTER_PRICE', 'DEFECTIVE', 'NO_REASON_GIVEN', 'NOT_AS_DESCRIBED', 'UNWANTED_ITEM', 'NOT_COMPATIBLE', 'DAMAGED_BY_FC')
		and rr.purchase_date <=date_sub(r.return_date,interval 16 day)
		)
		)
		or r.customer_comments is not null
		)



	and (r.detailed_disposition='SELLABLE'  and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))


	and (r.detailed_disposition!='SELLABLE' and (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))


	and (not (r.status='Repackaged Successfully' or r.status='Unit returned to inventory'))



		and r.sku like #{param.search,jdbcType=CHAR}


		and r.asin=#{param.search,jdbcType=CHAR}


group by date_format(r.return_date,'%Y-%m-%d')

		SELECT  max(rs.name) name,sum(r.quantity) quantity
		FROM  t_amz_returns_report   r
		left join t_amazon_auth a on a.sellerid=r.sellerid
		left join t_orders_report rr on rr.amazon_order_id=r.order_id  and  rr.amazonAuthId=a.id and rr.sku=r.sku
		LEFT JOIN t_marketplace m ON  m.point_name=rr.sales_channel
		left join t_amazon_group g on g.id=a.groupid
		left join t_product_info i on i.sku=r.sku and i.marketplaceid=ifnull(m.marketplaceid,r.marketplaceid)  and i.amazonauthid=a.id
		left join t_product_in_opt o on o.pid=i.id
		LEFT JOIN t_amz_returns_status s ON s.code=r.`status`
		LEFT JOIN t_amz_returns_reason rs ON rs.code=r.reason
		WHERE
		r.return_date >=#{param.startDate,jdbcType=TIMESTAMP}
		AND r.return_date <=#{param.endDate,jdbcType=TIMESTAMP}
		AND r.sellerid=#{param.sellerid,jdbcType=CHAR}

			and o.owner=#{param.owner,jdbcType=CHAR}


			and r.sku=#{param.sku,jdbcType=CHAR}


			and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}



				and r.sku like #{param.search,jdbcType=CHAR}


				and r.asin=#{param.search,jdbcType=CHAR}


				and r.order_id=#{param.search,jdbcType=CHAR}


         group by r.reason

SELECT
date_format(r.purchase_date,'%Y-%m-%d') purchaseDate,
sum(r.quantity) quantity
FROM  t_orders_summary   r
left join t_amazon_auth a on a.id=r.amazonAuthId
LEFT JOIN t_marketplace m ON  m.marketplaceid=r.marketplaceid
left join t_amazon_group g on g.id=a.groupid
left join t_product_info i on i.sku=r.sku and i.marketplaceid=r.marketplaceid  and i.amazonauthid=a.id
left join t_product_in_opt o on o.pid=i.id
WHERE
r.purchase_date >=#{param.startDate,jdbcType=TIMESTAMP}
AND r.purchase_date <=#{param.endDate,jdbcType=TIMESTAMP}
AND a.sellerid=#{param.sellerid,jdbcType=CHAR}

	and o.owner=#{param.owner,jdbcType=CHAR}


	and r.sku=#{param.sku,jdbcType=CHAR}


	and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}



		and r.sku like #{param.search,jdbcType=CHAR}


		and r.asin=#{param.search,jdbcType=CHAR}


group by date_format(r.purchase_date,'%Y-%m-%d')
