	where t.purchase_date >=#{startDate,jdbcType=TIMESTAMP}
		and t.purchase_date <=#{endDate,jdbcType=TIMESTAMP}
		and (t.quantity>0 or (t.quantity=0 and t.item_status='Cancelled'))

