SELECT g.name groupname,m.name marketname,ifnull(c.name,i.pgroup) catename,v.sku,v.quantity,v.orderprice ,r.afn_fulfillable_quantity fulfillable, IFNULL(p.location,p.url) image,ifnull(mt.name,i.name) name FROM ( SELECT s.amazonAuthId,s.marketplaceid,s.sku,SUM(s.quantity) quantity,SUM(s.orderprice) orderprice FROM t_orders_summary s WHERE EXISTS(SELECT id FROM t_amazon_auth WHERE id=s.amazonAuthId AND shop_id=#{shopid,jdbcType=CHAR}
and groupid=#{groupid,jdbcType=CHAR}
)
and s.purchase_date >= #{beginDate,jdbcType=CHAR}
and s.purchase_date <= #{endDate,jdbcType=CHAR}
GROUP BY s.amazonAuthId,s.marketplaceid,s.sku ORDER BY quantity desc LIMIT 15) v LEFT JOIN t_product_info i ON i.amazonAuthId=v.amazonAuthId AND i.marketplaceid=v.marketplaceid AND i.sku=v.sku LEFT JOIN t_amazon_auth a ON a.id=i.amazonAuthId LEFT JOIN t_amazon_group g ON g.id=a.groupid LEFT JOIN t_marketplace m ON m.marketplaceId=i.marketplaceid LEFT JOIN t_picture p ON p.id=i.image LEFT JOIN t_inventory_report r ON r.amazonAuthId=i.amazonAuthId AND r.sku=i.sku AND r.marketplaceid=case when m.region='EU' then 'EU' ELSE i.marketplaceid END LEFT JOIN t_product_in_opt o ON o.pid=i.id LEFT JOIN t_erp_material mt ON mt.sku=IFNULL(o.msku,i.sku) AND mt.shopid=g.shopid AND mt.isDelete=0 LEFT JOIN t_erp_material_category c ON c.id=mt.categoryid WHERE i.amazonAuthId IS NOT NULL LIMIT 5

select m.marketplaceid,v.sales_channel pointName,v.quantity quantity FROM (select sm.sales_channel,sum(sm.quantity) quantity from t_summaryall sm WHERE purchase_date =DATE_FORMAT(date_sub(curdate(),interval 2 day),'%Y-%m-%d') and exists(select id from t_amazon_auth where shop_id= #{shopid,jdbcType=CHAR} AND id=sm.amazonAuthId) and (order_status='Shipped' or order_status='Pending' )
and sm.amazonauthid = #{amazonAuthority.id,jdbcType=CHAR}
group by sm.sales_channel ) v LEFT JOIN t_marketplace m ON m.point_name=v.sales_channel order BY v.quantity desc

select purchase_date,sum(quantity) quantity from t_summaryall sm where purchase_date>=date_sub(curdate(),interval 8 day) and purchase_date < curdate() and (order_status='Shipped' or order_status='Pending' ) and exists(select id from t_amazon_auth where shop_id= #{shopid,jdbcType=CHAR} AND id=sm.amazonAuthId)
and sm.amazonauthid = #{amazonAuthority.id,jdbcType=CHAR}
