-- 根据selecteds参数动态构建SQL

    update t_amz_product_refresh r
    LEFT JOIN t_amazon_auth a ON a.id=r.amazonauthid
    SET


            r.detail_refresh_time = NULL,


            r.price_refresh_time = NULL,


            r.catalog_refresh_time = NULL,


    WHERE
    a.shop_id=#{dto.shopid,jdbcType=CHAR}

    AND a.groupid= #{dto.groupid,jdbcType=CHAR}


    AND r.marketplaceid=#{dto.marketplaceid,jdbcType=CHAR}


        AND r.pid IN (

            #{pid,jdbcType=CHAR}

        )
