mapper.select:
          select    
	              0  amount,

		          m.*

		          ,v2.avgsales-m.marketsalesday differentsales,v2.*

		  from (
		  	 select  msku,max(p.shopid) shopid,max(p.groupid) groupid,


		               SUM(needship) needship,
		  	           SUM(needshipfba) needshipfba,


		  	           SUM(needpurchase) needpurchase,

		  	           DATE_FORMAT(MAX(p.opttime),'%Y-%m-%d')  opttime


		                 ,SUM(case when p.marketplaceid in (

								 #{market,jdbcType=CHAR} 

						   )
		                 then needship else 0 end ) marketneedship




		               ,SUM(case when p.marketplaceid in (

								 #{market,jdbcType=CHAR} 
						   		)
						    then needpurchase else 0 end ) marketneedpurchase

		               ,MAX(case when ir.marketplaceid  in (

								 #{market,jdbcType=CHAR} 
						   		)
		               then ir.afn_fulfillable_quantity else 0 end ) afn_fulfillable_quantity
		  	           ,SUM(case when p.marketplaceid in (

								 #{market,jdbcType=CHAR} 
						   		)
		  	           then salesday else 0 end ) marketsalesday
		  	           ,SUM(case when p.marketplaceid in (

								 #{market,jdbcType=CHAR} 
						   		)
		  	           then avgsales else 0 end ) marketavgsales
		  	           ,MIN(case when p.marketplaceid in (

								 #{market,jdbcType=CHAR} 
						   		)

		  	           then short_time else '2399-01-01' end)  short_time



		                 ,SUM(needship) marketneedship


		  	           ,SUM(needpurchase) marketneedpurchase

		  	            ,SUM(salesday) marketsalesday
		  	            ,MIN(short_time)  short_time
		  	            ,SUM(avgsales) marketavgsales

		  	 from t_amz_product_sales_plan p

		  	        left join t_inventory_report ir on ir.amazonAuthId=p.amazonauthid and ir.sku=p.sku    and  ir.marketplaceid=p.marketplaceid 

		  	 where  p.shopid=#{dto.shopid,jdbcType=CHAR} 

			    and p.groupid=#{dto.groupid,jdbcType=CHAR}


						     and p.groupid in (

										#{groupid,jdbcType=CHAR} 

						   )


		        and (p.msku like #{dto.search,jdbcType=CHAR} or p.sku like #{dto.search,jdbcType=CHAR} )


				 and p.sku in (

										#{sku,jdbcType=CHAR} 

				              )

			 GROUP BY  p.msku
		  ) m 

		      LEFT JOIN ( 
			         select 
					  ifnull(o.msku,i.sku) v2msku,count(i.id) vfield,
					  sum(r.avgsales) avgsales,
					  sum(r.sales_seven) sales_seven,
					  sum(r.sales_month) sales_month
					  from  t_product_info i 
					  left join t_amazon_auth a on a.id=i.amazonauthid
					  left join t_product_in_opt o on o.pid=i.id
					  left join t_marketplace m on m.marketplaceid=i.marketplaceid
					  left join t_product_in_order r on r.pid=i.id
					  where  
					      a.shop_id=#{dto.shopid,jdbcType=CHAR} 
					      and  a.disable = FALSE  and (o.disable is null or o.disable=0) 
			              and  i.invalid=0   and i.isparent=0

						    and a.groupid=#{dto.groupid,jdbcType=CHAR}


						    and a.groupid in (

										#{groupid,jdbcType=CHAR} 

						   )



								and i.parentasin in (SELECT t1.parentAsin FROM t_product_info t1 WHERE t1.amazonAuthId=i.amazonAuthId
								and  t1.asin like #{dto.search,jdbcType=CHAR} AND t1.invalid=0 )


								and i.parentasin in  (select  t1.parentAsin FROM t_product_info t1 WHERE t1.amazonAuthId=i.amazonAuthId
								and  t1.sku like #{dto.search,jdbcType=CHAR} AND t1.invalid=0)



					      and o.status=#{dto.status,jdbcType=CHAR}


					      and  i.marketplaceid in ( 

								 #{market,jdbcType=CHAR} 

					       )



					         and  ifnull(i.inSnl,false)=true


					         and  ifnull(i.inSnl,false)=false



							and i.id in (

									    '#'	


										#{tags,jdbcType=CHAR} 


							    )

					      GROUP BY  ifnull(o.msku,i.sku) 
						) v2 ON v2.v2msku=m.msku

		   where  m.shopid=#{dto.shopid,jdbcType=CHAR}  

			      and m.groupid=#{dto.groupid,jdbcType=CHAR}


			     and v2.vfield>0


		         and v2.avgsales>m.marketavgsales


		         and m.short_time <=date_add(now(), interval 15 day)


		         and m.short_time<=date_add(now(), interval 30 day)


		         and m.short_time<=date_add(now(), interval 60 day)

SELECT ifnull(afn_fulfillable_quantity,0)+ifnull(afn_inbound_working_quantity,0)+ ifnull(afn_inbound_shipped_quantity,0)+ifnull(afn_inbound_receiving_quantity,0)+ case when rr.id is null then ifnull(afn_reserved_quantity,0) else ifnull(rr.reserved_fc_processing,0)+ifnull(rr.reserved_fc_transfers,0) end quantity, ifnull(afn_fulfillable_quantity,0)+ case when rr.id is null then ifnull(afn_reserved_quantity,0) else ifnull(rr.reserved_fc_processing,0)+ifnull(rr.reserved_fc_transfers,0) end localquantity,
v.sku,v.marketname,v.marketplaceid,v.sysavgsales,v.amazonauthid,v.groupname, v.openDate,v.sum15,v.summonth,v.sumweek,v.msku,v.groupid, v.sumseven,v.statusname,v.statuscolor,v.country,v.shopid,v.asin,
p.avgsales,p.needship,p.needshipfba,p.needpurchase,p.ship_min_cycle_sale,p.short_time, p.needpurchase,p.salesday,p.shipday,p.delivery_cycle, r.afn_fulfillable_quantity, r.afn_inbound_working_quantity, r.afn_unsellable_quantity, r.afn_inbound_shipped_quantity,r.afn_inbound_receiving_quantity,r.afn_reserved_quantity, r.afn_total_quantity,r.afn_researching_quantity,r.afn_reserved_future_supply, rr.reserved_fc_processing,rr.reserved_fc_transfers,rr.reserved_qty,rr.reserved_customerorders ,0 subnum, 0 aftersalesday, ifnull(p.needship,0) amount ,0 reallyamount FROM ( select p.sku sku,
p.msku msku,
max(p.msku) msku,
max(info.asin) asin, max(auth.id) amazonauthid, max(auth.shop_id) shopid, max(auth.groupid) groupid, max(g.name) groupname, p.marketplaceid marketplaceid, max(case when mk.region='EU' then '欧洲' ELSE mk.name END ) marketname, max(case when mk.region='EU' then 'EU' ELSE mk.market END ) country,
sum(t.sales_week) sumweek, sum(t.sales_seven) sumseven, sum(t.sales_month) summonth, sum(t.sales_fifteen) sum15, min(info.openDate) openDate, sum(t.avgsales) sysavgsales, max(d.name) statusname, max(d.color) statuscolor, max(mk.findex) findex FROM t_amz_product_sales_plan p left join t_amazon_auth auth on auth.id = p.amazonAuthId left join t_marketplace mk on mk.marketplaceid=p.marketplaceid or mk.region=p.marketplaceid left join t_product_info info on p.amazonauthid=info.amazonauthid and info.sku=p.sku and info.marketplaceid=mk.marketplaceid left join t_product_in_opt o on o.pid=info.id left join t_product_info_status_define d on d.id=o.status LEFT JOIN t_product_in_order t on t.pid = info.id left join t_amazon_group g on g.id=auth.groupid where auth.disable = FALSE and (o.disable is null or o.disable=0) and info.invalid=0 and info.isparent=0 and auth.shop_id=#{shopid,jdbcType=CHAR}
and auth.groupid=#{groupid,jdbcType=CHAR}
AND p.msku=#{msku,jdbcType=CHAR}
AND p.msku in (
#{mskuitem,jdbcType=CHAR}
)
and auth.groupid in (
#{group,jdbcType=CHAR}
)
and p.marketplaceid in (
#{market,jdbcType=CHAR}
)
group by
p.msku,
p.sku,p.marketplaceId ) v LEFT JOIN t_amz_product_sales_plan p ON p.sku=v.sku AND p.marketplaceid=v.marketplaceid AND p.amazonauthid=v.amazonauthid and p.shopid=v.shopid and p.groupid=v.groupid and p.msku=v.msku LEFT JOIN t_inventory_report r ON r.sku=v.sku AND r.marketplaceid=v.marketplaceid AND r.amazonAuthId=v.amazonauthid LEFT JOIN t_inventory_reserved_report rr on v.sku=rr.sku and v.marketplaceid=rr.marketplaceid and v.amazonAuthId=rr.amazonAuthId LEFT JOIN t_erp_market_priority pri on pri.marketplaceid=v.marketplaceid and pri.groupid=v.groupid order by ifnull(pri.priority,v.findex)

select * from ( select info.sku sku, info.asin, auth.id amazonauthid, auth.shop_id shopid, auth.groupid, mk.marketplaceId marketplaceid, mk.name marketname, 'EU' country, ifnull(o.msku,info.sku) msku, t.sales_week sumweek, t.sales_seven sumseven, t.sales_month summonth, t.sales_fifteen sum15, info.openDate openDate, t.avgsales sysavgsales, d.name statusname, d.color statuscolor, ifnull(pri.priority,mk.findex) findex, g.name groupname, ic.quantity ,0 amount ,0 reallyamount ,0 subnum FROM t_product_info info left join t_amazon_auth auth on auth.id = info.amazonauthid left join t_marketplace mk on mk.marketplaceid=info.marketplaceid left join t_amz_rpt_inventory_country ic on ic.sku=info.sku and ic.authid=info.amazonauthid and ic.country=mk.market left join t_product_in_opt o on o.pid=info.id left join t_product_info_status_define d on d.id=o.status LEFT JOIN t_product_in_order t on t.pid = info.id left join t_amazon_group g on g.id=auth.groupid LEFT JOIN t_erp_market_priority pri on pri.marketplaceid=mk.marketplaceid and pri.groupid=auth.groupid where auth.disable = FALSE and ic.id is null and info.invalid=0 and info.isparent=0 and ifnull(o.disable,0)=0 and auth.shop_id=#{shopid,jdbcType=CHAR}
and auth.groupid=#{groupid,jdbcType=CHAR}
AND info.sku=#{sku,jdbcType=CHAR}
and auth.groupid in (
#{group,jdbcType=CHAR}
)
and p.marketplaceid in (
#{market,jdbcType=CHAR}
)
and mk.marketplaceid in('A1PA6795UKMFR9','A13V1IB3VIYZZH','A33AVAJ2PDY3EV','A1RKKUPIHCS9HS','APJ6JRA9NG5V4','A1805IZSGTT6HS','A1C3SOZRARQ6R3','A2NODRKZP88ZB9') union all select ic.sku sku, info.asin, auth.id amazonauthid, auth.shop_id shopid, auth.groupid, mk.marketplaceId marketplaceid, mk.name marketname, 'EU' country, ifnull(o.msku,ic.sku) msku, t.sales_week sumweek, t.sales_seven sumseven, t.sales_month summonth, t.sales_fifteen sum15, info.openDate openDate, t.avgsales sysavgsales, d.name statusname, d.color statuscolor, ifnull(pri.priority,mk.findex) findex, g.name groupname, ic.quantity ,0 amount ,0 reallyamount ,0 subnum
FROM t_amz_rpt_inventory_country ic left join t_amazon_auth auth on auth.id = ic.authid left join t_marketplace mk on mk.market=ic.country left join t_product_info info on info.sku=ic.sku and info.amazonauthid=ic.authid and info.marketplaceid=mk.marketplaceid and info.invalid=0 and info.isparent=0 left join t_product_in_opt o on o.pid=info.id left join t_product_info_status_define d on d.id=o.status LEFT JOIN t_product_in_order t on t.pid = info.id left join t_amazon_group g on g.id=auth.groupid LEFT JOIN t_erp_market_priority pri on pri.marketplaceid=mk.marketplaceid and pri.groupid=auth.groupid where auth.disable = FALSE and auth.shop_id=#{shopid,jdbcType=CHAR}
and auth.groupid=#{groupid,jdbcType=CHAR}
AND ic.sku=#{sku,jdbcType=CHAR}
and auth.groupid in (
#{group,jdbcType=CHAR}
)
and p.marketplaceid in (
#{market,jdbcType=CHAR}
)
and mk.marketplaceid in('A1PA6795UKMFR9','A13V1IB3VIYZZH','A33AVAJ2PDY3EV','A1RKKUPIHCS9HS','APJ6JRA9NG5V4','A1805IZSGTT6HS','A1C3SOZRARQ6R3','A2NODRKZP88ZB9') ) v order by findex
