mapper.select:
select * from (select info.sku sku, max('EU') marketplaceid, max(auth.id) amazonauthid, max(auth.groupid) groupid, max(auth.shop_id) shopid, ifnull(max(o.msku),max(info.sku)) msku, sum(t.sales_week) sumweek, sum(t.sales_seven) sumseven, sum(t.sales_month) summonth, sum(t.sales_fifteen) sum15, min(info.openDate) openDate, sum(t.avgsales) avgsales, max(t.lastupdate) lastupdate from t_product_info info left join t_product_in_order t on t.pid = info.id left join t_product_in_opt o on o.pid=info.id left join t_marketplace mk on info.marketplaceid=mk.marketplaceid left join t_amazon_auth auth on auth.id = info.amazonAuthId where auth.disable = FALSE and (o.disable is null or o.disable=0) and info.invalid=0 and info.isparent=0 and mk.region='EU' and info.amazonauthid = #{amazonauthid,jdbcType=CHAR}
and info.sku = #{sku,jdbcType=CHAR}
group by sku) v inner join t_erp_material m on m.sku=v.msku and m.shopid=v.shopid and m.isdelete=0
LEFT JOIN t_amz_product_sales_plan p ON p.amazonauthid=v.amazonauthid and p.sku=v.sku and p.marketplaceid=v.marketplaceid and p.opttime>ifnull(v.lastupdate,CURRENT_DATE()) and p.groupid=v.groupid where p.id IS NULL
order by v.summonth asc
