mapper.select:
select v.* from (SELECT MAX(g.name) groupname, MAX(g.id) groupid, MAX(a.id) amazonauthid, MAX( case when m.region='EU' then '欧洲' ELSE m.name END ) marketname, MAX(i.openDate) openDate, MAX(i.asin) asin, MAX(i.name) name, MAX(i.sku) psku, IFNULL(MAX(o.msku),max(i.sku)) msku, case when m.region='EU' then 'EU' ELSE m.marketplaceId END marketplaceid, max(ifnull(p.location,p.url)) image, SUM(r.avgsales) avgsales, SUM(r.sales_seven) sumseven, SUM(r.sales_month) summonth, SUM(r.sales_fifteen) sum15, max(d.name) statusname, max(d.color) statuscolor FROM t_product_info i LEFT JOIN t_product_in_opt o ON o.pid=i.id LEFT JOIN t_product_in_order r ON r.pid=i.id LEFT JOIN t_product_info_status_define d on d.id=o.status LEFT JOIN t_amazon_auth a ON a.id=i.amazonAuthId LEFT JOIN t_amazon_group g ON g.id=a.groupid LEFT JOIN t_marketplace m ON m.marketplaceId=i.marketplaceid LEFT JOIN t_picture p ON p.id=i.image WHERE a.shop_id=#{param.shopid,jdbcType=CHAR} and a.disable = FALSE and (o.disable is null or o.disable=0) and i.invalid=0 and i.isparent=0
AND a.groupid=#{param.groupid,jdbcType=CHAR}
AND (m.marketplaceId=#{param.marketplaceid,jdbcType=CHAR} OR m.region=#{param.marketplaceid,jdbcType=CHAR})
AND sku like #{param.sku,jdbcType=CHAR}
AND i.openDate>= #{param.fromDate,jdbcType=DATE}
AND o.owner= #{param.owner,jdbcType=DATE}
AND i.openDate <= #{param.toDate,jdbcType=DATE}
AND IFNULL(o.msku,i.sku) like #{param.sku,jdbcType=CHAR}
AND i.asin like #{param.asin,jdbcType=CHAR}
GROUP BY i.amazonAuthId,i.sku,case when m.region='EU' then 'EU' ELSE m.marketplaceId END ) v left join t_amz_product_sales_plan p on p.groupid=v.groupid and p.marketplaceid=v.marketplaceid and v.psku=p.sku
where p.id is null

select max(p.id) id,p.sku,p.marketplaceid,p.groupid,sum(p.quantity) quantity, null operator, null opttime from t_product_in_presale p left join t_marketplace m on m.marketplaceid=p.marketplaceid where p.groupid=#{groupid,jdbcType=BIGINT} and (p.marketplaceid=#{marketplaceid,jdbcType=CHAR} or m.region=#{marketplaceid,jdbcType=CHAR})
and p.sku=#{sku,jdbcType=CHAR}
and p.date>=now() group by p.sku

select   p.id,p.sku,p.marketplaceid,p.groupid, p.date, p.quantity, p.operator, p.opttime
from t_product_in_presale p
left join t_marketplace m on m.marketplaceid=p.marketplaceid
where p.sku=#{sku,jdbcType=CHAR} and (p.marketplaceid=#{marketplaceid,jdbcType=CHAR} or m.region=#{marketplaceid,jdbcType=CHAR}) 
  and p.groupid=#{groupid,jdbcType=BIGINT} 
  and p.date=#{date,jdbcType=CHAR}

    select   p.id,p.sku,p.marketplaceid,p.groupid, p.date, p.quantity, p.operator, p.opttime
    from t_product_in_presale p
    left join t_marketplace m on m.marketplaceid=p.marketplaceid
    where p.sku=#{sku,jdbcType=CHAR} and (p.marketplaceid=#{marketplaceid,jdbcType=CHAR} or m.region=#{marketplaceid,jdbcType=CHAR}) 
      and p.groupid=#{groupid,jdbcType=BIGINT} 
      AND p.date>=#{beginDate,jdbcType=CHAR}
	  AND p.date<#{endDate,jdbcType=CHAR}

   SELECT month(s.purchase_date) month ,SUM(s.quantity) quantity FROM t_orders_summary s
    left join t_marketplace m on m.marketplaceid=s.marketplaceid
	LEFT JOIN t_amazon_auth a ON a.id=s.amazonAuthId
	WHERE a.groupid=#{groupid,jdbcType=BIGINT} 
	AND (s.marketplaceid=#{marketplaceid,jdbcType=CHAR}  or m.region=#{marketplaceid,jdbcType=CHAR})
	AND s.sku=#{sku,jdbcType=CHAR} 
	AND s.purchase_date>=#{beginDate,jdbcType=CHAR}
	AND s.purchase_date<#{endDate,jdbcType=CHAR}
	group by month(s.purchase_date)

	   SELECT DAY(s.purchase_date) day,SUM(s.quantity) quantity FROM t_orders_summary s
	    left join t_marketplace m on m.marketplaceid=s.marketplaceid
		LEFT JOIN t_amazon_auth a ON a.id=s.amazonAuthId
		WHERE a.groupid=#{groupid,jdbcType=BIGINT} 
		AND (s.marketplaceid=#{marketplaceid,jdbcType=CHAR}  or m.region=#{marketplaceid,jdbcType=CHAR})
		AND s.sku=#{sku,jdbcType=CHAR} 
		AND s.purchase_date>=#{beginDate,jdbcType=CHAR}
		AND s.purchase_date<#{endDate,jdbcType=CHAR}
        group by s.purchase_date

