mapper.select:
select *
from t_product_info
where sku = #{sku,jdbcType=CHAR}
	and marketplaceid=#{marketplaceid,jdbcType=CHAR}
	and amazonAuthId=#{amazonAuthId,jdbcType=CHAR}

   SELECT  t.* FROM t_product_info t
	LEFT JOIN t_product_in_opt o on o.pid=t.id
	LEFT JOIN t_amazon_auth auth ON auth.id=t.amazonAuthId
	LEFT JOIN t_amazon_group g ON g.id=auth.groupid 
	LEFT JOIN t_marketplace m ON m.marketplaceId=t.marketplaceid
	WHERE ifnull(o.msku,t.sku)=#{msku,jdbcType=CHAR} 

	 and  m.region=#{marketplaceid,jdbcType=CHAR}



			 and  t.marketplaceid=#{marketplaceid,jdbcType=CHAR}


	and ifnull(o.disable,false)=false

	   AND g.id=#{groupid,jdbcType=CHAR} 

	   AND g.shopid=#{shopid,jdbcType=CHAR}  AND t.invalid=0

select ifnull(p.location,p.url) location,t.*
from t_product_info t
left join t_picture p on p.id=t.image
left join t_amazon_auth au on au.id=t.amazonAuthId
where au.shop_id=#{shopid,jdbcType=CHAR}
	and t.sku like #{sku,jdbcType=CHAR}

SELECT amazonAuthId,marketplaceid,sku,SUM(quantity) sales30,SUM(ordersum) order30, SUM(case when (purchase_date >= DATE_FORMAT(DATE_SUB(now(),INTERVAL 15 DAY),'%Y-%m-%d') and purchase_date < now()) then quantity ELSE 0 END ) sales15, SUM(case when (purchase_date >= DATE_FORMAT(DATE_SUB(now(),INTERVAL 7 DAY),'%Y-%m-%d') and purchase_date < now()) then quantity ELSE 0 END ) sales7, SUM(case when (purchase_date >= DATE_FORMAT(DATE_SUB(now(),INTERVAL 8 DAY),'%Y-%m-%d') and purchase_date < DATE_FORMAT(DATE_SUB(now(),INTERVAL 1 DAY),'%Y-%m-%d')) then quantity ELSE 0 END ) salesWeek, SUM(case when (purchase_date >= DATE_FORMAT(DATE_SUB(now(),INTERVAL 7 DAY),'%Y-%m-%d') and purchase_date < now()) then ordersum ELSE 0 END ) order7, SUM(case when (purchase_date >= DATE_FORMAT(DATE_SUB(now(),INTERVAL 7 DAY),'%Y-%m-%d') and purchase_date < now()) then orderprice ELSE 0 END ) orderprice7 FROM t_orders_summary orde WHERE purchase_date >= DATE_FORMAT(DATE_SUB(now(),INTERVAL 30 DAY),'%Y-%m-%d') AND purchase_date < now()
and orde.amazonAuthId = #{amazonAuthId,jdbcType=CHAR}
group by orde.sku,orde.marketplaceid,orde.amazonAuthId having sales30 > 0

SELECT a.shop_id from t_product_info p 
left join t_amazon_auth a on a.id=p.amazonAuthId
where p.id=#{pid,jdbcType=CHAR}

select * from (select m.id,i.sku sku,i.asin asin,m.color color,m.name name,m.shopid shopid,m.sku msku ,m.upc upc, IFNULL(p.location,p.url) image,m.brand brand,m.price price,ifnull(m.boxnum,0) boxnum, ifnull(inv.quantity,0) fulfillable,
ifnull(inv2.quantity,0) willfulfillable,
m.MOQ,m.isDelete isDelete,c.name supplier, m.issfg issfg, m.remark remark,u.name operator,m.supplier supplierid,m.opttime opttime,m.delivery_cycle, m.effectivedate createdate,m.purchaseUrl,i.id pid, ifnull(i.fnsku,o.fnsku) fnsku,i.name pname,i.marketplaceid from t_product_info i Left join t_product_in_opt o on o.pid=i.id Left join t_erp_material m on m.sku=ifnull(o.msku,i.sku) and m.shopid=#{param.shopid,jdbcType=CHAR} and m.isDelete = 0 left join t_erp_customer c on c.id=m.supplier left join t_picture p on m.image=p.id left join t_erp_inventory inv on inv.materialid=m.id and inv.status='fulfillable' and inv.warehouseid = #{param.warehouseid,jdbcType=CHAR} and inv.shopid=#{param.shopid,jdbcType=CHAR}
left join ( select shopid,warehouseid,a.mainmid materialid , min(ifnull(inv.quantity,0)/a.subnumber) quantity from t_erp_assembly a left join t_erp_inventory inv on inv.materialid=a.submid and inv.warehouseid=#{param.warehouseid,jdbcType=CHAR} and inv.status='fulfillable' where inv.shopid=#{param.shopid,jdbcType=CHAR}
and inv.warehouseid = #{param.warehouseid,jdbcType=CHAR}
group by shopid,a.mainmid ) inv2 on inv2.materialid=m.id and m.issfg=1
left join t_userinfo u on u.id=m.operator where i.amazonAuthId=#{param.amazonauthid,jdbcType=CHAR} and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}
and (i.sku like #{param.search,jdbcType=CHAR})
) v
where fulfillable > 0 or willfulfillable>0

select ap.sku,ap.name,ifnull(p.location,p.url) image from t_product_info ap left join t_amazon_auth a on a.id=ap.amazonAuthId left join t_picture p on p.id=ap.image where ap.sku = #{sku,jdbcType=CHAR}
and (ap.marketplaceid=#{marketplaceid,jdbcType=CHAR} or a.region=#{marketplaceid,jdbcType=CHAR} )
and a.groupid=#{groupid,jdbcType=CHAR} limit 0,1

SELECT IFNULL(max(o.msku) ,Max(p.sku)) msku from  t_product_info p 
left join t_product_in_opt o on p.id = o.pid
left join t_marketplace mk on mk.marketplaceid=p.marketplaceid
where p.sku=#{sku,jdbcType=CHAR}
and (p.marketplaceid=#{marketplaceid,jdbcType=CHAR} or mk.region=#{marketplaceid,jdbcType=CHAR})
and p.amazonAuthId=#{amazonAuthId,jdbcType=CHAR}

select m.id,i.sku sku,m.color color,m.name name,m.shopid shopid,m.sku msku ,m.upc upc, IFNULL(p.location,p.url) image,m.brand brand,m.price price,ifnull(m.boxnum,0) boxnum, ifnull(inv.quantity,0) fulfillable,m.MOQ,m.isDelete isDelete,c.name supplier, m.issfg issfg, m.remark remark,u.name operator,m.supplier supplierid,m.opttime opttime,m.delivery_cycle, m.effectivedate createdate,m.purchaseUrl,i.asin asin from t_product_info i Left join t_product_in_opt o on o.pid=i.id Left join t_erp_material m on m.sku=ifnull(o.msku,i.sku) and m.shopid=#{param.shopid,jdbcType=CHAR} and m.isDelete = 0 left join t_erp_customer c on c.id=m.supplier left join t_picture p on m.image=p.id left join t_erp_inventory inv on inv.materialid=m.id and inv.status='fulfillable' and inv.warehouseid = #{param.warehouseid,jdbcType=CHAR} and inv.shopid=#{param.shopid,jdbcType=CHAR} left join t_userinfo u on u.id=m.operator where i.amazonAuthId=#{param.amazonauthid,jdbcType=CHAR} and i.marketplaceid=#{param.marketplaceid,jdbcType=CHAR}
and
i.sku = #{sku,jdbcType=CHAR}

select t.id, ifnull(op.msku,t.sku) msku, t.asin, t.sku, t.marketplaceid, m.point_name, t.name, t.parentAsin from t_product_info t left join t_amazon_auth a on a.id=t.amazonAuthId left join t_product_in_opt op on t.id=op.pid left join t_marketplace m on m.marketplaceId=t.marketplaceid where a.shop_id=#{param.shopid,jdbcType=CHAR}
and t.amazonAuthId= #{param.amazonAuthId,jdbcType=CHAR}
and (t.marketplaceid = #{param.marketplaceid,jdbcType=CHAR})
and (a.groupid=#{param.groupid,jdbcType=CHAR})
and a.groupid in(
#{grop,jdbcType=BIGINT}
)
and (op.owner=#{param.myself,jdbcType=CHAR})
t.parentasin like #{param.search,jdbcType=CHAR}
and t.parentasin in (SELECT t1.parentAsin FROM t_product_info t1 WHERE t1.amazonAuthId=t.amazonAuthId and t1.asin like #{param.search,jdbcType=CHAR} AND t1.invalid=0 )
and t.parentasin in (select t1.parentAsin FROM t_product_info t1 WHERE t1.amazonAuthId=t.amazonAuthId and t1.sku like #{param.search,jdbcType=CHAR} AND t1.invalid=0)
and (t.sku like #{param.search,jdbcType=CHAR})
and (t.asin like #{param.search,jdbcType=CHAR})
and (ifnull(op.msku,t.sku) like #{param.search,jdbcType=CHAR})
and (t.name like #{param.search,jdbcType=CHAR})
and (op.disable = 0 or op.disable is null) and (t.invalid = 0) and (t.isparent = 0)
