mapper.select:
select startDate
from t_report_requestrecord
where sellerid= #{sellerid,jdbcType=CHAR}
	and reporttype= #{reporttype,jdbcType=CHAR}
	and startDate is not null

	and
	(

		marketplaceid = #{marketplaceid,jdbcType=CHAR}

	)

order by startDate asc
limit 0,1;

select lastupdate
from t_report_requestrecord
where sellerid= #{sellerid,jdbcType=CHAR} 
	and reporttype= #{reporttype,jdbcType=CHAR}
	and lastupdate>date_add(curdate(), interval -3 day)

    and startDate= #{starttime,jdbcType=CHAR}


	and
	(

		marketplaceid = #{marketplaceid,jdbcType=CHAR}

	)

and (report_processing_status is null or report_processing_status='DONE' 
or report_processing_status='SUBMITTED' 
or report_processing_status='success' 
or report_processing_status='treat' 
or  report_processing_status='IN_QUEUE' 
or  report_processing_status='IN_PROGRESS' )

	and report_processing_status!='DONE_NO_DATA'

order by lastupdate desc
limit 0,1;

select
from t_report_requestrecord r inner join ( select max(id) vid from t_report_requestrecord where isnewest=true and treatnumber <50 and isrun=0 and lastupdate>date_add(curdate(), interval -3 day)
and reportType=#{reporttype,jdbcType=CHAR}
and reportType!='GET_V2_SETTLEMENT_REPORT_DATA_FLAT_FILE_V2'
and marketPlaceId=#{marketplaceid,jdbcType=CHAR}
and (report_processing_status is null or report_processing_status='SUBMITTED' or report_processing_status='IN_QUEUE' or report_processing_status='IN_PROGRESS' ) group by sellerid,marketPlaceId ) v on r.id=v.vid order by availableDate asc,ifnull(treatnumber,0) asc

select
from t_report_requestrecord r inner join ( select max(id) vid from t_report_requestrecord where isnewest=true and ( treatnumber <10 and lastupdate>date_add(curdate(), interval -3 day) or (treatnumber <30 and reportType='GET_V2_SETTLEMENT_REPORT_DATA_FLAT_FILE_V2') )
and reportType=#{reporttype,jdbcType=CHAR}
and reportType!='GET_V2_SETTLEMENT_REPORT_DATA_FLAT_FILE_V2'
and marketPlaceId=#{marketplaceid,jdbcType=CHAR}
AND ((report_processing_status='DONE' and isrun=0) or (report_processing_status='treat' and isrun=1 and lastupdate<DATE_SUB(NOW(),INTERVAL 2 hour) and lastupdate>DATE_SUB(NOW(),INTERVAL 1 day)) ) group by sellerid,marketPlaceId ) v on r.id=v.vid order by availableDate asc,ifnull(treatnumber,0) asc

SELECT

from t_report_requestrecord
where sellerid=#{sellerid,jdbcType=CHAR} 
	and reportType=#{reporttype,jdbcType=CHAR}
	and lastupdate >=DATE_FORMAT(now(),'%Y-%m-%d')
order by lastupdate desc;

select
from t_report_requestrecord r inner join ( select max(id) vid from t_report_requestrecord where isnewest=true and reportid is not null and ifnull(treatnumber,0) <10 and isrun!=1 and lastupdate>curdate()
and reportType=#{reporttype,jdbcType=CHAR}
and reportType!='GET_V2_SETTLEMENT_REPORT_DATA_FLAT_FILE_V2'
and marketPlaceId=#{marketplaceid,jdbcType=CHAR}
group by sellerid,marketPlaceId ) v on r.id=v.vid order by availableDate asc,ifnull(treatnumber,0) asc

    SELECT t.cname,r.report_processing_status,r.lastupdate
	from t_amz_report_request_type t
	LEFT JOIN  t_report_requestrecord r ON t.code=r.reportType  AND r.lastupdate>DATE_ADD(NOW(),INTERVAL  -1 DAY) 
 	AND r.marketPlaceId=#{marketplaceid,jdbcType=CHAR}
 	and r.sellerid=#{sellerid,jdbcType=CHAR}
 	WHERE t.disabled=0 AND t.id NOT IN (17,22)
