mapper.select:
select
item.SellerSKU sku,
plan.marketplaceid,CONCAT('FBA-',mkp.`market`) warehouse,
SUM(item.Quantity) totalqty, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped else 0 end ) totalout, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived else 0 end ) totalrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived-item.QuantityShipped else 0 end ) lessrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped*m.price else 0 end ) worth, sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.QuantityShipped else 0 end ) needrec, SUM(case when shipment.`status` >=2 and shipment.`status` <=5 then item.Quantity-item.QuantityReceived else 0 end ) needout from t_erp_ship_inbounditem item left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_material m on m.sku=item.SellerSKU and plan.shopid=m.shopid and m.isDelete = 0 left join t_erp_ship_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId where plan.shopid=#{shopid,jdbcType=CHAR}
and plan.createdate >=#{fromDate,jdbcType=DATE} and plan.createdate <=#{endDate,jdbcType=DATE}
and shipment.shiped_date >=#{fromDate,jdbcType=DATE} and shipment.shiped_date <=#{endDate,jdbcType=DATE}
and w.parentid=#{warehouseid,jdbcType=CHAR}
and (m.sku like #{search,jdbcType=CHAR})
and (mkp.`market` like replace(#{search,jdbcType=CHAR},"FBA-","") )
group by item.sellersku
group by plan.marketplaceid

select SUM(item.Quantity) totalqty, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped else 0 end ) totalout, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived else 0 end ) totalrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived-item.QuantityShipped else 0 end ) lessrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped*m.price else 0 end ) worth, sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.QuantityShipped else 0 end ) needrec, SUM(case when shipment.`status` >=2 and shipment.`status` <=5 then item.Quantity-item.QuantityReceived else 0 end ) needout from t_erp_ship_inbounditem item left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_material m on m.sku=item.SellerSKU and plan.shopid=m.shopid and m.isDelete = 0 left join t_erp_ship_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId where plan.shopid=#{shopid,jdbcType=CHAR}
and plan.createdate >=#{fromDate,jdbcType=DATE} and plan.createdate <=#{endDate,jdbcType=DATE}
and shipment.shiped_date >=#{fromDate,jdbcType=DATE} and shipment.shiped_date <=#{endDate,jdbcType=DATE}
and w.parentid=#{warehouseid,jdbcType=CHAR}
and (m.sku like #{search,jdbcType=CHAR})
and (mkp.`market` like replace(#{search,jdbcType=CHAR},"FBA-","") )

SELECT * FROM ( select
de.company, max(com.`name`) logitics, de.channame channame,
case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end warehouse,
sum(ifnull(dd.weight,0)*item.Quantity) readweight, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped else 0 end ) totalout, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived else 0 end ) totalrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived-item.QuantityShipped else 0 end ) lessrec, sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped*m.price else 0 end ) worth, sum(case when shipment.`status` >=2 and shipment.`status`<=5 then item.QuantityShipped else 0 end ) needrec, sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.Quantity-item.QuantityReceived else 0 end ) needout from t_erp_ship_inbounditem item left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_material m on m.sku=item.SellerSKU and plan.shopid=m.shopid and m.isDelete = 0 left join t_Dimensions dd on dd.id=m.pkgDimensions left join t_erp_ship_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{shopid,jdbcType=CHAR} and shipment.status>=5 and com.`name` is not null
and plan.createdate >=#{fromDate,jdbcType=DATE} and plan.createdate <=#{endDate,jdbcType=DATE}
and shipment.shiped_date >=#{fromDate,jdbcType=DATE} and shipment.shiped_date <=#{endDate,jdbcType=DATE}
and w.parentid=#{warehouseid,jdbcType=CHAR}
and de.company =#{companyid,jdbcType=CHAR}
and trans.channel=#{channelid,jdbcType=CHAR}
and (com.`name` like #{search,jdbcType=CHAR} )
and (mkp.`market` like #{search,jdbcType=CHAR} or mkp.region like #{search,jdbcType=CHAR} )
group by de.company,de.channame
group by case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end
) v left join ( select
de.company, max(com.`name`) logitics, de.channame channame,
case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end warehouse,
sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg, sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm, sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee, sum(trans.otherfee) totalotherfee, count(shipment.shipmentid) shipmentnum,sum(shipment.boxnum) totalbox, ROUND(avg(ifnull(datediff(shipment.start_receive_date, shipment.shiped_date),0)),2) avgtime, count(if(shipment.status='-1',true,null )) problem FROM t_erp_ship_inboundshipment shipment left join t_erp_ship_inboundplan plan on plan.id=shipment.inboundplanid left join t_marketplace mkp on mkp.marketplaceid=plan.marketplaceid left join t_erp_warehouse w on plan.warehouseid=w.id left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.ShipmentId left join t_erp_ship_transdetail de on de.id=trans.channel left join t_erp_ship_transcompany com on com.id=de.company where plan.shopid=#{shopid,jdbcType=CHAR} and (shipment.status>=5 or shipment.start_receive_date is not null) and com.`name` is not null
and plan.createdate >=#{fromDate,jdbcType=DATE} and plan.createdate <=#{endDate,jdbcType=DATE}
and shipment.shiped_date >=#{fromDate,jdbcType=DATE} and shipment.shiped_date <=#{endDate,jdbcType=DATE}
and w.parentid=#{warehouseid,jdbcType=CHAR}
and de.company =#{companyid,jdbcType=CHAR}
and trans.channel=#{channelid,jdbcType=CHAR}
and (com.`name` like #{search,jdbcType=CHAR} )
and (mkp.`market` like #{search,jdbcType=CHAR} or mkp.region like #{search,jdbcType=CHAR} )
group by de.company, de.channame
group by case when mkp.region='EU' then 'FBA-EU' else CONCAT('FBA-',mkp.`market`) end
) n on
v.company=n.company and v.channame=n.channame
v.warehouse =n.warehouse

SELECT * FROM (
	select sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped else 0 end ) totalout,
		sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived else 0 end ) totalrec,
		sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityReceived-item.QuantityShipped else 0 end ) lessrec,
		sum(case when shipment.`status`=5 or shipment.`status`=6 then item.QuantityShipped*m.price else 0 end ) worth,
		sum(case when shipment.`status` >=2 and shipment.`status`<=5 then item.QuantityShipped else 0 end ) needrec,
		sum(case when shipment.`status` >=2 and shipment.`status` <=5 then item.Quantity-item.QuantityReceived else 0 end ) needout,
		sum(ifnull(dd.weight,0)*item.Quantity) readweight
	from t_erp_ship_inbounditem item
	left join t_erp_ship_inboundplan plan on plan.id=item.inboundplanid
	left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid
	left join t_erp_warehouse w on plan.warehouseid=w.id
	left join t_erp_material m on m.sku=item.SellerSKU and plan.shopid=m.shopid and m.isDelete = 0
	left join t_Dimensions dd on dd.id=m.pkgDimensions
	left join t_erp_ship_inboundshipment shipment on shipment.ShipmentId=item.ShipmentId
	left join t_erp_ship_inboundtrans trans on trans.shipmentid=item.ShipmentId
	left join t_erp_ship_transdetail de on de.id=trans.channel
	left join t_erp_ship_transcompany com on com.id=de.company
	where plan.shopid=#{shopid,jdbcType=CHAR} 
		and shipment.status>=5 
		and com.`name` is not null

		and  plan.createdate >=#{fromDate,jdbcType=DATE}
		and  plan.createdate <=#{endDate,jdbcType=DATE}


		and  shipment.shiped_date >=#{fromDate,jdbcType=DATE}
		and  shipment.shiped_date <=#{endDate,jdbcType=DATE}


		and w.parentid=#{warehouseid,jdbcType=CHAR}


		and de.company =#{companyid,jdbcType=CHAR}


		and trans.channel=#{channelid,jdbcType=CHAR}




				and (com.`name` like #{search,jdbcType=CHAR} )


				and (mkp.`market` like #{search,jdbcType=CHAR} or  mkp.`region` like #{search,jdbcType=CHAR})



) v
join (
	select sum(case when trans.wunit='kg' then trans.transweight else 0 end) transweight_kg,
		sum(case when trans.wunit='cbm' then trans.transweight else 0 end) transweight_cbm,
		sum(ifnull(trans.singleprice,0)*ifnull(trans.transweight,0)+ifnull(trans.otherfee,0)) shipfee,
		sum(trans.otherfee) totalotherfee,sum(shipment.boxnum) totalbox,
		count(shipment.shipmentid) shipmentnum,
		ROUND(avg(ifnull(datediff(shipment.start_receive_date, shipment.shiped_date),0)),2) avgtime,
		count(if(shipment.status='-1',true,null )) problem
	FROM t_erp_ship_inboundshipment shipment
	left join t_erp_ship_inboundplan plan on plan.id=shipment.inboundplanid
	left join t_marketplace mkp on mkp.marketplaceId=plan.marketplaceid
	left join t_erp_warehouse w on plan.warehouseid=w.id
	left join t_erp_ship_inboundtrans trans on trans.shipmentid=shipment.ShipmentId
	left join t_erp_ship_transdetail de on de.id=trans.channel
	left join t_erp_ship_transcompany com on com.id=de.company
	where plan.shopid=#{shopid,jdbcType=CHAR} 
		and shipment.status>=5 
		and com.`name` is not null

		and  plan.createdate >=#{fromDate,jdbcType=DATE}
		and  plan.createdate <=#{endDate,jdbcType=DATE}


		and  shipment.shiped_date >=#{fromDate,jdbcType=DATE}
		and  shipment.shiped_date <=#{endDate,jdbcType=DATE}


		and w.parentid=#{warehouseid,jdbcType=CHAR}


		and de.company =#{companyid,jdbcType=CHAR}


		and trans.channel=#{channelid,jdbcType=CHAR}




				and (com.`name` like #{search,jdbcType=CHAR} )


				and (mkp.`market` like #{search,jdbcType=CHAR} or  mkp.`region` like #{search,jdbcType=CHAR})



) n
