mapper.select:
  	select t.*,ui.name name from t_erp_assembly_from_instock t 
	left join t_userinfo ui on ui.id=t.operator
	where t.formid =#{formid,jdbcType=CHAR};

SELECT count(0) from  t_erp_assembly_from_instock s
						  left join t_erp_assembly_form a on a.id=s.formid
where s.shipmentid=#{shipmentid,jdbcType=CHAR} and a.shopid=#{shopid,jdbcType=CHAR}

SELECT s.* from  t_erp_assembly_from_instock s
						  left join t_erp_assembly_form a on a.id=s.formid
where s.shipmentid=#{shipmentid,jdbcType=CHAR} and a.shopid=#{shopid,jdbcType=CHAR}

SELECT max(r.opttime) asstimek, GROUP_CONCAT(distinct m.id) mainmid, GROUP_CONCAT(distinct m.sku) mainsku, max(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then r.opttime else null end ) asstime, sum(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then r.amount else 0 end ) assqty, sum(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then r.amount*e.subnumber else 0 end) subqty, max(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then e.subnumber else 0 end) subnumber, GROUP_CONCAT(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then r.shipmentid else null end ) shipmentids, GROUP_CONCAT(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then concat(m.sku,":",r.amount) else null end ) skuassqty, GROUP_CONCAT(case when r.opttime>=#{inwaretime,jdbcType=CHAR} then concat(m.sku,":",e.subnumber) else null end ) skusubnumber, max(m.name) mainname, max(p.location) mainimage FROM t_erp_assembly_form_entry e left join t_erp_assembly_form f on e.formid=f.id left join t_erp_material m on m.id=f.mainmid left join t_picture p on p.id=m.image LEFT JOIN t_erp_assembly_from_instock r ON r.formid=f.id WHERE e.materialid= #{materialid,jdbcType=CHAR} and f.shopid= #{shopid,jdbcType=CHAR}
and f.warehouseid= #{warehouseid,jdbcType=CHAR}
and r.opttime>=DATE_SUB(#{inwaretime,jdbcType=CHAR},INTERVAL 30 DAY) AND f.ftype='ass' AND f.auditstatus=3
