mapper.select:
SELECT f.*,m.name,f.check_inv,IFNULL(p.location,p.url) image,f.mainmid materialid,f.amount QuantityShipped,f.amount quantity, ifnull( (select sum(iv.quantity) from t_erp_inventory iv where iv.materialid=m.id and iv.warehouseid=f.warehouseid and iv.`status`='fulfillable' ) ,0) invquantity,
ifnull( ( select sum(iv2.quantity) from t_erp_inventory iv2 where iv2.materialid=m.id and iv2.warehouseid=f.warehouseid and iv2.status like 'outbound_%' ),0) outbound
FROM t_erp_assembly_form f LEFT JOIN t_erp_material m ON m.id=f.mainmid and m.isDelete = 0 LEFT JOIN t_picture p ON p.id=m.image WHERE f.id IN (
#{formid,jdbcType=CHAR}
)

SELECT SUM((f.amount-f.amount_handle)*e.subnumber) as needprocess
from t_erp_assembly_form f
LEFT JOIN t_erp_assembly_form_entry e ON e.formid=f.id
WHERE e.materialid=#{materialid,jdbcType=CHAR} AND f.shopid= #{shopid,jdbcType=CHAR}
and f.warehouseid=#{warehouseid,jdbcType=CHAR}
and f.auditstatus=2
