mapper.select:
select ta.id,ta.number number,ta.ftype,m.sku sku, ta.amount,u.name creator, ta.auditstatus auditstatus, ta.remark remark,ta.opttime opttime , v.needpo purchaseneed, v.hasqty,v.needin ,ta.createdate, p.location image,m.name,ta.amount_handle,v2.shipmentids,w.name wname,w.id warehouseid from t_erp_assembly_form ta left join t_erp_inventory i on ta.mainmid=i.materialid and i.warehouseid=ta.warehouseid and i.shopid=ta.shopid and i.`status` ='fulfillable' left join t_erp_warehouse w on w.id=ta.warehouseid LEFT JOIN t_userinfo u on ta.creator = u.id LEFT join t_erp_material m on ta.mainmid = m.id left join t_picture p on p.id=m.image left join ( select entry.formid,
max(case when m2.sku like #{param.search,jdbcType=CHAR} then 1 else 0 end) subhave,
max( case when form.ftype='ass' then case when ifnull(i2.quantity,0) < (entry.amount/form.amount) then 1 else 0 end else case when i1.quantity>=form.amount then 0 else 1 end end ) needpo , max( case when form.ftype='ass' then case when ifnull(i3.quantity,0)+ifnull(i2.quantity,0) < (entry.amount/form.amount*(form.amount-ifnull(form.amount_handle,0))) then (entry.amount/form.amount*(form.amount-ifnull(form.amount_handle,0)))-(ifnull(i3.quantity,0)+ifnull(i2.quantity,0)) else 0 end else 0 end ) needin , min( case when form.ftype='ass' then ifnull(i2.quantity,0)/(entry.amount/(form.amount)) else case when i1.quantity>=form.amount then form.amount else 0 end end ) hasqty from t_erp_assembly_form_entry entry
left join t_erp_material m2 on m2.id=entry.materialid
left join t_erp_assembly_form form on entry.formid=form.id left join t_erp_inventory i1 on form.mainmid=i1.materialid and i1.warehouseid=form.warehouseid and i1.shopid=#{param.shopid,jdbcType=CHAR} and i1.`status` ='fulfillable' left join t_erp_inventory i2 on entry.materialid=i2.materialid and i2.warehouseid=entry.warehouseid and i2.`status` ='fulfillable' and i2.shopid=#{param.shopid,jdbcType=CHAR} left join t_erp_inventory i3 on entry.materialid=i3.materialid and i3.warehouseid=entry.warehouseid and i3.`status` = 'inbound_purchase' and i3.shopid=#{param.shopid,jdbcType=CHAR} where form.shopid=#{param.shopid,jdbcType=CHAR}
and form.createdate >= #{param.fromDate,jdbcType=TIMESTAMP} and form.createdate <= #{param.toDate,jdbcType=TIMESTAMP}
and (form.auditstatus=2 or form.auditstatus=1 )
and (form.auditstatus= #{param.auditstatus,jdbcType=CHAR} )
group by entry.formid ) v on v.formid=ta.id left join (select formid,group_concat(shipmentid) shipmentids,count(0) assnum from t_erp_assembly_from_instock where 1=1
and shipmentid=#{param.search,jdbcType=CHAR}
group by formid) v2 on v2.formid=ta.id where ta.shopid=#{param.shopid,jdbcType=CHAR}
and (m.sku like #{param.search,jdbcType=CHAR} or v.subhave=1)
and ta.number like #{param.search,jdbcType=CHAR}
and assnum>0
and (ta.auditstatus=2 or ta.auditstatus=1 )
and (ta.auditstatus= #{param.auditstatus,jdbcType=CHAR} )
and m.owner=#{param.owner,jdbcType=CHAR}
and v.needpo=0
and v.needpo=1
and (ta.ftype= #{param.ftype,jdbcType=CHAR} )
and m.supplier= #{param.supplier,jdbcType=CHAR}
and ta.createdate >= #{param.fromDate,jdbcType=TIMESTAMP} and ta.createdate <= #{param.toDate,jdbcType=TIMESTAMP}
and (w.id= #{param.warehouseid,jdbcType=CHAR} )
ORDER BY ta.number

SELECT amount,date_format(createdate,'%Y-%m-%d') createdate
from t_erp_assembly_form t
where t.shopid=#{shopid,jdbcType=CHAR}
	and t.ftype='ass'
	and t.warehouseid=#{warehouseid,jdbcType=CHAR}
	and t.auditstatus = 3
order by createdate desc limit 3;

select
date_format(instock.opttime,'%Y-%m-%d') createdate,
date_format(subdate(instock.opttime,date_format(instock.opttime,'%w')-7),'%Y-%m-%d') createdate,
concat( year(instock.opttime) ,'-' ,month(instock.opttime)) createdate,
sum(instock.amount) amount from t_erp_assembly_form form left join t_erp_assembly_from_instock instock on instock.formid=form.id left join t_erp_material m on m.id=form.mainmid left join t_erp_warehouse w on form.warehouseid = w.id where form.ftype='ass' and form.auditstatus>=3
and form.shopid =#{shopid,jdbcType=CHAR}
and w.parentid =#{warehouseid,jdbcType=CHAR}
and m.sku like #{sku,jdbcType=CHAR}
and instock.opttime >=#{begindate,jdbcType=CHAR}
and instock.opttime <=#{enddate,jdbcType=CHAR}
group by date_format(instock.opttime,'%Y-%m-%d')
group by date_format(subdate(instock.opttime,date_format(instock.opttime,'%w')-7),'%Y-%m-%d')
group by concat( year(instock.opttime) ,'-' ,month(instock.opttime))

select *
from t_erp_assembly_form a
where a.warehouseid = #{warehouseid,jdbcType=CHAR} 
    and a.mainmid=#{materialid,jdbcType=CHAR} 
    and a.shopid=#{shopid,jdbcType=CHAR} 
    and a.ftype='ass'
	and (auditstatus=1 OR auditstatus=2) AND (amount>ifnull(amount_handle,0))
	order by a.createdate asc

SELECT f.mainmid,f.number,f.amount,f.amount_handle,f.createdate,f.auditstatus FROM ( select a.mainmid,max(createdate) createdate from t_erp_assembly_form a where a.mainmid IN (
#{id,jdbcType=CHAR}
) AND a.auditstatus IN (1,2,3,4) AND a.createdate>DATE_SUB(CURRENT_date() ,INTERVAL 1 YEAR) AND a.ftype='ass' GROUP BY a.mainmid ) v LEFT JOIN t_erp_assembly_form f ON f.mainmid=v.mainmid AND f.createdate=v.createdate
