mapper.select:
select distinct a.submid,a.mainmid,a.subnumber, IFNULL(mp.location,mp.url) mimage, IFNULL(sp.location,sp.url) simage, mm.name mname, mm.sku msku, sm.name sname, sm.sku ssku , ifnull(sinv.quantity,0) squantity, ifnull(minv.quantity,0) mquantity from t_erp_assembly a left join t_erp_material mm on mm.id=a.mainmid left join t_erp_material sm on sm.id=a.submid left join t_picture mp on mp.id=mm.image left join t_picture sp on sp.id=mm.image left join t_erp_inventory minv on minv.materialid=a.mainmid and minv.warehouseid=#{warehouseid,jdbcType=CHAR} and minv.status='fulfillable' left join t_erp_inventory sinv on sinv.materialid=a.submid and sinv.warehouseid=#{warehouseid,jdbcType=CHAR} and sinv.status='fulfillable' where mm.sku in
#{msku,jdbcType=CHAR}
and mm.isdelete=0 and mm.shopid= #{shopid,jdbcType=CHAR}

select * 
from t_erp_assembly t 
where t.mainmid=#{mainmid,jdbcType=CHAR} 
	and t.submid is not null;
