mapper.select:
select iv.id,iv.shopid,iv.warehouseid,iv.materialid,iv.quantity,iv.status,iv.operator,iv.opttime
from t_erp_inventory iv
inner join t_erp_warehouse w on w.id=iv.warehouseid and w.disabled=0
where iv.warehouseid=#{warehouse,jdbcType=CHAR} 
	and iv.materialid=#{material,jdbcType=CHAR}
	and iv.shopid=#{shopid,jdbcType=CHAR}
	and iv.status=#{status,jdbcType=CHAR}
for update;

select iv.materialid id,iv.shopid,max(iv.warehouseid) warehouseid,iv.materialid,sum(iv.quantity) quantity,max(iv.status) status,
max(iv.operator) operator,max(iv.opttime) opttime
from t_erp_inventory iv
inner join t_erp_warehouse w on w.id=iv.warehouseid and w.disabled=0
where w.parentid=#{warehouse,jdbcType=CHAR} 
	and iv.materialid=#{material,jdbcType=CHAR}
	and iv.shopid=#{shopid,jdbcType=CHAR}
	and iv.status=#{status,jdbcType=CHAR}
	and w.ftype='self_usable'
	group by iv.materialid

select inv.materialid ,
	sum( case when substring_index(status,'_',1) ='inbound' then quantity else 0 end ) inbound,
	sum( case when substring_index(status,'_',1) ='outbound' then quantity else 0 end ) outbound,
	sum( case when substring_index(status,'_',1) ='fulfillable' then quantity else 0 end ) fulfillable
from t_erp_inventory inv
where inv.shopid=#{shopid,jdbcType=CHAR}

	and inv.warehouseid=#{warehouseid,jdbcType=CHAR} 

	and inv.materialid=#{materialid,jdbcType=CHAR}
group by inv.materialid

select uuid_short() id, i.warehouseid warehouseid, i.name warehouse, i.name warehousename, m.sku, m.name name, m.id materialid, ifnull(i.inbound,0) inbound, ifnull(i.fulfillable,0) fulfillable, ifnull(i.outbound,0) outbound, m.price, ifnull(p.location,p.url) image, m.issfg from t_erp_material m left join ( select iv.shopid,iv.warehouseid,iv.materialid,max(w.name) name, sum( case when substring_index(iv.status,'_',1)='inbound' then iv.quantity else 0 end ) inbound, sum( case when substring_index(iv.status,'_',1)='outbound' then iv.quantity else 0 end ) outbound, sum( case when substring_index(iv.status,'_',1)='fulfillable' then iv.quantity else 0 end ) fulfillable from t_erp_inventory iv left join t_erp_warehouse w on w.id=iv.warehouseid where iv.shopid=#{param.shopid,jdbcType=CHAR} and w.disabled=0
and iv.warehouseid=#{param.warehouseid,jdbcType=CHAR}
and w.addressid=#{param.addressid,jdbcType=CHAR}
group by iv.shopid,iv.warehouseid,iv.materialid ) i on i.materialid=m.id
left join t_erp_assembly ass on ass.mainmid =m.id left join t_erp_material m2 on m2.id=ass.submid
left join t_erp_material_consumable cons on cons.materialid=m.id left join t_erp_material m3 on m3.id=cons.submaterialid
left join t_picture p on p.id=m.image where m.shopid=#{param.shopid,jdbcType=CHAR} and m.isDelete=false
and m.owner=#{param.myself,jdbcType=CHAR}
and m.id=#{param.skuid,jdbcType=CHAR}
and m2.sku=#{param.itemsku,jdbcType=CHAR}
and m3.sku=#{param.conssku,jdbcType=CHAR}
and m.categoryid=#{param.category,jdbcType=CHAR}
and m.issfg=#{param.ftypes,jdbcType=CHAR}
and m.sku like #{param.search,jdbcType=CHAR}
and (i.fulfillable>0 or i.outbound>0 or i.inbound>0)
and (i.fulfillable>0 or i.outbound>0 or i.inbound>0)
ORDER BY i.name

select.if:
select max(w.id) id, max(w.parentid) parentid, w.name warehouse,max(w.ftype) ftype,max(m.sku) sku,max(m.id) skuid, max(ifnull(s.stockingCycle,wp.stocking_cycle)) stockingCycle, max(ifnull(s.min_cycle,wp.min_cycle)) mincycle,0 firstlegcharges, sum(i.inbound) inbound,sum(i.fulfillable) fulfillable,sum(i.outbound) outbound from t_erp_warehouse w left join t_erp_warehouse wp on w.parentid=wp.id left join t_erp_stockcycle s on w.parentid=s.warehouseid and s.materialid=#{id,jdbcType=CHAR} cross join t_erp_material m left join ( select shopid,warehouseid,materialid , sum( case when substring_index(status,'_',1)='inbound' then quantity else 0 end ) inbound, sum( case when substring_index(status,'_',1)='outbound' then quantity else 0 end ) outbound, sum( case when substring_index(status,'_',1)='fulfillable' then quantity else 0 end ) fulfillable from t_erp_inventory where shopid=#{shopid,jdbcType=CHAR} group by shopid,warehouseid,materialid ) i on i.materialid=m.id and w.id=i.warehouseid where wp.ftype ='self'
and w.ftype=#{ftype,jdbcType=CHAR}
and w.disabled=0 and m.id=#{id,jdbcType=CHAR} and w.shopid=#{shopid,jdbcType=CHAR} GROUP BY w.name ORDER BY w.name

mapper.select:
select '' sku,sum(i.inbound) inbound,sum(i.fulfillable) fulfillable,sum(i.outbound) outbound
from (
	select shopid,warehouseid,materialid ,
		sum( case when substring_index(status,'_',1)='inbound' then quantity else 0 end ) inbound,
		sum( case when substring_index(status,'_',1)='outbound' then quantity else 0 end ) outbound,
		sum( case when substring_index(status,'_',1)='fulfillable' then quantity else 0 end ) fulfillable
	from t_erp_inventory
	where shopid=#{param.shopid,jdbcType=CHAR}

	    and  warehouseid=#{param.warehouseid,jdbcType=CHAR}

	group by shopid,warehouseid,materialid
) i  
left join t_erp_material m on i.materialid=m.id

	left join t_erp_assembly ass on ass.mainmid =m.id
	left join t_erp_material m2 on m2.id=ass.submid


	left join t_erp_material_consumable cons on cons.materialid=m.id
	left join t_erp_material m3 on m3.id=cons.submaterialid

where  m.shopid=#{param.shopid,jdbcType=CHAR}  and m.isDelete=false

	and m.owner=#{param.myself,jdbcType=CHAR} 


	and m.id=#{param.skuid,jdbcType=CHAR}


	and m2.sku=#{param.itemsku,jdbcType=CHAR}


	and m3.sku=#{param.conssku,jdbcType=CHAR}


	and m.categoryid=#{param.category,jdbcType=CHAR}


	and m.issfg=#{param.ftypes,jdbcType=CHAR}


	and m.sku like #{param.search,jdbcType=CHAR}

select w.id, w.name warehouse, ifnull(i.inbound,0) inbound,ifnull(i.fulfillable,0) fulfillable, ifnull(i.outbound,0) outbound from t_erp_warehouse w left join t_erp_warehouse wp on w.parentid=wp.id left join ( select shopid,warehouseid,materialid , sum( case when substring_index(status,'_',1)='inbound' then quantity else 0 end ) inbound, sum( case when substring_index(status,'_',1)='outbound' then quantity else 0 end ) outbound, sum( case when substring_index(status,'_',1)='fulfillable' then quantity else 0 end ) fulfillable from t_erp_inventory where shopid=#{shopid,jdbcType=CHAR}
and materialid=#{skuid,jdbcType=CHAR}
group by shopid,warehouseid ) i on w.id=i.warehouseid where wp.ftype ='self' and w.shopid=#{shopid,jdbcType=CHAR} and w.parentid=#{whparentid,jdbcType=CHAR}

select t.*,w.name warehouse,m.sku skuid,m.name mname,m.color mcolor,IFNULL(pic.location,pic.url) image,t.quantity amount from t_erp_inventory t left join t_erp_warehouse w on w.id=t.warehouseid left join t_erp_material m on m.id=t.materialid left join t_picture pic on m.image=pic.id where t.materialid=#{materialid,jdbcType=CHAR}
and t.warehouseid=#{warehouseid,jdbcType=CHAR}
and t.shopid=#{shopid,jdbcType=CHAR} and t.`status`='fulfillable' order by t.quantity desc limit 1

select ( select max(name) from t_erp_formtype where id = substring(status,position('_' in status)+1 ,length(status)) ) as name, status, quantity , warehouseid, materialid from t_erp_inventory where shopid=#{shopid,jdbcType=CHAR} and warehouseid=#{warehouseid,jdbcType=CHAR}
and materialid=#{materialid,jdbcType=CHAR}
and status like 'inbound_%'

select ( select max(name) from t_erp_formtype where id = substring(status,position('_' in status)+1 ,length(status)) ) as name, status, quantity , warehouseid, materialid from t_erp_inventory where shopid=#{shopid,jdbcType=CHAR} and warehouseid=#{warehouseid,jdbcType=CHAR}
and materialid=#{materialid,jdbcType=CHAR}
and status like 'outbound_%'

SELECT t.shopid,t.materialid,t.warehouseid,m.sku,max(t.opttime) createdate,max(case when t.`status`='fulfillable' then t.opttime else null end) opttime, sum(case when t.`status`='outbound' and (t.operate='readyout' or t.operate='changeadd') then t.quantity ELSE 0 END) outboundadd, sum(case when t.`status`='outbound' AND (t.operate='out' OR t.operate='cancel' or t.operate='changesub') then t.quantity ELSE 0 END) outboundsub, sum(case when t.`status`='fulfillable' AND (t.operate='out' OR t.operate='readyout' or t.operate='changeadd') then t.quantity ELSE 0 END) fulfillablesub, sum(case when t.`status`='fulfillable' AND (t.operate='in' OR t.operate='cancel' or t.operate='changesub') then t.quantity ELSE 0 END) fulfillableadd, sum(case when t.`status`='inbound' and (t.operate='readyin' or t.operate='in') then t.quantity ELSE 0 END) inboundadd, sum(case when t.`status`='inbound' and (t.operate='out' or t.operate='stop' or t.operate='cancel') then t.quantity ELSE 0 END) inboundsub, min(case when t.`status`='fulfillable' and t.operate !='readyout' then t.operate ELSE CONCAT('z',t.operate) END ) operate, min(case when t.operate !='changeadd' and t.operate !='changesub' then t.operate ELSE CONCAT('z',t.operate) END) operate2, t.number, w.name wname, p.name oftype, wt.name wftype, u.name operator ,t.formtype, max(case when t.`status`='fulfillable' then t.invqty else null end) invqty from t_erp_inventory_record t left join t_erp_material m on m.id=t.materialid left join t_erp_warehouse w on w.id=t.warehouseid left join t_erp_formtype p on p.id=t.formtype left join t_erp_warehouse_type wt on wt.id=w.ftype left join t_userinfo u on u.id=t.operator where t.shopid=#{shopid,jdbcType=CHAR}
and m.sku=#{search,jdbcType=CHAR}
and m.id=#{skuid,jdbcType=CHAR }
and w.parentid=#{wparentid,jdbcType=CHAR}
and t.opttime >= #{fromDate,jdbcType=TIMESTAMP} and t.opttime <= #{toDate,jdbcType=TIMESTAMP}
and p.id=#{ftypeid,jdbcType=CHAR}
and t.number=#{formnum,jdbcType=CHAR}
GROUP BY t.materialid,t.NUMBER,t.warehouseid

select w.name,v1.qtysum startqty,v2.qtysum endqty,v3.salesum salesum,
	case when v1.qtysum+v2.qtysum>0 then v3.salesum/((v1.qtysum+v2.qtysum)/2) else 0 end wrate,
	case when v1.qtysum+v2.qtysum>0 then 
	(datediff(#{fromDate,jdbcType=DATE} ,#{endDate,jdbcType=DATE} )+1)/(v3.salesum/((v1.qtysum+v2.qtysum)/2)) else 0 end wday
from t_erp_warehouse w 
left join (
	select his.shopid,wh.parentid warehouseid, modifyday,
		sum(case when `status`='fulfillable' or LOCATE('outbound',`status`)>0 then his.quantity else 0 end ) qtysum
	from t_erp_inventory_his his
	left join t_erp_warehouse wh on his.warehouseid=wh.id
	where his.modifyday<=#{fromDate,jdbcType=DATE}
		and his.shopid=#{shopid,jdbcType=CHAR}

		and materialid=#{materialid,jdbcType=CHAR}

	group by his.shopid,wh.parentid
	having modifyday=max(modifyday) 
) v1 on v1.shopid=w.shopid and v1.warehouseid=w.id
left join (
	select his.shopid,wh.parentid warehouseid, modifyday,
		sum(case when `status`='fulfillable' or LOCATE('outbound',`status`)>0 then his.quantity else 0 end ) qtysum
	from t_erp_inventory_his his
	left join t_erp_warehouse wh on his.warehouseid=wh.id
	where his.modifyday<=#{endDate,jdbcType=DATE}
		and his.shopid=#{shopid,jdbcType=CHAR}

		and materialid=#{materialid,jdbcType=CHAR}

	group by his.shopid,wh.parentid
	having modifyday=max(modifyday) 
)v2 on w.shopid=v2.shopid and w.id=v2.warehouseid
left join (
	select re.shopid,wh.parentid warehouseid ,sum(quantity) salesum 
	from t_erp_inventory_record re
	left join t_erp_warehouse wh on re.warehouseid=wh.id
	where re.opttime>=#{fromDate,jdbcType=DATE} 
		and re.opttime<=#{endDate,jdbcType=DATE}
		and re.operate='out' and re.shopid=#{shopid,jdbcType=CHAR}

		and re.materialid=#{materialid,jdbcType=CHAR}

	group by re.shopid,wh.parentid 
) v3 on v3.shopid=w.shopid and v3.warehouseid=w.id
where w.disabled =false 
	and w.ftype = 'self' 
	and w.shopid=#{shopid,jdbcType=CHAR}

	union
	select w.name,v1.startqty startqty,v1.endqty endqty,v3.salesum,
		case when v1.startqty+v1.endqty>0 then v3.salesum/((v1.startqty+v1.endqty)/2) else 0 end wrate,
		case when v1.startqty+v1.endqty>0 then
		(datediff(#{fromDate,jdbcType=DATE},#{endDate,jdbcType=DATE})+1)/(v3.salesum/((v1.startqty+v1.endqty)/2))
		else 0 end wday
	from t_erp_warehouse_fba w
	left join (
		select marketplaceid,
		sum(case when date(byday) =#{fromDate,jdbcType=DATE} then ir.afn_fulfillable_quantity else 0 end ) startqty,
		sum(case when date(byday) =#{endDate,jdbcType=TIMESTAMP} then ir.afn_fulfillable_quantity else 0 end ) endqty
		from t_inventory_report ir
		left join t_amazon_auth auth on auth.id=ir.amazonAuthId
		where date(byday) =#{fromDate,jdbcType=DATE} or date(byday) =#{endDate,jdbcType=DATE}
			and auth.shop_id=#{shopid,jdbcType=CHAR}
			and sku=#{sku,jdbcType=CHAR}
		group by marketplaceid
	) v1 on v1.marketplaceid=w.marketplaceid
	left join (
		select marketplaceid,sum(quantity) salesum 
		from t_orders_summary s
		left join t_amazon_auth auth on auth.id=s.amazonAuthId
		where auth.shop_id=#{shopid,jdbcType=CHAR}
			and purchase_date>=#{fromDate,jdbcType=DATE} 
			and purchase_date<=#{endDate,jdbcType=DATE}
			and sku=#{sku,jdbcType=CHAR}
		group by marketplaceid
	) v3 on v3.marketplaceid=w.marketplaceid
	where w.shopid=#{shopid,jdbcType=CHAR}

select his.shopid,w.parentid,sum(his.quantity) qty

	,his.materialid

from (select shopid,warehouseid,materialid,`status`,max(modifyday) modifyday, max(opttime) opttime
from t_erp_inventory_his
where modifyday <= #{byday,jdbcType=DATE}
	and (`status`='fulfillable' or `status` like 'outbound%')

	and materialid=#{materialid,jdbcType=CHAR}


	and warehouseid=#{warehouseid,jdbcType=CHAR}

	and shopid=#{shopid,jdbcType=CHAR}
group by shopid,warehouseid,materialid,status
) v
left join t_erp_inventory_his his on his.shopid=v.shopid and his.warehouseid=v.warehouseid
	and his.materialid=v.materialid and his.`status`=v.`status` and his.modifyday=v.modifyday
	and his.opttime=v.opttime
left join t_erp_warehouse w on w.id=his.warehouseid

	where w.parentid=#{parentid,jdbcType=CHAR}

group by his.shopid,w.parentid

	,his.materialid

select re.shopid,wh.parentid,sum(quantity) salesum

	,re.materialid

from t_erp_inventory_record re
left join t_erp_warehouse wh on re.warehouseid=wh.id
where re.opttime>=#{fromDate,jdbcType=DATE} 

 and re.opttime<=#{endDate,jdbcType=DATE}

	and re.operate='out' and (re.`status`='fulfillable' or re.`status`='outbound') and re.shopid=#{shopid,jdbcType=CHAR}

	and re.materialid=#{materialid,jdbcType=CHAR}


	and re.shopid=#{shopid,jdbcType=CHAR}


	and re.warehouseid=#{warehouseid,jdbcType=CHAR}


	and wh.parentid =#{parentid,jdbcType=CHAR}

group by re.shopid,wh.parentid

	,re.materialid

select * from ( select m.id id,m.sku,max(m.price) price,m.remark, sum(case when (iv.`status`='fulfillable' or POSITION('outbound' IN iv.`status`) ) then iv.quantity else 0 end) quantity, sum(case when (iv.`status`='fulfillable') then iv.quantity else 0 end) fulfillable, sum(case when POSITION('inbound' IN iv.`status`) then iv.quantity else '0' end) inbound, sum(case when (iv.`status`='fulfillable' or POSITION('outbound' IN iv.`status`) ) then iv.quantity else 0 end )*max(m.price) totalprice, sum(case when (iv.`status`='fulfillable' or POSITION('outbound' IN iv.`status`) ) then iv.quantity else 0 end )*max(m.other_cost) otherFeer from t_erp_inventory iv left join t_erp_material m on m.id=iv.materialid and iv.shopid=m.shopid where iv.quantity >0 and iv.warehouseid=#{warehouseid,jdbcType=CHAR} and iv.shopid=#{shopid,jdbcType=CHAR} and m.isDelete = 0
and m.sku like #{sku,jdbcType=CHAR}
group by m.id ) vold left join ( select m.id mtid, sum(case when(entry.inwhstatus=0) then case when(ifnull(entry.orderprice,0)-(ifnull(entry.totalin,0))*ifnull(entry.itemprice,0)>=0) then ifnull(entry.orderprice,0)-(ifnull(entry.totalin,0))*ifnull(entry.itemprice,0) else 0 end else 0 end) inpasscost, sum(case when(entry.paystatus=0) then case when(((entry.totalin)*entry.itemprice-entry.totalpay)>=0) then (entry.totalin)*entry.itemprice-entry.totalpay else 0 end else 0 end) inpasspay from t_erp_purchase_form_entry entry left join t_erp_material m on entry.materialid=m.id and entry.auditstatus=2 left join t_erp_purchase_form ff on ff.id=entry.formid where ff.warehouseid=#{warehouseid,jdbcType=CHAR} and m.shopid=#{shopid,jdbcType=CHAR}
and m.sku like #{sku,jdbcType=CHAR}
group by m.id ) vnew on vold.id=vnew.mtid

select fulfillable,inbound,totalprice,inpasscost,inpasspay,otherFeer
from (
	select iv.warehouseid,
		sum(case when (iv.`status`='fulfillable' or POSITION('outbound' IN iv.`status`)) then iv.quantity else 0 end) fulfillable,
		sum(case when POSITION('inbound' IN iv.`status`) then iv.quantity else 0 end) inbound,
		sum(case when (iv.`status`='fulfillable' or POSITION('outbound' IN iv.`status`)) then iv.quantity else 0 end*m.price) totalprice,
		sum(case when (iv.`status`='fulfillable' or POSITION('outbound' IN iv.`status`)) then iv.quantity else 0 end*m.other_cost) otherFeer
	from t_erp_inventory iv
	left join t_erp_material m on m.id=iv.materialid and iv.shopid=m.shopid
	where iv.quantity >0
	and iv.warehouseid=#{warehouseid,jdbcType=CHAR}
	and iv.shopid=#{shopid,jdbcType=CHAR}
	and m.shopid=#{shopid,jdbcType=CHAR} and m.isDelete = 0

		and m.sku like #{sku,jdbcType=CHAR}

) vold
left join (
	select ff.warehouseid wid,
	   sum(case when(entry.inwhstatus=0) then
		case when(ifnull(entry.orderprice,0)-(ifnull(entry.totalin,0))*ifnull(entry.itemprice,0)>=0)
		then ifnull(entry.orderprice,0)-(ifnull(entry.totalin,0))*ifnull(entry.itemprice,0) else 0 end else 0 end) inpasscost,
	sum(case when(entry.paystatus=0) then
	case when(((entry.totalin)*entry.itemprice-entry.totalpay)>=0)
	then (entry.totalin)*entry.itemprice-entry.totalpay else 0 end else 0 end) inpasspay
	from t_erp_purchase_form_entry entry
	left join t_erp_material m on entry.materialid=m.id and entry.auditstatus=2
	left join t_erp_purchase_form ff on ff.id=entry.formid
	where ff.warehouseid=#{warehouseid,jdbcType=CHAR}
	and m.shopid=#{shopid,jdbcType=CHAR}

		and m.sku like #{sku,jdbcType=CHAR}

) vnew on vold.warehouseid=vnew.wid

    select  sum( case when substring_index(status,'_',1)='inbound' then quantity else 0 end ) inbound,
			sum( case when substring_index(status,'_',1)='outbound' then quantity else 0 end ) outbound,
			sum( case when substring_index(status,'_',1)='fulfillable' then quantity else 0 end ) fulfillable
		from t_erp_inventory
		where shopid=#{shopid,jdbcType=CHAR}
	    and materialid=#{materialid,jdbcType=CHAR}
	    and warehouseid=#{warehouseid,jdbcType=CHAR}

select max(m.id) id, max(m.name) name, max(m.sku) sku, max(m.delivery_cycle) delivery_cycle, max(m.price) price, sum(case when (iv.`status`='fulfillable' or POSITION('inbound' IN iv.`status`)) then iv.quantity else 0 end) overseaqty from t_erp_material m left join t_erp_warehouse w on w.shopid=#{shopid,jdbcType=CHAR} and (w.groupid is null or w.groupid=#{groupid,jdbcType=CHAR})
and w.country in ('DE','FR','IT','ES','SA','NL','PL','SE')
and w.country=#{country,jdbcType=CHAR}
left join t_erp_inventory iv on iv.materialid =m.id and iv.warehouseid=w.id where m.sku=#{sku,jdbcType=CHAR} and m.isdelete=0 and m.shopid=#{shopid,jdbcType=CHAR}
