mapper.select:
select  min(ifnull(inv.quantity,0)/a.amount)
from  t_erp_material_consumable  a
left join t_erp_inventory inv on inv.materialid=a.submaterialid and inv.status='fulfillable'
left join t_erp_warehouse w on w.id=inv.warehouseid
left join t_erp_material m on m.id=a.materialid
where a.materialid = #{materialid,jdbcType=CHAR}
and m.shopid = #{shopid,jdbcType=CHAR}
and w.shopid = #{shopid,jdbcType=CHAR}
and (w.id=#{warehouseid,jdbcType=CHAR} or w.parentid=#{warehouseid,jdbcType=CHAR})

select max(IFNULL(pic.location,pic.url)) image,max(msub.name) mname, max(msub.sku) sku ,max(ifnull(inv.quantity,0)) inventoryqty,
ifnull(sum( case m.sku
when #{item.msku,jdbcType=CHAR} then ${item.quantityshipped}
else 0 end *cs.amount),0)+ifnull(max(ci.quantity),0) needamount,
msub.id materialid from t_erp_material m left join t_erp_material_consumable cs on cs.materialid=m.id left join t_erp_material msub on cs.submaterialid = msub.id and msub.isdelete=0 left join t_erp_material_consumable_inventory ci on ci.materialid= cs.submaterialid and ci.warehouseid=#{warehouseid,jdbcType=CHAR} and ci.shopid=#{shopid,jdbcType=CHAR} left join t_picture pic on msub.image=pic.id left join t_erp_inventory inv on inv.materialid=msub.id and inv.warehouseid=#{warehouseid,jdbcType=CHAR} and inv.status='fulfillable' and inv.shopid=#{shopid,jdbcType=CHAR} where m.shopid=#{shopid,jdbcType=CHAR} and m.isdelete=0 AND msub.id IS NOT null
and m.sku in
#{item.msku,jdbcType=CHAR}
group by msub.id
