mapper.select:
select a.id, max(a.number) number, max(u.name) creator, MAX(a.auditstatus) auditstatus, MAX(a.remark) remark, MAX(a.opttime) opttime, MAX(w.name) wname, MAX(a.createtime) createdate, MAX(a.amount) amount, MAX(m.sku) sku, MAX(f.number) pnumber
from t_erp_purchase_form_entry_change a LEFT JOIN t_userinfo u on a.creator = u.id left join t_erp_warehouse w ON a.warehouseid=w.id LEFT JOIN t_erp_purchase_form_entry b ON a.entryid = b.id left join t_erp_purchase_form f on f.id=b.formid LEFT join t_erp_material m ON a.materialid = m.id where a.shopid = #{params.shopid,jdbcType=CHAR}
and (m.sku like #{params.search,jdbcType=CHAR} or m.name like #{params.search,jdbcType=CHAR} or a.number like #{params.search,jdbcType=CHAR})
and a.auditstatus = #{params.status,jdbcType=INTEGER}
and DATE_FORMAT(a.opttime,'%Y-%m-%d') >=#{params.fromDate,jdbcType=DATE} and DATE_FORMAT(a.opttime,'%Y-%m-%d') <=#{params.endDate,jdbcType=DATE}
group by a.id
