mapper.select:
select * from ( SELECT sum(case when quantity >0 then 1 else 0 end) skunum,sum(quantity) quantity,sum(size) size , max(e.name) warehousename, max(u.name) operator, max(s.opttime) opttime, max(u2.name) creator, max(s.creattime) creattime FROM t_erp_warehouse_shelf s left join t_erp_warehouse_address e on e.id=s.addressid left join t_erp_warehouse_shelf_inventory i on s.id=i.shelfid and s.shopid=i.shopid left join t_userinfo u on u.id=s.operator left join t_userinfo u2 on u2.id=s.creator where s.shopid=#{shopid,jdbcType=CHAR} and s.addressid=#{addressid,jdbcType=CHAR}
and (s.treepath like CONCAT(#{treepath,jdbcType=CHAR},'!%') or s.treepath=#{treepath,jdbcType=CHAR} )
and s.isdelete=0 ) v1

SELECT i.id,i.shelfid,i.materialid,s.`name` shelfname,i.quantity,i.size, m.sku,m.name,p.location image,w.name warehousename,i.warehouseid, s.treepath FROM t_erp_warehouse_shelf_inventory i left join t_erp_warehouse w on w.id=i.warehouseid left join t_erp_material m on m.id=i.materialid left join t_picture p on p.id=m.image left join t_erp_warehouse_shelf s on s.id=i.shelfid and s.shopid=i.shopid where s.shopid=#{shopid,jdbcType=CHAR} and s.addressid=#{addressid,jdbcType=CHAR}
and (i.warehouseid=#{warehouseid,jdbcType=CHAR} or i.warehouseid is null)
and i.materialid=#{materialid,jdbcType=CHAR} and s.isdelete=0 and i.quantity>0 order by i.opttime asc

SELECT p.location image, m.sku, m.name, ifnull(v1.quantity,0) warehousequantity, m.id id, ifnull(v2.quantity,0) shelfquantity, ifnull(v1.quantity,0)- ifnull(v2.quantity,0) quantity, ifnull(v1.quantity,0)- ifnull(v2.quantity,0) amount, m.id materialid , v1.opttime, v2.shelfid, v1.warehouseid, w.name warehousename, adr.name addressname,
case when v2.shelfid=#{param.shelfid,jdbcType=CHAR} then now() else v1.opttime end orderitem
v1.opttime orderitem
from t_erp_material m left join (select i.materialid,i.warehouseid,sum(i.quantity) quantity,max(i.opttime) opttime from t_erp_inventory i left join t_erp_warehouse w on w.id=i.warehouseid where i.shopid=#{param.shopid,jdbcType=CHAR} and w.addressid=#{param.addressid,jdbcType=CHAR} and status ='fulfillable'
and i.warehouseid=#{param.warehouseid,jdbcType=CHAR}
group by i.materialid,i.warehouseid ) v1 on m.id=v1.materialid left join ( select i.materialid,i.warehouseid,ifnull(sum(i.quantity),0) quantity,max(s.id) shelfid from t_erp_warehouse_shelf_inventory i left join t_erp_warehouse_shelf s on s.id=i.shelfid where i.shopid=#{param.shopid,jdbcType=CHAR} and s.addressid=#{param.addressid,jdbcType=CHAR}
and i.warehouseid=#{param.warehouseid,jdbcType=CHAR}
group by i.materialid,i.warehouseid ) v2 on v2.materialid=v1.materialid and v2.warehouseid=v1.warehouseid left join t_erp_warehouse w on w.id=v1.warehouseid left join t_erp_warehouse_address adr on adr.id=w.addressid left join t_picture p on p.id=m.image where m.shopid=#{param.shopid,jdbcType=CHAR} and m.isdelete=0 and v1.quantity!=0
and m.sku like #{param.search,jdbcType=CHAR}
and m.mtype = #{param.mtype,jdbcType=CHAR}
and v1.materialid like #{param.materialid,jdbcType=CHAR}

SELECT m.name,m.sku,p.location image,w.name addressname,wh.name warehousename, ss.amount,ss.overamount ,ss.lossamount ,m.id materialid, s.treepath,s.addressid,t.quantity,ss.id ,t.shelfid,m.price ,t.warehouseid FROM t_erp_material m left join t_erp_warehouse_address w on w.id=#{param.addressid,jdbcType=CHAR} left join t_erp_warehouse_shelf s on s.addressid=w.id and s.isdelete=0
and s.id=#{param.shelfid,jdbcType=CHAR}
left join t_erp_warehouse_shelf_inventory t ON m.id=t.materialid and t.shelfid=s.id and t.shopid=m.shopid left join t_erp_warehouse wh on wh.id=t.warehouseid left join t_erp_stocktaking_item_shelf ss on ss.shelfid=s.id and ss.warehouseid=t.warehouseid and stocktakingid=#{param.stocktakingid,jdbcType=CHAR} and ss.materialid=m.id LEFT JOIN t_picture p ON p.id=m.image WHERE m.shopid=#{param.shopid,jdbcType=CHAR} and m.isdelete=0
and t.quantity>0
and s.treepath like CONCAT(#{param.treepath,jdbcType=CHAR},'%')
and m.sku like #{param.search,jdbcType=CHAR}
and m.sku = #{param.sku,jdbcType=CHAR}
and t.materialid like #{param.materialid,jdbcType=CHAR}
and ss.id is not null
order by t.opttime desc

SELECT m.name,m.sku,p.location image,t.quantity amount, w.name addressname , wh.name  warehousename,s.treepath,s.addressid,t.*
FROM t_erp_warehouse_shelf_inventory t
left join t_erp_warehouse_shelf s on s.id=t.shelfid
left join t_erp_warehouse wh on wh.id=t.warehouseid
left join t_erp_warehouse_address w on w.id=s.addressid
LEFT JOIN t_erp_material m ON m.id=t.materialid
LEFT JOIN t_picture p ON p.id=m.image
WHERE t.shopid=#{param.shopid,jdbcType=CHAR}

    and t.shelfid=#{param.shelfid,jdbcType=CHAR}

   and s.addressid=#{param.addressid,jdbcType=CHAR}

    and s.treepath like CONCAT(#{param.treepath,jdbcType=CHAR},'%') 

   and t.quantity>0

	and m.sku like #{param.search,jdbcType=CHAR}


	and m.sku = #{param.sku,jdbcType=CHAR}


   and t.materialid like #{param.materialid,jdbcType=CHAR}

and s.isdelete=0
order by t.opttime desc
