mapper.select:
SELECT w.name wname,s.name shelfname,s.number,u.name operator,m.sku,m.name mname,r.opttime, ifnull(t.name,'上下架') formname,r.quantity, case r.opt when 0 then '下架' when 1 then '上架' when 2 then '修正下架' when 3 then '修正上架' ELSE '未知' END optname, IFNULL(p.location,p.url) image , r.shelfid
from t_erp_warehouse_shelf_inventory_opt_record r LEFT JOIN t_erp_formtype t ON t.id=r.formtype LEFT JOIN t_erp_material m ON m.id=r.materialid LEFT JOIN t_picture p ON p.id=m.image LEFT JOIN t_erp_warehouse_shelf s ON s.id=r.shelfid LEFT JOIN t_erp_warehouse w ON w.id=r.warehouseid LEFT JOIN t_userinfo u ON u.id=r.operator WHERE r.shopid=#{shopid,jdbcType=CHAR}
and w.id = #{warehouseid,jdbcType=CHAR}
and r.opttime >= #{startDate,jdbcType=CHAR} and r.opttime <= #{endDate,jdbcType=CHAR}
and m.sku like #{search,jdbcType=CHAR}

SELECT max(r.id) `id`, r.`shelfid`, max(r.materialid) `materialid` , max(r.shopid) `shopid` , max(r.`warehouseid`) warehouseid , sum(r.`quantity`) quantity, sum(r.`size` ) `size`, max(r.`opt`) opt, max(r.`formid`) formid, max(r.`formtype` ) formtype, max(r.`operator` ) operator, max(r.`opttime`) opttime from t_erp_warehouse_shelf_inventory_opt_record r WHERE r.shopid=#{param.shopid,jdbcType=CHAR}
and r.formid = #{param.formid,jdbcType=CHAR}
and r.formid in (
#{item,jdbcType=CHAR}
)
and r.formtype = #{param.formtype,jdbcType=CHAR}
and r.materialid = #{param.materialid,jdbcType=CHAR}
group by r.shelfid
