mapper.select:
select s.* from t_shipment s where s.buyerid=#{param.buyerid,jdbcType=CHAR}
and status=6
and status=5
and status=0
and status=1
and s.opttime >= #{param.fromDate,jdbcType=DATE}
and s.opttime <= #{param.toDate,jdbcType=DATE}
and (s.name like concat('%',#{param.search,jdbcType=CHAR},'%') or s.number like concat('%',#{param.search,jdbcType=CHAR},'%') or s.destination like concat('%',#{param.search,jdbcType=CHAR},'%') or s.country like concat('%',#{param.search,jdbcType=CHAR},'%') or s.shipmentid like concat('%',#{param.search,jdbcType=CHAR},'%') or s.groupname like concat('%',#{param.search,jdbcType=CHAR},'%') or s.warehousename like concat('%',#{param.search,jdbcType=CHAR},'%') or s.remark like concat('%',#{param.search,jdbcType=CHAR},'%') or exists(select * from t_order o left join t_order_shipment so on o.id=so.orderid where so.shipmentid=s.shipmentid and o.number like concat('%',#{param.search,jdbcType=CHAR},'%')) or exists(select * from t_shipment_item i where i.shipmentid=s.shipmentid and i.sku like concat('%',#{param.search,jdbcType=CHAR},'%')) )
and s.number in
#{number,jdbcType=CHAR}
and s.shipmentid in
#{shipmentid,jdbcType=CHAR}
and s.groupname like concat('%',#{param.groupname,jdbcType=CHAR},'%')
and s.country like concat('%',#{param.country,jdbcType=CHAR},'%')
and s.warehousename like concat('%',#{param.warehousename,jdbcType=CHAR},'%')
and s.remark like concat('%',#{param.remark,jdbcType=CHAR},'%')
